INSERT INTO `products` (`id`, `name`, `images`, `price`, `quantity`, `warranty`, `accessories`, `discount`, `status`, `active`, `hot`, `desc`, `categories_id`, `created_at`, `updated_at`) VALUES
(11, 'Apple Watch S6 40mm viền nhôm dây cao su hồng', 'apple-watch-s6-40mm-vien-nhom-day-cao-su-ava-600x600.jpg', 9990000, 20, '12 tháng', 'Cáp - Sạc -Tai nghe', '800000', 'Nguyên Seal - Chính Hãng', 1, 1, '<h2>Đặc điểm nổi bật của Apple Watch S6 40mm viền nh&ocirc;m d&acirc;y cao su hồng</h2>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/7077/229044/Slider/apple-watch-s6-40mm-vien-nhom-day-cao-su-221020-1219320.jpg\" /></p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/dong-ho-thong-minh-apple-watch-s6\" target=\"_blank\">Apple Watch S6</a>&nbsp;mang đến những n&acirc;ng cấp hữu &iacute;ch để hỗ trợ người d&ugrave;ng một c&aacute;ch tối ưu nhất. Nổi bật trong số đ&oacute; l&agrave; chip xử l&yacute; S6 cải thiện hiệu năng, hệ điều h&agrave;nh watchOS 7 với nhiều t&iacute;nh năng mới hứa hẹn sẽ mang đến những trải nghiệm tốt hơn, th&uacute; vị hơn</h3>\r\n\r\n<h3>Thiết kế sang trọng, hiện đại, l&agrave; phụ kiện thời trang khẳng định đẳng cấp</h3>\r\n\r\n<p>Chiếc&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh/apple-watch-s6-40mm-vien-nhom-day-cao-su\" target=\"_blank\">Apple Watch S6 40mm viền nh&ocirc;m d&acirc;y cao su</a>&nbsp;n&agrave;y vẫn giữ trọn vẹn n&eacute;t tinh tế v&agrave; sắc sảo trong thiết kế từ trước đến nay, mặt k&iacute;nh&nbsp;Ion-X strengthened glass&nbsp;ưu việt, viền nh&ocirc;m được v&aacute;t gọt c&ocirc;ng phu v&agrave;&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh?g=silicon\" target=\"_blank\">d&acirc;y đeo cao su</a>&nbsp;co d&atilde;n tốt, &ecirc;m tay.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/229044/apple-watch-s6-40mm-vien-nhom-day-cao-su.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S6 với thiết kế sang trọng và hiện đại\" src=\"https://cdn.tgdd.vn/Products/Images/7077/229044/apple-watch-s6-40mm-vien-nhom-day-cao-su.jpg\" /></a></p>\r\n\r\n<h3>C&ocirc;ng nghệ mới gi&uacute;p bạn quản l&yacute; v&agrave; theo d&otilde;i sức khỏe của bản th&acirc;n dễ d&agrave;ng, khoa học hơn</h3>\r\n\r\n<p>S&aacute;ng gi&aacute; nhất trong đợt update lần n&agrave;y ch&iacute;nh l&agrave; t&iacute;nh năng đo nồng độ oxy trong m&aacute;u SpO2 v&agrave; điện t&acirc;m đồ ECG. Tuy nhi&ecirc;n ch&uacute;ng ta sẽ phải chờ th&ecirc;m một thời gian để c&oacute; thể trải nghiệm v&igrave; t&iacute;nh năng n&agrave;y vẫn chưa được hỗ trợ tại Việt Nam.</p>\r\n\r\n<p>- Cụm đ&egrave;n&nbsp;LED v&agrave; hồng ngoại chiếu &aacute;nh s&aacute;ng s&acirc;u v&agrave;o c&aacute;c mạch m&aacute;u ở cổ tay, diode quang đo lượng &aacute;nh s&aacute;ng phản xạ trở lại c&ugrave;ng với c&aacute;c thuật to&aacute;n n&acirc;ng cao sẽ t&iacute;nh to&aacute;n m&agrave;u m&aacute;u của bạn từ đ&oacute; cho biết lượng oxy hiện c&oacute;.</p>\r\n\r\n<p>- Ứng dụng ECG của Apple Watch Series 6 c&oacute; khả năng tạo ECG tương tự như điện t&acirc;m đồ. Đ&acirc;y&nbsp;l&agrave; một th&agrave;nh tựu quan trọng khi một chiếc đồng hồ c&oacute; thể mang lại những dữ liệu đ&aacute;ng gi&aacute; cho c&aacute;c b&aacute;c sĩ v&agrave; cả sự an t&acirc;m cho bạn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/229044/apple-watch-s6-40mm-vien-nhom-day-cao-su-162720-122727.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S6 nổi bật với tính năng đo nồng độ oxi trong máu (SpO2) và điện tâm đồ ECG\" src=\"https://cdn.tgdd.vn/Products/Images/7077/229044/apple-watch-s6-40mm-vien-nhom-day-cao-su-162720-122727.jpg\" /></a></p>\r\n\r\n<h3>Chip xử l&yacute; S6 nhanh hơn tới 20% so với S5</h3>\r\n\r\n<p>Một ưu điểm phải kể đến của d&ograve;ng&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh-apple\" target=\"_blank\">Apple Watch</a>&nbsp;S6 n&agrave;y đ&oacute; l&agrave; chip xử l&yacute; S6&nbsp;cải thiện hiệu năng th&ecirc;m 20% so với ở thế hệ S5 gi&uacute;p n&acirc;ng cao sự mượt m&agrave; v&agrave; độ ch&iacute;nh x&aacute;c, đem đến những trải nghiệm tốt hơn cho người d&ugrave;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/229044/apple-watch-s6-40mm-vien-nhom-day-cao-su-254420-074413.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S6 trang bị chip xử lý thế hệ S6 nhanh hơn 20% so với S5\" src=\"https://cdn.tgdd.vn/Products/Images/7077/229044/apple-watch-s6-40mm-vien-nhom-day-cao-su-254420-074413.jpg\" /></a></p>\r\n\r\n<h3>Hệ điều h&agrave;nh WatchOS 7 mang đến nhiều t&iacute;nh năng mới hỗ trợ người d&ugrave;ng tối ưu</h3>\r\n\r\n<p>WatchOS 7 tr&ecirc;n Apple Watch series 6 được điều chỉnh để tối ưu h&oacute;a việc theo d&otilde;i chế độ luyện tập, ra mắt dịch vụ fitness hỗ trợ tập luyện hiệu quả qua c&aacute;c chương tr&igrave;nh như: Yoga, đạp xe, nhảy, chạy, bơi lội,... Đồng thời trang bị th&ecirc;m nhiều t&iacute;nh năng hữu &iacute;ch nổi bật như&nbsp;Family Setup, nhận biết rửa tay, chia sẻ m&agrave;n h&igrave;nh qua iMessages, Siri dịch nhanh,...</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/229044/apple-watch-s6-40mm-vien-nhom-day-cao-su-254420-074419.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S6 có hệ điều hành watchOS 7 với nhiều tính năng mới hữu ích\" src=\"https://cdn.tgdd.vn/Products/Images/7077/229044/apple-watch-s6-40mm-vien-nhom-day-cao-su-254420-074419.jpg\" /></a></p>\r\n\r\n<h3>M&agrave;n h&igrave;nh OLED Retina lu&ocirc;n lu&ocirc;n hiển thị gi&uacute;p bạn xem giờ v&agrave; th&ocirc;ng b&aacute;o thuận tiện</h3>\r\n\r\n<p>- M&agrave;n h&igrave;nh của đồng hồ&nbsp;sử dụng tấm nền OLED cao cấp mang đến độ sắc n&eacute;t ch&acirc;n thực v&agrave; gi&uacute;p tiết kiệm pin.</p>\r\n\r\n<p>- T&iacute;nh năng&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh?g=man-hinh-luon-hien-thi\" target=\"_blank\">m&agrave;n h&igrave;nh lu&ocirc;n hiển thị</a>&nbsp;(Always-on) ở đồng hồ th&ocirc;ng minh Apple 6 giống với d&ograve;ng Apple Watch series 5 nhưng ưu điểm l&agrave; m&agrave;n h&igrave;nh&nbsp;ở chế độ chờ s&aacute;ng hơn 2.5 lần nhưng kh&ocirc;ng g&acirc;y tốn pin hơn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/229044/apple-watch-s6-40mm-vien-nhom-day-cao-su-254320-074323.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S6 sở hữu mà hình OLED luôn hiển thị với độ sắc nét cao\" src=\"https://cdn.tgdd.vn/Products/Images/7077/229044/apple-watch-s6-40mm-vien-nhom-day-cao-su-254320-074323.jpg\" /></a></p>\r\n\r\n<h3>Nhận th&ocirc;ng b&aacute;o v&agrave;&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh?g=nghe-goi-tren-dong-ho\" target=\"_blank\">nghe gọi tr&ecirc;n đồng hồ</a>&nbsp;một c&aacute;ch thuận tiện</h3>\r\n\r\n<p>Kết nối đồng hồ với điện thoại qua cổng bluetooth hay wifi cho ph&eacute;p người d&ugrave;ng dễ d&agrave;ng thực hiện nghe gọi, xem tin nhắn, kiểm tra th&ocirc;ng b&aacute;o ứng dụng (Messenger, Line, Viber,&hellip;), th&ocirc;ng b&aacute;o theo d&otilde;i sức khỏe, r&egrave;n luyện thể thao,&hellip; Hỗ trợ bạn dễ d&agrave;ng quản l&yacute; hiệu quả c&ocirc;ng việc v&agrave; sinh hoạt thường ng&agrave;y.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/229044/apple-watch-s6-40mm-vien-nhom-day-cao-su-254320-074317.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S6 có tính năng nghe gọi trên đồng hồ\" src=\"https://cdn.tgdd.vn/Products/Images/7077/229044/apple-watch-s6-40mm-vien-nhom-day-cao-su-254320-074317.jpg\" /></a></p>\r\n\r\n<h3>Nhiều giao diện với phong c&aacute;ch đa dạng, ph&ugrave; hợp với mọi người, mọi lứa tuổi</h3>\r\n\r\n<p>Thoải m&aacute;i lựa chọn giao diện ph&ugrave; hợp với phong c&aacute;ch ri&ecirc;ng của bản th&acirc;n hoặc bạn cũng c&oacute; thể t&igrave;m kiếm c&aacute;c giao diện y&ecirc;u th&iacute;ch tr&ecirc;n App Store.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/229044/apple-watch-s6-40mm-vien-nhom-day-cao-su-254320-074329.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S6 bộ sưu tập giao diện đa dạng cho bạn thoải mái lựa chọn\" src=\"https://cdn.tgdd.vn/Products/Images/7077/229044/apple-watch-s6-40mm-vien-nhom-day-cao-su-254320-074329.jpg\" /></a></p>\r\n\r\n<h3>Pin dung lượng lớn, sạc đầy nhanh ch&oacute;ng</h3>\r\n\r\n<p>Pin của Apple Watch S6 d&ugrave;ng được trong khoảng 1.5 ng&agrave;y v&agrave; chỉ cần 2 giờ đồng hồ để sạc đầy pin gi&uacute;p bạn sử dụng đồng hồ một c&aacute;ch thoải m&aacute;i, trọn vẹn nhất.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/229044/apple-watch-s6-40mm-vien-nhom-day-cao-su-254320-074335.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S6 có pin dung lượng lớn, sạc đầy nhanh chóng\" src=\"https://cdn.tgdd.vn/Products/Images/7077/229044/apple-watch-s6-40mm-vien-nhom-day-cao-su-254320-074335.jpg\" /></a></p>\r\n\r\n<h3>Quản l&yacute; sức khỏe của bản th&acirc;n tốt hơn</h3>\r\n\r\n<p>- Đồng hồ lu&ocirc;n&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh-do-nhip-tim\" target=\"_blank\">theo d&otilde;i nhịp tim</a>&nbsp;của bạn mọi l&uacute;c mọi nơi v&agrave; th&ocirc;ng b&aacute;o cho bạn khi nhịp tim cao, thấp hoặc kh&ocirc;ng đều bất thường.</p>\r\n\r\n<p>-&nbsp;Ứng dụng Sleep mới&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh-theo-doi-giac-ngu\" target=\"_blank\">theo d&otilde;i giấc ngủ</a>&nbsp;của bạn hằng đ&ecirc;m v&agrave; x&acirc;y dựng cho bạn một lịch tr&igrave;nh ngủ hợp l&yacute;, gi&uacute;p bạn thiết lập th&oacute;i quen đi ngủ đều đặn, tốt cho sức khỏe.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/229044/apple-watch-s6-40mm-vien-nhom-day-cao-su-254320-074341.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S6 có tính năng đo nhịp tim và theo dõi sức khỏe 24/24\" src=\"https://cdn.tgdd.vn/Products/Images/7077/229044/apple-watch-s6-40mm-vien-nhom-day-cao-su-254320-074341.jpg\" /></a></p>\r\n\r\n<h3>Tr&aacute;nh những rủi ro kh&ocirc;ng mong muốn với t&iacute;nh năng ph&aacute;t hiện t&eacute; ng&atilde;</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/dong-ho-thong-minh\" target=\"_blank\">Đồng hồ th&ocirc;ng minh</a>&nbsp;c&oacute; t&iacute;nh năng ph&aacute;t hiện khi bạn bị ng&atilde; v&agrave; bất động trong 15 gi&acirc;y, n&oacute; sẽ ph&aacute;t t&iacute;n hiệu SOS đến số được c&agrave;i sẵn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/229044/apple-watch-s6-40mm-vien-nhom-day-cao-su-254320-074347.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S6 có tính năng phát hiện té ngã\" src=\"https://cdn.tgdd.vn/Products/Images/7077/229044/apple-watch-s6-40mm-vien-nhom-day-cao-su-254320-074347.jpg\" /></a></p>\r\n\r\n<h3>Nhiều chế độ tập luyện kh&aacute;c nhau hỗ trợ bạn r&egrave;n luyện cơ thể một c&aacute;ch khoa học, tốt cho sức khỏe</h3>\r\n\r\n<p>Đồng hồ th&ocirc;ng minh Apple S6 được t&iacute;ch hợp nhiều b&agrave;i&nbsp;luyện tập thể thao&nbsp;như nhảy, đạp xe, bơi lội, leo n&uacute;i, yoga, ... gi&uacute;p n&acirc;ng cao hiệu quả tập luyện v&agrave; cải thiện sức khỏe mỗi ng&agrave;y.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/229044/apple-watch-s6-40mm-vien-nhom-day-cao-su-254320-074353.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S6 hỗ trợ bạn luyện tập thể thao hiệu quả và khoa học\" src=\"https://cdn.tgdd.vn/Products/Images/7077/229044/apple-watch-s6-40mm-vien-nhom-day-cao-su-254320-074353.jpg\" /></a></p>\r\n\r\n<h3>Hỗ trợ người d&ugrave;ng tốt hơn với trợ l&yacute; ảo Siri</h3>\r\n\r\n<p>Siri c&oacute; thể dịch nhanh 10 ng&ocirc;n ngữ tr&ecirc;n thế giới (chưa c&oacute; tiếng Việt), nhận diện giọng n&oacute;i dễ d&agrave;ng, với chức năng n&agrave;y, Siri sẽ gi&uacute;p bạn thực hiện nhanh ch&oacute;ng một v&agrave;i t&aacute;c vụ đơn giản hoặc đưa ra hướng dẫn hỗ trợ bạn thực hiện.</p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/images/7077/229044/apple-watch-s6-40mm-vien-nhom-day-cao-su-254420-074425.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S6 có trợ lý ảo Siri để hỗ trợ người dùng tốt hơn\" src=\"https://cdn.tgdd.vn/Products/Images/7077/229044/apple-watch-s6-40mm-vien-nhom-day-cao-su-254420-074425.jpg\" /></a></h3>\r\n\r\n<h3>Family Setup hỗ trợ bạn quản l&yacute; v&agrave; chăm s&oacute;c mọi người trong gia đ&igrave;nh</h3>\r\n\r\n<p>T&iacute;nh năng n&agrave;y cho ph&eacute;p&nbsp;1 t&agrave;i khoản iCloud c&oacute; thể quản l&yacute; nhiều chiếc Apple Watch kh&aacute;c nhau, thuận tiện trong việc chăm s&oacute;c trẻ em v&agrave; người gi&agrave; th&ocirc;ng qua c&aacute;c t&iacute;nh năng: theo d&otilde;i sức khỏe,&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh?g=dinh-vi-tre-em\" target=\"_blank\">định vị trẻ em</a>, ph&aacute;t hiện t&eacute; ng&atilde;,...</p>\r\n\r\n<p>Lưu &yacute;: T&iacute;nh năng n&agrave;y hiện kh&ocirc;ng khả dụng ở Việt Nam.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/229044/apple-watch-s6-40mm-vien-nhom-day-cao-su-1.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S6 có tính năng Family Setup\" src=\"https://cdn.tgdd.vn/Products/Images/7077/229044/apple-watch-s6-40mm-vien-nhom-day-cao-su-1.jpg\" /></a></p>\r\n\r\n<h3>C&ograve;n rất nhiều&nbsp;c&aacute;c tiện&nbsp;&iacute;ch th&uacute; vị&nbsp;kh&aacute;c chờ bạn kh&aacute;m ph&aacute;</h3>\r\n\r\n<p>Apple Watch series 6 sở hữu nhiều tiện &iacute;ch kh&aacute;c như t&igrave;m&nbsp;điện thoại, hướng dẫn&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh?g=lo-trinh-duong-di\" target=\"_blank\">lộ tr&igrave;nh</a>,&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh?g=dem-so-buoc-chan\" target=\"_blank\">đếm số bước ch&acirc;n</a>, dự b&aacute;o thời tiết,&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh?g=dinh-vi\" target=\"_blank\">định vị</a>,&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh?g=nghe-nhac-voi-tai-nghe-bluetooth\" target=\"_blank\">nghe nhạc</a>&nbsp;tr&ecirc;n đồng hồ,&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh?g=tinh-luong-calories-tieu-thu\" target=\"_blank\">t&iacute;nh lượng calories ti&ecirc;u thụ</a>, ... mang đến cho bạn một cuộc sống thoải m&aacute;i, hiện đại.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/229044/apple-watch-s6-40mm-vien-nhom-day-cao-su-254420-074406.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S6 còn có rất nhiều tiện ích thú vị và hữu ích khác\" src=\"https://cdn.tgdd.vn/Products/Images/7077/229044/apple-watch-s6-40mm-vien-nhom-day-cao-su-254420-074406.jpg\" /></a></p>\r\n\r\n<p>Apple Watch S6 với thiết kế sang trọng v&agrave; thời trang c&ugrave;ng d&acirc;y đeo cao su &ecirc;m &aacute;i sẽ l&agrave; phụ kiện thời trang đẳng cấp d&agrave;nh cho bạn. Hơn thế, sản phẩm mới n&agrave;y c&ograve;n mang đến những n&acirc;ng cấp ấn tượng như: d&ograve;ng chip S6 cải thiện 20% hiệu năng, hệ điều h&agrave;nh WatchOS 7 được cập nhật th&ecirc;m nhiều t&iacute;nh năng hữu &iacute;ch (Family Setup, nhận biết rửa tay, chia sẻ m&agrave;n h&igrave;nh qua iMessages, Siri dịch nhanh,...). Đ&acirc;y hứa hẹn l&agrave; mẫu Apple Watch mới đ&aacute;ng mong chờ trong năm 2020, mang đến cho người d&ugrave;ng một cuộc sống thoải m&aacute;i v&agrave; hiện đại.</p>', 4, '2021-04-25 21:33:08', '2021-04-25 21:33:12'),
(12, 'Apple Watch S5 44mm viền nhôm dây cao su đen', 'apple-watch-s5-44mm-vien-nhom-day-cao-su-10-600x600.jpg', 12041000, 20, '12 tháng - Chính hãng', 'Cáp - Sạc -Tai nghe', '300000', 'Nguyên Seal - Chính Hãng', 1, 0, '<h2>Đặc điểm nổi bật của Apple Watch S5 44mm viền nh&ocirc;m d&acirc;y cao su đen</h2>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/7077/212770/Slider/apple-watch-s5-44mm-vien-nhom-day-cao-su-220920-0956320.jpg\" /><img src=\"https://www.thegioididong.com/Content/desktop/images/V4/icon-yt.png\" /></p>\r\n\r\n<h2><a href=\"https://www.thegioididong.com/dong-ho-thong-minh/apple-watch-s5\" target=\"_blank\">Apple Watch S5&nbsp;44 mm</a>&nbsp;l&agrave; phi&ecirc;n bản n&acirc;ng cấp nhẹ so với phi&ecirc;n bản Apple Watch S4 tiền nhiệm. Lần đầu ti&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh-apple\" target=\"_blank\">Apple Watch</a>&nbsp;sẽ được trang bị m&agrave;n h&igrave;nh OLED lu&ocirc;n bật, t&iacute;nh năng la b&agrave;n v&agrave; khả năng cảnh b&aacute;o khẩn cấp tr&ecirc;n nhiều quốc gia hơn.&nbsp;</h2>\r\n\r\n<h3>M&agrave;n h&igrave;nh OLED Retina kh&ocirc;ng bao giờ tắt</h3>\r\n\r\n<p>T&iacute;nh năng&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh?g=man-hinh-luon-hien-thi\" target=\"_blank\">m&agrave;n h&igrave;nh lu&ocirc;n hiển thị</a>&nbsp;(Always-on) gi&uacute;p bạn xem giờ v&agrave; th&ocirc;ng b&aacute;o tiện lợi bất cứ l&uacute;c n&agrave;o. Khi kh&ocirc;ng quan s&aacute;t, đồng hồ sẽ tự giảm độ s&aacute;ng xuống để tiết kiệm pin.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/212770/apple-watch-s5-11.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S5 luôn luôn hiển thị\" src=\"https://cdn.tgdd.vn/Products/Images/7077/212770/apple-watch-s5-11.jpg\" /></a></p>\r\n\r\n<p>M&agrave;n h&igrave;nh tr&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh/apple-watch-s5\" target=\"_blank\">Apple Watch S5 44mm</a>&nbsp;sử dụng tấm nền OLED cao cấp, tiết kiệm pin hơn, độ sắc n&eacute;t đạt chuẩn Retina - nghĩa l&agrave; bạn rất kh&oacute; để nhận biết c&aacute;c điểm ảnh, rỗ hạt bằng mắt thường.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/212770/apple-watch-s5-22.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S5 hiển thị siêu nét\" src=\"https://cdn.tgdd.vn/Products/Images/7077/212770/apple-watch-s5-22.jpg\" /></a></p>\r\n\r\n<h3>X&aacute;c định phương hướng bằng la b&agrave;n</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/dong-ho-thong-minh\" target=\"_blank\">Đồng hồ th&ocirc;ng minh</a>&nbsp;Apple Watch S5 c&oacute; t&iacute;nh năng&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh?g=gps\" target=\"_blank\">định vị GPS</a>&nbsp;gi&uacute;p&nbsp;<strong>định vị ch&iacute;nh x&aacute;c</strong>&nbsp;qu&atilde;ng đường đi khi tập, từ đấy cung cấp th&ocirc;ng tin về lộ tr&igrave;nh trong qu&aacute; tr&igrave;nh luyện tập cho bạn.</p>\r\n\r\n<p>Điểm cải tiến ở Apple Watch S5 so với c&aacute;c thế hệ trước đ&oacute; l&agrave;&nbsp;<strong>la b&agrave;n từ t&iacute;nh để x&aacute;c định phương hướng</strong>. T&iacute;nh năng n&agrave;y sẽ hữu &iacute;ch đối với những người d&ugrave;ng th&iacute;ch đi chạy bộ trong rừng, gi&uacute;p định vị phương hướng ở nơi kh&ocirc;ng thể truy cập mạng hay GPS kh&ocirc;ng thể định vị được.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/212770/apple-watch-s5-44mm-vien-nhom-day-cao-su3.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S5 định vị chính xác\" src=\"https://cdn.tgdd.vn/Products/Images/7077/212770/apple-watch-s5-44mm-vien-nhom-day-cao-su3.jpg\" /></a></p>\r\n\r\n<h3>T&iacute;nh năng&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh?g=cuoc-goi-khan-cap-sos\" target=\"_blank\">gọi cứu hộ khẩn cấp</a>&nbsp;c&oacute; sẵn ở nhiều khu vực hơn</h3>\r\n\r\n<p>T&iacute;nh năng gọi cứu hộ khẩn cấp c&oacute; hỗ trợ ở Việt Nam, v&agrave; đặc biệt hữu &iacute;ch với ai thường xuy&ecirc;n đi du lịch. Apple Watch sẽ định vị bạn đang ở đ&acirc;u v&agrave; gọi đến cứu hộ của địa phương đ&oacute; khi bật SOS. Apple c&oacute; ghi ch&uacute; r&otilde; c&aacute;c địa phương kh&ocirc;ng được hỗ trợ, tham khảo&nbsp;<strong><a href=\"https://www.apple.com/watchos/feature-availability/#branded-international-emergency\" target=\"_blank\">Tại đ&acirc;y</a></strong>.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/212770/apple-watch-s5-44mm-vien-nhom-day-cao-su4.jpg\" onclick=\"return false;\"><img alt=\"Tính năng gọi khẩn cấp trên Apple Watch S5\" src=\"https://cdn.tgdd.vn/Products/Images/7077/212770/apple-watch-s5-44mm-vien-nhom-day-cao-su4.jpg\" /></a></p>\r\n\r\n<h3>Hỗ trợ Apple Music</h3>\r\n\r\n<p>Apple Watch thế hệ mới hỗ trợ kho nhạc số Apple Music với hơn 50 triệu b&agrave;i h&aacute;t c&oacute; sẵn. Bạn c&oacute; thể nghe nhạc trực tiếp tr&ecirc;n đồng hồ bằng loa ngo&agrave;i trong trường hợp kh&ocirc;ng c&oacute; loa, điện thoại kế b&ecirc;n.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/212770/apple-watch-s5-3.jpg\" onclick=\"return false;\"><img alt=\"Kho nhạc khổng lồ\" src=\"https://cdn.tgdd.vn/Products/Images/7077/212770/apple-watch-s5-3.jpg\" /></a></p>\r\n\r\n<h3>Ph&aacute;t hiện t&eacute; ng&atilde;</h3>\r\n\r\n<p>Đồng hồ c&oacute; t&iacute;nh năng ph&aacute;t hiện khi bạn bị ng&atilde; v&agrave; bất động 15 gi&acirc;y, n&oacute; sẽ ph&aacute;t t&iacute;n hiệu SOS đến số được c&agrave;i sẵn.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/212770/apple-watch-s5-44mm-vien-nhom-day-cao-su9.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S5 phát hiện té ngã\" src=\"https://cdn.tgdd.vn/Products/Images/7077/212770/apple-watch-s5-44mm-vien-nhom-day-cao-su9.jpg\" /></a></p>\r\n\r\n<h3>Đo nhịp tim li&ecirc;n tục</h3>\r\n\r\n<p>Cảm biến điện tử đo nhịp tim một c&aacute;ch ch&iacute;nh x&aacute;c, bạn c&oacute; thể liếc nhanh để kiểm tra t&igrave;nh trạng nhịp tim của m&igrave;nh.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/212770/apple-watch-s5-44mm-vien-nhom-day-cao-su-7.jpg\" onclick=\"return false;\"><img alt=\"Đo nhịp tim liên tục\" src=\"https://cdn.tgdd.vn/Products/Images/7077/212770/apple-watch-s5-44mm-vien-nhom-day-cao-su-7.jpg\" /></a></p>\r\n\r\n<h3>Thiết kế sang trọng</h3>\r\n\r\n<p>Apple Watch S5 44mm viền nh&ocirc;m d&acirc;y cao su c&oacute; thiết kế hầu như kh&ocirc;ng c&oacute; g&igrave; thay đổi so với Series S4, vẫn l&agrave; mặt đồng hồ sapphire tinh xảo, viền nh&ocirc;m được v&aacute;t gọt tinh tế v&agrave; d&acirc;y cao su dễ d&agrave;ng thay đổi.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/212770/apple-watch-s5-44mm-vien-nhom-day-cao-su-6.jpg\" onclick=\"return false;\"><img alt=\" Apple Watch S5 dây đeo mềm mại, ít bám bụi\" src=\"https://cdn.tgdd.vn/Products/Images/7077/212770/apple-watch-s5-44mm-vien-nhom-day-cao-su-6.jpg\" /></a></p>\r\n\r\n<p>Thiết kế sang trọng v&agrave; mỏng nhẹ, Apple Watch kh&ocirc;ng chỉ l&agrave; một chiếc đồng hồ th&ocirc;ng minh m&agrave; c&ograve;n l&agrave; một phụ kiện thời trang đẳng cấp.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/212770/apple-watch-s5-44mm-vien-nhom-day-cao-su10.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế sang trọng của Apple Watch S5\" src=\"https://cdn.tgdd.vn/Products/Images/7077/212770/apple-watch-s5-44mm-vien-nhom-day-cao-su10.jpg\" /></a></p>\r\n\r\n<h3>Nhắc nhở sức khỏe</h3>\r\n\r\n<p>B&ecirc;n cạnh t&iacute;nh năng đo nhịp tim, đồng hồ c&ograve;n gi&uacute;p &iacute;ch cho sức khỏe của bạn với c&aacute;c t&iacute;nh năng như: Nhắc khi ngồi qu&aacute; l&acirc;u, nhắc nhở tập luyện, kiểm so&aacute;t căng thẳng mệt mỏi khi l&agrave;m việc qu&aacute; l&acirc;u.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/212770/apple-watch-s5-44mm-vien-nhom-day-cao-su8.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S5 nhắc nhở hoạt động\" src=\"https://cdn.tgdd.vn/Products/Images/7077/212770/apple-watch-s5-44mm-vien-nhom-day-cao-su8.jpg\" /></a></p>\r\n\r\n<h3>C&aacute;c chế độ luyện tập</h3>\r\n\r\n<p>Apple Watch S5 t&iacute;ch hợp nhiều bộ m&ocirc;n tập luyện như chạy bộ, boxing, đạp xe,... Đồng hồ c&ograve;n đo lường ch&iacute;nh x&aacute;c được lượng calories ti&ecirc;u thụ đối với c&aacute;c m&ocirc;n thể thao, gi&uacute;p bạn điều chỉnh chế độ tập luyện ph&ugrave; hợp.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/212770/apple-watch-s5-44mm-vien-nhom-day-cao-su13-saochep.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S5 có nhiều chế độ tập luyện khác nhau\" src=\"https://cdn.tgdd.vn/Products/Images/7077/212770/apple-watch-s5-44mm-vien-nhom-day-cao-su13-saochep.jpg\" /></a></p>', 4, '2021-04-25 21:35:22', '2021-04-25 21:59:09'),
(13, 'Apple Watch S6 40mm viền nhôm dây cao su (Product RED)', 'apple-watch-s6-44mm-vien-nhom-day-cao-su-red-232420-102444-600x600.jpg', 9990000, 20, '12 tháng', 'Cáp - Sạc -Tai nghe', '0', 'Nguyên Seal - Chính Hãng', 1, 0, '<h2>Đặc điểm nổi bật của Apple Watch S6 40mm viền nh&ocirc;m d&acirc;y cao su (Product RED)</h2>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/7077/229054/Slider/apple-watch-s6-40mm-vien-nhom-day-cao-su-red-221020-1220110.jpg\" /></p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/dong-ho-thong-minh-apple-watch-s6\" target=\"_blank\">Apple Watch S6</a>&nbsp;mang đến những n&acirc;ng cấp hữu &iacute;ch để hỗ trợ người d&ugrave;ng một c&aacute;ch tối ưu nhất. Nổi bật trong số đ&oacute; l&agrave; chip xử l&yacute; S6 cải thiện hiệu năng, hệ điều h&agrave;nh watchOS 7 với nhiều t&iacute;nh năng mới hứa hẹn sẽ mang đến những trải nghiệm tốt hơn, th&uacute; vị hơn</h3>\r\n\r\n<h3>Giữ vững n&eacute;t sang trọng v&agrave; hiện đại qua từng thế hệ</h3>\r\n\r\n<p>Chiếc&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh/apple-watch-s6-40mm-vien-nhom-day-cao-su-red\" target=\"_blank\">Apple Watch S6 40mm viền nh&ocirc;m d&acirc;y cao su (RED)</a>&nbsp;n&agrave;y vẫn giữ trọn vẹn n&eacute;t tinh tế v&agrave; sắc sảo trong thiết kế từ trước đến nay, mặt đồng hồ sapphire ưu việt, viền nh&ocirc;m được v&aacute;t gọt c&ocirc;ng phu v&agrave;&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh?g=silicon\" target=\"_blank\">d&acirc;y đeo cao su</a>&nbsp;co d&atilde;n, &ecirc;m tay.&nbsp;Số tiền m&agrave; Apple thu được từ việc b&aacute;n sản phẩm n&agrave;y sẽ quy&ecirc;n g&oacute;p v&agrave; gửi đến Quỹ to&agrave;n cầu ph&ograve;ng chống HIV/AIDS.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/229054/229054-fix.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S6 40mm viền nhôm dây cao su (RED) nổi bật với thiết kế sang trọng, hiện đại\" src=\"https://cdn.tgdd.vn/Products/Images/7077/229054/229054-fix.jpg\" /></a></p>\r\n\r\n<h3>C&ocirc;ng nghệ mới gi&uacute;p bạn quản l&yacute; v&agrave; theo d&otilde;i sức khỏe của bản th&acirc;n dễ d&agrave;ng, khoa học hơn</h3>\r\n\r\n<p>S&aacute;ng gi&aacute; nhất trong đợt update lần n&agrave;y ch&iacute;nh l&agrave; t&iacute;nh năng đo nồng độ oxy trong m&aacute;u SpO2 v&agrave; điện t&acirc;m đồ ECG. Tuy nhi&ecirc;n ch&uacute;ng ta sẽ phải chờ th&ecirc;m một thời gian để c&oacute; thể trải nghiệm v&igrave; t&iacute;nh năng n&agrave;y vẫn chưa được hỗ trợ tại Việt Nam.</p>\r\n\r\n<p>- Cụm đ&egrave;n&nbsp;LED v&agrave; hồng ngoại chiếu &aacute;nh s&aacute;ng s&acirc;u v&agrave;o c&aacute;c mạch m&aacute;u ở cổ tay, diode quang đo lượng &aacute;nh s&aacute;ng phản xạ trở lại c&ugrave;ng với c&aacute;c thuật to&aacute;n n&acirc;ng cao sẽ t&iacute;nh to&aacute;n m&agrave;u m&aacute;u của bạn từ đ&oacute; cho biết lượng oxy hiện c&oacute;.</p>\r\n\r\n<p>- Ứng dụng ECG của Apple Watch Series 6 c&oacute; khả năng tạo ECG tương tự như điện t&acirc;m đồ. Đ&acirc;y&nbsp;l&agrave; một th&agrave;nh tựu quan trọng khi một chiếc đồng hồ c&oacute; thể mang lại những dữ liệu đ&aacute;ng gi&aacute; cho c&aacute;c b&aacute;c sĩ v&agrave; cả sự an t&acirc;m cho bạn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/229054/apple-watch-s6-40mm-vien-nhom-day-cao-su-red-162720-122757.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S6 nổi bật với tính năng đo đồng hồ oxy trong máu ( SpO2) và điện tâm đồ ECG\" src=\"https://cdn.tgdd.vn/Products/Images/7077/229054/apple-watch-s6-40mm-vien-nhom-day-cao-su-red-162720-122757.jpg\" /></a></p>\r\n\r\n<h3>Chip xử l&yacute; S6 nhanh hơn tới 20% so với S5</h3>\r\n\r\n<p>Một ưu điểm phải kể đến của d&ograve;ng&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh-apple\" target=\"_blank\">Apple Watch</a>&nbsp;S6 n&agrave;y đ&oacute; l&agrave; chip xử l&yacute; S6&nbsp;cải thiện hiệu năng th&ecirc;m 20% so với ở Apple Watch S5 gi&uacute;p n&acirc;ng cao sự mượt m&agrave; v&agrave; độ ch&iacute;nh x&aacute;c, đem đến những trải nghiệm tốt hơn cho người d&ugrave;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/229054/apple-watch-s6-40mm-vien-nhom-day-cao-su-red-252320-122348.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S6 40mm viền nhôm dây cao su (RED) trang bị chip xử lý S6 nhanh hơn 20% so với dòng S5\" src=\"https://cdn.tgdd.vn/Products/Images/7077/229054/apple-watch-s6-40mm-vien-nhom-day-cao-su-red-252320-122348.jpg\" /></a></p>\r\n\r\n<h3>Hệ điều h&agrave;nh WatchOS 7 mang đến nhiều t&iacute;nh năng mới hỗ trợ người d&ugrave;ng tối ưu</h3>\r\n\r\n<p>WatchOS 7 tr&ecirc;n đồng hồ th&ocirc;ng m&igrave;nh Apple S6 được điều chỉnh để tối ưu h&oacute;a việc theo d&otilde;i chế độ luyện tập, ra mắt dịch vụ fitness hỗ trợ tập luyện hiệu quả qua c&aacute;c chương tr&igrave;nh như: Yoga, đạp xe, nhảy, chạy, bơi lội,... Đồng thời trang bị th&ecirc;m nhiều t&iacute;nh năng hữu &iacute;ch nổi bật như&nbsp;Family Setup, nhận biết rửa tay, chia sẻ m&agrave;n h&igrave;nh qua iMessages, Siri dịch nhanh,...</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/229054/apple-watch-s6-40mm-vien-nhom-day-cao-su-red-252320-122354.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S6 40mm viền nhôm dây cao su (RED) sử dụng hệ điều hành watchOS 7 mang đến nhiều tính năng mới\" src=\"https://cdn.tgdd.vn/Products/Images/7077/229054/apple-watch-s6-40mm-vien-nhom-day-cao-su-red-252320-122354.jpg\" /></a></p>\r\n\r\n<h3>M&agrave;n h&igrave;nh OLED lu&ocirc;n lu&ocirc;n hiển thị, độ sắc n&eacute;t đạt chuẩn Retina</h3>\r\n\r\n<p>- M&agrave;n h&igrave;nh của đồng hồ&nbsp;sử dụng tấm nền OLED cao cấp mang đến độ sắc n&eacute;t ch&acirc;n thực v&agrave; gi&uacute;p tiết kiệm pin.</p>\r\n\r\n<p>- T&iacute;nh năng&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh?g=man-hinh-luon-hien-thi\" target=\"_blank\">m&agrave;n h&igrave;nh lu&ocirc;n hiển thị</a>&nbsp;(Always-on) ở Apple Watch series 6 giống với d&ograve;ng đồng hồ th&ocirc;ng minh Apple S5 nhưng ưu điểm l&agrave; m&agrave;n h&igrave;nh&nbsp;ở chế độ chờ s&aacute;ng hơn 2.5 lần nhưng kh&ocirc;ng g&acirc;y tốn pin hơn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/229054/apple-watch-s6-40mm-vien-nhom-day-cao-su-red-252220-122258.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S6 40mm viền nhôm dây cao su (RED) sở hữu màn hình OLED luôn luôn hiển thị, sắc nét đạt chuẩn Retina\" src=\"https://cdn.tgdd.vn/Products/Images/7077/229054/apple-watch-s6-40mm-vien-nhom-day-cao-su-red-252220-122258.jpg\" /></a></p>\r\n\r\n<h3>Nhận th&ocirc;ng b&aacute;o v&agrave;&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh?g=nghe-goi-tren-dong-ho\" target=\"_blank\">nghe gọi tr&ecirc;n đồng hồ</a>&nbsp;một c&aacute;ch thuận tiện</h3>\r\n\r\n<p>Kết nối đồng hồ với điện thoại qua cổng bluetooth hay wifi cho ph&eacute;p người d&ugrave;ng dễ d&agrave;ng thực hiện nghe gọi, xem tin nhắn, kiểm tra th&ocirc;ng b&aacute;o ứng dụng (Messenger, Line, Viber,&hellip;), th&ocirc;ng b&aacute;o theo d&otilde;i sức khỏe, r&egrave;n luyện thể thao,&hellip; Hỗ trợ bạn dễ d&agrave;ng quản l&yacute; hiệu quả c&ocirc;ng việc v&agrave; sinh hoạt thường ng&agrave;y.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/229054/apple-watch-s6-40mm-vien-nhom-day-cao-su-red-252220-122252.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S6 40mm viền nhôm dây cao su (RED) có chức năng nghe gọi trên đồng hồ\" src=\"https://cdn.tgdd.vn/Products/Images/7077/229054/apple-watch-s6-40mm-vien-nhom-day-cao-su-red-252220-122252.jpg\" /></a></p>\r\n\r\n<h3>Nhiều giao diện với phong c&aacute;ch đa dạng, ph&ugrave; hợp với mọi người, mọi lứa tuổi</h3>\r\n\r\n<p>Thoải m&aacute;i lựa chọn giao diện ph&ugrave; hợp với phong c&aacute;ch ri&ecirc;ng của bản th&acirc;n với bộ sưu tập giao diện đa dạng, hoặc bạn cũng c&oacute; thể t&igrave;m kiếm c&aacute;c giao diện y&ecirc;u th&iacute;ch tr&ecirc;n App Store.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/229054/apple-watch-s6-40mm-vien-nhom-day-cao-su-red-252320-122305.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S6 40mm viền nhôm dây cao su (RED) có bộ sưu tập giao diện đa dạng và phong cách\" src=\"https://cdn.tgdd.vn/Products/Images/7077/229054/apple-watch-s6-40mm-vien-nhom-day-cao-su-red-252320-122305.jpg\" /></a></p>\r\n\r\n<h3>Pin dung lượng lớn, sạc đầy nhanh ch&oacute;ng</h3>\r\n\r\n<p>Pin của Apple Watch Series 6 d&ugrave;ng được trong khoảng 1.5 ng&agrave;y v&agrave; chỉ cần 2 giờ đồng hồ để sạc đầy pin gi&uacute;p bạn sử dụng đồng hồ một c&aacute;ch thoải m&aacute;i, trọn vẹn nhất.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/229054/apple-watch-s6-40mm-vien-nhom-day-cao-su-red-252320-122312.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S6 40mm viền nhôm dây cao su (RED) có pin dung lượng lớn, sạc đầy nhanh chóng\" src=\"https://cdn.tgdd.vn/Products/Images/7077/229054/apple-watch-s6-40mm-vien-nhom-day-cao-su-red-252320-122312.jpg\" /></a></p>\r\n\r\n<h3>Quản l&yacute; sức khỏe của bản th&acirc;n tốt hơn</h3>\r\n\r\n<p>- Đồng hồ lu&ocirc;n&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh-do-nhip-tim\" target=\"_blank\">theo d&otilde;i nhịp tim</a>&nbsp;của bạn mọi l&uacute;c mọi nơi v&agrave; th&ocirc;ng b&aacute;o cho bạn khi nhịp tim cao, thấp hoặc kh&ocirc;ng đều bất thường.</p>\r\n\r\n<p>-&nbsp;Ứng dụng Sleep mới&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh-theo-doi-giac-ngu\" target=\"_blank\">theo d&otilde;i giấc ngủ</a>&nbsp;của bạn hằng đ&ecirc;m v&agrave; x&acirc;y dựng cho bạn một lịch tr&igrave;nh ngủ hợp l&yacute;, gi&uacute;p bạn thiết lập th&oacute;i quen đi ngủ đều đặn, tốt cho sức khỏe.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/229054/apple-watch-s6-40mm-vien-nhom-day-cao-su-red-252320-122319.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S6 40mm viền nhôm dây cao su (RED) có tính năng đo nhịp tim, theo dõi giấc ngủ\" src=\"https://cdn.tgdd.vn/Products/Images/7077/229054/apple-watch-s6-40mm-vien-nhom-day-cao-su-red-252320-122319.jpg\" /></a></p>\r\n\r\n<h3>Tr&aacute;nh những rủi ro kh&ocirc;ng mong muốn với t&iacute;nh năng ph&aacute;t hiện t&eacute; ng&atilde;</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/dong-ho-thong-minh\" target=\"_blank\">Smartwatch</a>&nbsp;c&oacute; t&iacute;nh năng ph&aacute;t hiện khi bạn bị ng&atilde; v&agrave; bất động trong15 gi&acirc;y, n&oacute; sẽ ph&aacute;t t&iacute;n hiệu SOS đến số được c&agrave;i sẵn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/229054/apple-watch-s6-40mm-vien-nhom-day-cao-su-red-252320-122325.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S6 40mm viền nhôm dây cao su (RED) có tính năng phát hiện té ngã\" src=\"https://cdn.tgdd.vn/Products/Images/7077/229054/apple-watch-s6-40mm-vien-nhom-day-cao-su-red-252320-122325.jpg\" /></a></p>\r\n\r\n<h3>Nhiều chế độ tập luyện kh&aacute;c nhau hỗ trợ bạn r&egrave;n luyện cơ thể một c&aacute;ch khoa học, tốt cho sức khỏe</h3>\r\n\r\n<p>Chiếc Apple Watch S6 n&agrave;y được t&iacute;ch hợp nhiều b&agrave;i&nbsp;luyện tập thể thao&nbsp;như nhảy, đạp xe, bơi lội, leo n&uacute;i, yoga, ... gi&uacute;p n&acirc;ng cao hiệu quả tập luyện v&agrave; cải thiện sức khỏe mỗi ng&agrave;y.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/229054/apple-watch-s6-40mm-vien-nhom-day-cao-su-red-252320-122331.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S6 40mm viền nhôm dây cao su (RED) có nhiều chế độ luyện tập khác nhau giúp bạn rèn luyện cơ thể hiệu quả\" src=\"https://cdn.tgdd.vn/Products/Images/7077/229054/apple-watch-s6-40mm-vien-nhom-day-cao-su-red-252320-122331.jpg\" /></a></p>\r\n\r\n<h3>Hỗ trợ người d&ugrave;ng tốt hơn với trợ l&yacute; ảo Siri</h3>\r\n\r\n<p>Siri c&oacute; thể dịch nhanh 10 ng&ocirc;n ngữ tr&ecirc;n thế giới (chưa c&oacute; tiếng Việt), nhận diện giọng n&oacute;i dễ d&agrave;ng, với chức năng n&agrave;y, Siri sẽ gi&uacute;p bạn thực hiện nhanh ch&oacute;ng một v&agrave;i t&aacute;c vụ đơn giản hoặc đưa ra hưỡng dẫn hỗ trợ bạn thực hiện.</p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/images/7077/229054/apple-watch-s6-40mm-vien-nhom-day-cao-su-red-252420-122411.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S6 40mm viền nhôm dây cao su (RED) hỗ trợ người dùng tốt hơn với trợ lý ảo Siri\" src=\"https://cdn.tgdd.vn/Products/Images/7077/229054/apple-watch-s6-40mm-vien-nhom-day-cao-su-red-252420-122411.jpg\" /></a></h3>\r\n\r\n<h3>Dễ d&agrave;ng quản l&yacute; v&agrave; chăm s&oacute;c sức khỏe mọi người trong gia đ&igrave;nh</h3>\r\n\r\n<p>T&iacute;nh năng Family Setup cho ph&eacute;p&nbsp;1 t&agrave;i khoản iCloud c&oacute; thể quản l&yacute; nhiều chiếc đồng hồ th&ocirc;ng minh Apple kh&aacute;c nhau, thuận tiện trong việc chăm s&oacute;c trẻ em v&agrave; người gi&agrave; th&ocirc;ng qua c&aacute;c t&iacute;nh năng: theo d&otilde;i sức khỏe,&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh?g=dinh-vi-tre-em\" target=\"_blank\">định vị trẻ em</a>, ph&aacute;t hiện t&eacute; ng&atilde;,...</p>\r\n\r\n<p>Lưu &yacute;: T&iacute;nh năng n&agrave;y hiện kh&ocirc;ng khả dụng ở Việt Nam.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/229054/apple-watch-s6-40mm-vien-nhom-day-cao-su-red-1.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S6 40mm viền nhôm dây cao su (RED) có tính năng Family Setup hữu ích\" src=\"https://cdn.tgdd.vn/Products/Images/7077/229054/apple-watch-s6-40mm-vien-nhom-day-cao-su-red-1.jpg\" /></a></p>\r\n\r\n<h3>C&ograve;n rất nhiều&nbsp;c&aacute;c tiện&nbsp;&iacute;ch th&uacute; vị&nbsp;kh&aacute;c chờ bạn kh&aacute;m ph&aacute;</h3>\r\n\r\n<p>Apple Watch series 6 sở hữu nhiều tiện &iacute;ch kh&aacute;c như t&igrave;m&nbsp;điện thoại, hướng dẫn&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh?g=lo-trinh-duong-di\" target=\"_blank\">lộ tr&igrave;nh</a>,&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh?g=dem-so-buoc-chan\" target=\"_blank\">đếm số bước ch&acirc;n</a>, dự b&aacute;o thời tiết,&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh?g=dinh-vi\" target=\"_blank\">định vị</a>,&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh?g=nghe-nhac-voi-tai-nghe-bluetooth\" target=\"_blank\">nghe nhạc</a>&nbsp;tr&ecirc;n đồng hồ,&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh?g=tinh-luong-calories-tieu-thu\" target=\"_blank\">t&iacute;nh lượng calories ti&ecirc;u thụ</a>, ... mang đến cho bạn một cuộc sống thoải m&aacute;i, hiện đại.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/229054/apple-watch-s6-40mm-vien-nhom-day-cao-su-red-252320-122342.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S6 40mm viền nhôm dây cao su (RED) còn nhiều tiện ích khác chờ bạn khám phá\" src=\"https://cdn.tgdd.vn/Products/Images/7077/229054/apple-watch-s6-40mm-vien-nhom-day-cao-su-red-252320-122342.jpg\" /></a></p>\r\n\r\n<p>D&ograve;ng Apple Watch đ&aacute;ng mong chờ trong năm 2020 với thiết kế sang trọng v&agrave; hiện đại, d&acirc;y đeo cao su &ecirc;m &aacute;i, l&agrave; phụ kiện thời trang thể hiện đẳng cấp cũng như người trợ thủ l&yacute; tưởng trong thời đại khoa học c&ocirc;ng nghệ 4.0.&nbsp;Hơn thế, Apple Watch Series 6&nbsp;c&ograve;n mang đến những n&acirc;ng cấp ấn tượng như: d&ograve;ng chip S6 cải thiện 20% hiệu năng, hệ điều h&agrave;nh WatchOS 7 được cập nhật th&ecirc;m nhiều t&iacute;nh năng hữu &iacute;ch (Family Setup, nhận biết rửa tay, chia sẻ m&agrave;n h&igrave;nh qua iMessages, Siri dịch nhanh,...).</p>', 4, '2021-04-25 21:36:37', '2021-04-25 22:00:56'),
(14, 'Apple Watch S3 LTE 42mm viền nhôm dây cao su đen', 'apple-watch-s3-lte-42mm-vien-nhom-day-cao-su-ava-600x600.jpg', 8992000, 20, '12 tháng', 'Cáp - Sạc -Tai nghe', '300000', 'Nguyên Seal - Chính Hãng', 1, 1, '<h2>Đặc điểm nổi bật của Apple Watch S3 LTE 42mm viền nh&ocirc;m d&acirc;y cao su đen</h2>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/7077/215838/Slider/apple-watch-s3-lte-42mm-vien-nhom-day-cao-su-301020-1053580.jpg\" /></p>\r\n\r\n<h2><a href=\"https://www.thegioididong.com/dong-ho-thong-minh/apple-watch-s3-lte-42mm-vien-nhom-day-cao-su\" target=\"_blank\">Apple Watch S3 LTE 42 mm</a>&nbsp;viền nh&ocirc;m d&acirc;y cao su l&agrave; phi&ecirc;n bản đồng hồ th&ocirc;ng minh hỗ trợ eSim vừa được giới thiệu. Đồng hồ c&oacute; thiết kế trẻ trung, hiện đại c&ugrave;ng nhiều tiện &iacute;ch về sức khỏe, thể thao d&agrave;nh cho người d&ugrave;ng.&nbsp;</h2>\r\n\r\n<h3>Hỗ trợ eSim&nbsp;</h3>\r\n\r\n<p>Phi&ecirc;n bản đồng hồ Apple Watch S3 LTE t&iacute;ch hợp&nbsp;<strong>eSim</strong>&nbsp;để sử dụng nghe gọi như tr&ecirc;n điện thoại. Dễ d&agrave;ng nghe gọi bất cứ l&uacute;c n&agrave;o m&agrave; kh&ocirc;ng phải lo gi&aacute;n đoạn việc tập luyện hay phải k&egrave; k&egrave; chiếc điện thoại b&ecirc;n cạnh.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/dong-ho-thong-minh-apple-watch-s3\" target=\"_blank\">Apple Watch S3</a>&nbsp;đồng bộ th&ocirc;ng b&aacute;o từ c&aacute;c ứng dụng như Facebook, Zalo, Line, Viber,... gi&uacute;p bạn dễ d&agrave;ng kiểm tra c&aacute;c cuộc gọi, tin nhắn v&agrave; th&ocirc;ng tin.&nbsp;</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/esim-la-gi-esim-co-su-dung-duoc-o-viet-nam-khong-1118062#subhmenuid7\" target=\"_blank\">C&aacute;ch kết nối eSIM tr&ecirc;n smartwatch</a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/215838/apple-watch-s3-lte-42mm-vien-nhom-day-cao-su5.jpg\" onclick=\"return false;\"><img alt=\"Đồng hồ thông minh Apple Watch S3 LTE hỗ trợ eSim\" src=\"https://cdn.tgdd.vn/Products/Images/7077/215838/apple-watch-s3-lte-42mm-vien-nhom-day-cao-su5.jpg\" /></a></p>\r\n\r\n<h3>Nhắc nhở sức khỏe</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/dong-ho-thong-minh-apple\" target=\"_blank\">Apple Watch</a>&nbsp;S3 LTE gi&uacute;p bạn quan t&acirc;m v&agrave; theo d&otilde;i sức khỏe của m&igrave;nh tốt hơn. Đồng hồ c&oacute; t&iacute;nh năng nhắc nhở vận động, đếm bước ch&acirc;n, theo d&otilde;i v&agrave; ph&acirc;n t&iacute;ch giấc ngủ.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/215838/apple-watch-s3-lte-42mm-vien-nhom-day-cao-su1.jpg\" onclick=\"return false;\"><img alt=\"Đồng hồ thông minh Apple Watch S3 LTE \" src=\"https://cdn.tgdd.vn/Products/Images/7077/215838/apple-watch-s3-lte-42mm-vien-nhom-day-cao-su1.jpg\" /></a></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, Apple Watch S3 đo nhịp tim li&ecirc;n tục với độ ch&iacute;nh x&aacute;c cao. Đồng hồ sẽ đưa ra cảnh b&aacute;o nếu ph&aacute;t hiện bất thường để người d&ugrave;ng c&oacute; thể ph&aacute;t hiện kịp thời c&aacute;c dấu hiệu về bệnh tim mạch.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/215838/apple-watch-s3-lte-42mm-vien-nhom-day-cao-su6.jpg\" onclick=\"return false;\"><img alt=\"Đồng hồ thông minh Apple Watch S3 LTE theo dõi nhịp tim 24/24\" src=\"https://cdn.tgdd.vn/Products/Images/7077/215838/apple-watch-s3-lte-42mm-vien-nhom-day-cao-su6.jpg\" /></a></p>\r\n\r\n<h3>M&agrave;n h&igrave;nh hiển thị si&ecirc;u sắc n&eacute;t&nbsp;</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/dong-ho-thong-minh\" target=\"_blank\">Đồng hồ th&ocirc;ng minh</a>&nbsp;Apple Watch c&oacute; m&agrave;n h&igrave;nh&nbsp;<strong>OLED Retina</strong>&nbsp;cho mọi h&igrave;nh ảnh sắc n&eacute;t, hiển thị tốt cả khi sử dụng ngo&agrave;i trời. K&iacute;ch thước&nbsp;<strong>1.65 inch</strong>&nbsp;gi&uacute;p c&aacute;c th&ocirc;ng tin hiển thị to r&otilde;, xem dễ d&agrave;ng hơn.&nbsp;&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/215838/apple-watch-s3-lte-42mm-vien-nhom-day-cao-su2.jpg\" onclick=\"return false;\"><img alt=\"Đồng hồ thông minh Apple Watch S3 LTE màn hình hiển thị chân thực\" src=\"https://cdn.tgdd.vn/Products/Images/7077/215838/apple-watch-s3-lte-42mm-vien-nhom-day-cao-su2.jpg\" /></a></p>\r\n\r\n<h3>Chế độ tập luyện thể thao&nbsp;</h3>\r\n\r\n<p>Thiết bị c&oacute; nhiều b&agrave;i tập cho bạn lựa chọn như: đạp xe, đi bộ, boxing, ch&egrave;o thuyền,... đồng thời t&iacute;nh lượng Calories ti&ecirc;u thụ. Định vị bằng GPS gi&uacute;p bạn x&aacute;c định vị tr&iacute; dễ d&agrave;ng, t&iacute;nh qu&atilde;ng đường chạy một c&aacute;ch hợp l&iacute;.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/215838/apple-watch-s3-lte-42mm-vien-nhom-day-cao-su4.jpg\" onclick=\"return false;\"><img alt=\"Đồng hồ thông minh Apple Watch S3 LTE 42mm hỗ trợ nhiều bài tập cơ bản\" src=\"https://cdn.tgdd.vn/Products/Images/7077/215838/apple-watch-s3-lte-42mm-vien-nhom-day-cao-su4.jpg\" /></a></p>\r\n\r\n<h3>Thiết kế sang trọng, mỏng nhẹ, dễ d&agrave;ng ph&acirc;n biệt</h3>\r\n\r\n<p>Ph&acirc;n biệt 2 phi&ecirc;n bản LTE v&agrave; GPS rất đơn giản bằng c&aacute;ch sau:</p>\r\n\r\n<p>- Apple Watch LTE sẽ c&oacute; n&uacute;m vặn Digital Crown m&agrave;u đỏ nguy&ecirc;n n&uacute;m xoay đối với Series 3 v&agrave; viền đỏ với Series 4 v&agrave; Series 5.</p>\r\n\r\n<p>- Apple Watch GPS thường c&oacute; phần n&uacute;m vặn Digital Crown m&agrave;u trơn, đồng m&agrave;u với th&acirc;n chiếc đồng hồ.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/215838/apple-watch-s3-lte-42mm-vien-nhom-day-cao-su10.jpg\" onclick=\"return false;\"><img alt=\"Đồng hồ thông minh Apple Watch S3 LT nút vặn Digital Crown với màu đỏ nổi bật\" src=\"https://cdn.tgdd.vn/Products/Images/7077/215838/apple-watch-s3-lte-42mm-vien-nhom-day-cao-su10.jpg\" /></a></p>\r\n\r\n<p>D&acirc;y đeo cao su mềm mại, dễ d&agrave;ng th&aacute;o rời tiện lợi, kiểu d&aacute;ng sang trọng, mỏng nhẹ tạo cảm gi&aacute;c dễ chịu khi đeo tr&ecirc;n tay.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/215838/apple-watch-s3-lte-42mm-vien-nhom-day-cao-su9.jpg\" onclick=\"return false;\"><img alt=\"Đồng hồ thông minh Apple Watch S3 LTE thiết kế nổi bật\" src=\"https://cdn.tgdd.vn/Products/Images/7077/215838/apple-watch-s3-lte-42mm-vien-nhom-day-cao-su9.jpg\" /></a></p>', 4, '2021-04-25 21:38:56', '2021-04-25 22:01:28');
INSERT INTO `products` (`id`, `name`, `images`, `price`, `quantity`, `warranty`, `accessories`, `discount`, `status`, `active`, `hot`, `desc`, `categories_id`, `created_at`, `updated_at`) VALUES
(15, 'Apple Watch S6 LTE 40mm viền thép dây cao su xanh', '1-600x600.jpg', 16792000, 10, '12 tháng', 'Cáp - Sạc -Tai nghe', '100000', 'Nguyên Seal - Chính Hãng', 1, 1, '<h2>Đặc điểm nổi bật của Apple Watch S6 LTE 40mm viền th&eacute;p d&acirc;y cao su xanh</h2>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/7077/229036/Slider/apple-watch-s6-lte-40mm-vien-thep-day-cao-su-221020-1217180.jpg\" /></p>\r\n\r\n<h3>Sở hữu thiết kế sang trọng, m&agrave;u sắc thời thượng</h3>\r\n\r\n<p>Chiếc&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh/apple-watch-s6-lte-40mm-vien-thep-day-cao-su\" target=\"_blank\">Apple Watch S6 LTE</a>&nbsp;với&nbsp;<a href=\"https://www.thegioididong.com/day-dong-ho\" target=\"_blank\">d&acirc;y đeo</a>&nbsp;cao su trơn được thiết kế liền mạch c&ugrave;ng sắc xanh tinh tế, m&agrave;n h&igrave;nh tr&agrave;n viền mang lại sự trải nghiệm sắc n&eacute;t, r&otilde; r&agrave;ng. Phần kim loại của th&acirc;n m&aacute;y l&agrave;m từ chất liệu th&eacute;p kh&ocirc;ng gỉ chắc chắn, bền đẹp trước mọi thời tiết l&agrave; những đặc điểm nổi bật gi&uacute;p bạn kh&ocirc;ng thể bỏ qua mẫu đồng hồ n&agrave;y.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/229036/apple-watch-s6-lte-40mm-vien-thep-day-cao-su-fixx-1.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S6 LTE 40mm viền thép dây cao su mang thiết kế sang trọng\" src=\"https://cdn.tgdd.vn/Products/Images/7077/229036/apple-watch-s6-lte-40mm-vien-thep-day-cao-su-fixx-1.jpg\" /></a></p>\r\n\r\n<h3>Cấu h&igrave;nh mạnh mẽ hơn</h3>\r\n\r\n<p>Phần cứng b&ecirc;n trong của chiếc&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh-apple-watch-s6\" target=\"_blank\">Apple watch S6&nbsp;</a>cũng được n&acirc;ng cấp mạnh mẽ khi trang bị bộ chip S6 c&oacute;&nbsp;hiệu năng nhanh hơn 20% so với thế hệ chip Apple S5. Thế hệ chip n&agrave;y mang nhiều ưu điểm vượt trội như:&nbsp;độ mượt tốt v&agrave; khả năng xử l&yacute; phần mềm nhạy b&eacute;n.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/229036/apple-watch-s6-lte-40mm-vien-thep-day-cao-su-241220-101234.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S6 LTE 40mm viền thép dây cao su với bộ chip xử lý S6\" src=\"https://cdn.tgdd.vn/Products/Images/7077/229036/apple-watch-s6-lte-40mm-vien-thep-day-cao-su-241220-101234.jpg\" /></a></p>\r\n\r\n<h3>Sở hữu hệ điều h&agrave;nh mới WatchOS 7</h3>\r\n\r\n<p>WatchOS 7 mang đến một giao diện th&ocirc;ng minh v&agrave; được thiết kế ph&ugrave; hợp với nhiều đối tượng người d&ugrave;ng kh&aacute;c nhau, đem đến cho bạn những trải nghiệm th&uacute; vị.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/229036/apple-watch-s6-lte-40mm-vien-thep-day-cao-su-241220-101242.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S6 LTE 40mm viền thép dây cao su với hệ điều hành WatchOS7\" src=\"https://cdn.tgdd.vn/Products/Images/7077/229036/apple-watch-s6-lte-40mm-vien-thep-day-cao-su-241220-101242.jpg\" /></a></p>\r\n\r\n<h3>Hỗ trợ cảm biến gi&uacute;p chăm s&oacute;c sức khỏe</h3>\r\n\r\n<p>Được hỗ trợ t&iacute;nh năng đo&nbsp;nồng độ oxy trong m&aacute;u SpO2 v&agrave; điện t&acirc;m đồ ECG, đ&acirc;y l&agrave; 2 th&ocirc;ng số m&agrave; c&oacute; rất &iacute;t h&atilde;ng trang bị cho chiếc đồng hồ của m&igrave;nh cho đến thời điểm hiện tại, tuy nhi&ecirc;n Apple đ&atilde; trang bị cho chiếc đồng hồ th&ocirc;ng minh năm 2020 n&agrave;y của m&igrave;nh để gi&uacute;p người d&ugrave;ng theo d&otilde;i sức khỏe tốt hơn.</p>\r\n\r\n<p>* Lưu &yacute;: t&iacute;nh năng hiện chưa khả dụng ở Việt Nam</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/229036/apple-watch-s6-lte-40mm-vien-thep-day-cao-su-162320-122313.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S6 LTE 40mm viền thép dây cao su giúp bạn theo dõi sức khỏe tốt hơn\" src=\"https://cdn.tgdd.vn/Products/Images/7077/229036/apple-watch-s6-lte-40mm-vien-thep-day-cao-su-162320-122313.jpg\" /></a></p>\r\n\r\n<h3>T&iacute;nh năng eSim gi&uacute;p nghe gọi tr&ecirc;n đồng hồ</h3>\r\n\r\n<p>Xem th&ocirc;ng b&aacute;o, tin nhắn,... dễ d&agrave;ng v&agrave; nhanh ch&oacute;ng hơn tr&ecirc;n đồng hồ với&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh-esim\" target=\"_blank\">t&iacute;nh năng eSim</a>. Với t&iacute;nh năng n&agrave;y, chiếc đồng hồ gi&uacute;p bạn&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh?g=nghe-goi-tren-dong-ho\" target=\"_blank\">nghe gọi</a>&nbsp;một c&aacute;ch thuận tiện hơn&nbsp;m&agrave; kh&ocirc;ng cần d&ugrave;ng đến&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\">điện thoại</a>.</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/esim-la-gi-esim-co-su-dung-duoc-o-viet-nam-khong-1118062#subhmenuid7\" target=\"_blank\">C&aacute;ch kết nối eSIM tr&ecirc;n smartwatch</a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/229036/apple-watch-s6-lte-40mm-vien-thep-day-cao-su-fixx-2.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S6 LTE 40mm viền thép dây cao su có tính năng eSim\" src=\"https://cdn.tgdd.vn/Products/Images/7077/229036/apple-watch-s6-lte-40mm-vien-thep-day-cao-su-fixx-2.jpg\" /></a></p>\r\n\r\n<h3>Được trang bị m&agrave;n h&igrave;nh Retina sắc n&eacute;t</h3>\r\n\r\n<p>Sở hữu m&agrave;n h&igrave;nh OLED với nhiều ưu điểm như:&nbsp;m&agrave;u sắc sống động, g&oacute;c nh&igrave;n rộng, cải thiện độ s&aacute;ng,... Hỗ trợ người d&ugrave;ng thuận tiện hơn trong c&ocirc;ng việc nhờ&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh?g=man-hinh-luon-hien-thi\" target=\"_blank\">m&agrave;n h&igrave;nh lu&ocirc;n hiển thị</a>, cung cấp c&aacute;c th&ocirc;ng tin cho bạn mọi l&uacute;c, mọi nơi.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/229036/apple-watch-s6-lte-40mm-vien-thep-day-cao-su-writee-3.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S6 LTE 40mm viền thép dây cao su với màn hình OLED\" src=\"https://cdn.tgdd.vn/Products/Images/7077/229036/apple-watch-s6-lte-40mm-vien-thep-day-cao-su-writee-3.jpg\" /></a></p>\r\n\r\n<h3>Trải nghiệm th&uacute; vị hơn với bộ sưu tập mặt đồng hồ mới</h3>\r\n\r\n<p>Mỗi ng&agrave;y với bạn sẽ l&agrave; một trải nghiệm đầy mới mẻ v&agrave; th&uacute; vị với bộ sưu tập giao diện phong ph&uacute;, đặc biệt hơn với bộ m&agrave;n h&igrave;nh nghệ sĩ mới lạ. Bạn thoải m&aacute;i lựa chọn giao diện ph&ugrave; hợp với sở th&iacute;ch của bản th&acirc;n.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/229036/apple-watch-s6-lte-40mm-vien-thep-day-cao-su-writee-4.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S6 LTE 40mm viền thép dây cao su sở hữu bộ sưu tập giao diện phong cách\" src=\"https://cdn.tgdd.vn/Products/Images/7077/229036/apple-watch-s6-lte-40mm-vien-thep-day-cao-su-writee-4.jpg\" /></a></p>\r\n\r\n<h3>An t&acirc;m hoạt động với thời gian sử dụng pin l&ecirc;n đến 1.5 ng&agrave;y</h3>\r\n\r\n<p>Với thời lượng sử dụng&nbsp;<a href=\"https://www.thegioididong.com/sac-dtdd\" target=\"_blank\">pin</a>&nbsp;l&agrave; 1.5 ng&agrave;y v&agrave; chỉ mất 2 giờ để sạc đầy chiếc&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh\" target=\"_blank\">đồng hồ th&ocirc;ng minh</a>&nbsp;n&agrave;y.&nbsp;Người d&ugrave;ng c&oacute; thể y&ecirc;n t&acirc;m m&igrave;nh sử dụng hết ng&agrave;y d&agrave;i m&agrave; kh&ocirc;ng sạc giữa ng&agrave;y.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/229036/apple-watch-s6-lte-40mm-vien-thep-day-cao-su-writee-5.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S6 LTE 40mm viền thép dây cao su có dung lượng pin tốt\" src=\"https://cdn.tgdd.vn/Products/Images/7077/229036/apple-watch-s6-lte-40mm-vien-thep-day-cao-su-writee-5.jpg\" /></a></p>\r\n\r\n<h3>Hỗ trợ bạn theo d&otilde;i sức khỏe tốt hơn</h3>\r\n\r\n<p>C&aacute;c chức năng như: đo nhịp tim,&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh-theo-doi-giac-ngu\" target=\"_blank\">theo d&otilde;i giấc ngủ</a>,&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh?g=tinh-luong-calories-tieu-thu\" target=\"_blank\">t&iacute;nh lượng calories ti&ecirc;u thụ</a>,... được t&iacute;ch hợp tr&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/dong-ho\" target=\"_blank\">đồng hồ</a>, l&agrave; một c&ocirc;ng cụ hiệu quả v&agrave; tiện lợi gi&uacute;p cuộc sống của bạn l&agrave;nh mạnh hơn.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/229036/apple-watch-s6-lte-40mm-vien-thep-day-cao-su-writee-6.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S6 LTE 40mm viền thép dây cao su hỗ trợ bạn theo dõi sức khỏe tốt hơn\" src=\"https://cdn.tgdd.vn/Products/Images/7077/229036/apple-watch-s6-lte-40mm-vien-thep-day-cao-su-writee-6.jpg\" /></a></p>\r\n\r\n<h3>Dễ d&agrave;ng gọi trợ gi&uacute;p khẩn cấp</h3>\r\n\r\n<p>Với SOS khẩn cấp tr&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh-apple\" target=\"_blank\">Apple Watch</a>, cho khả năng ph&aacute;t hiện khi bạn bị ng&atilde; v&agrave; nếu người d&ugrave;ng bất động trong v&ograve;ng 15 gi&acirc;y, n&oacute; sẽ ph&aacute;t t&iacute;n hiệu SOS đến số đ&atilde; được c&agrave;i sẵn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/229036/apple-watch-s6-lte-40mm-vien-thep-day-cao-su-writee-7.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S6 LTE 40mm viền thép dây cao su có chức năng phát hiện té ngã\" src=\"https://cdn.tgdd.vn/Products/Images/7077/229036/apple-watch-s6-lte-40mm-vien-thep-day-cao-su-writee-7.jpg\" /></a></p>\r\n\r\n<h3>R&egrave;n luyện sức khỏe hiệu quả hơn</h3>\r\n\r\n<p>D&agrave;nh cho cả những vận động vi&ecirc;n chuyện nghiệp v&agrave; những người kh&ocirc;ng chuy&ecirc;n khi gi&uacute;p bạn tạo những th&oacute;i quen l&agrave;nh mạnh, tốt cho sức khỏe với nhiều&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh?g=che-do-luyen-tap\" target=\"_blank\">chế độ luyện tập</a>&nbsp;như: leo n&uacute;i, yoga, chạy bộ hay bơi lội,...&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/229036/apple-watch-s6-lte-40mm-vien-thep-day-cao-su-writee-8.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S6 LTE 40mm viền thép dây cao su với nhiều chế độ luyện tập\" src=\"https://cdn.tgdd.vn/Products/Images/7077/229036/apple-watch-s6-lte-40mm-vien-thep-day-cao-su-writee-8.jpg\" /></a></p>\r\n\r\n<h3>Giữ li&ecirc;n lạc với c&aacute;c th&agrave;nh vi&ecirc;n trong gia đ&igrave;nh nhờ t&iacute;nh năng Family Setup</h3>\r\n\r\n<p>T&iacute;nh năng n&agrave;y cho ph&eacute;p một t&agrave;i khoản iCloud c&oacute; thể quản l&yacute; nhiều chiếc đồng hồ th&ocirc;ng minh Apple kh&aacute;c nhau của c&aacute;c th&agrave;nh vi&ecirc;n trong gia đ&igrave;nh, th&iacute;ch hợp để chăm s&oacute;c trẻ em th&ocirc;ng qua việc theo d&otilde;i vị tr&iacute; hoặc ph&aacute;t hiện tai nạn t&eacute; ng&atilde;.</p>\r\n\r\n<p>Lưu &yacute;: T&iacute;nh năng n&agrave;y hiện kh&ocirc;ng khả dụng ở Việt Nam.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/229036/apple-watch-s6-lte-40mm-vien-thep-day-cao-su-1.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S6 LTE 40mm viền thép dây cao su có tính năng Family Setup\" src=\"https://cdn.tgdd.vn/Products/Images/7077/229036/apple-watch-s6-lte-40mm-vien-thep-day-cao-su-1.jpg\" /></a></p>\r\n\r\n<h3>Trợ l&yacute; ảo Siri cho cuộc sống th&ecirc;m dễ d&agrave;ng hơn</h3>\r\n\r\n<p>Với chức năng nhận diện giọng n&oacute;i r&otilde; r&agrave;ng, người d&ugrave;ng c&oacute; thể đặt lời nhắc, tra cứu th&ocirc;ng tin,... một c&aacute;ch nhanh ch&oacute;ng.</p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/images/7077/229036/apple-watch-s6-lte-40mm-vien-thep-day-cao-su-241220-101249.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S6 LTE 40mm viền thép dây cao su tiện ích với trợ lý ảo Siri\" src=\"https://cdn.tgdd.vn/Products/Images/7077/229036/apple-watch-s6-lte-40mm-vien-thep-day-cao-su-241220-101249.jpg\" /></a></h3>\r\n\r\n<h3>Th&ecirc;m nhiều t&iacute;nh năng tiện lợi</h3>\r\n\r\n<p>Người d&ugrave;ng c&oacute; thể điều khiển ph&aacute;t nhạc,&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh?g=dem-so-buoc-chan\" target=\"_blank\">đếm số bước ch&acirc;n</a>,&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh?g=dinh-vi\" target=\"_blank\">định vị</a>,&nbsp;xem dự b&aacute;o t&igrave;nh h&igrave;nh thời tiết hay t&igrave;m kiếm điện thoại th&ocirc;ng minh của m&igrave;nh,... tất cả chỉ với những thao t&aacute;c đơn giản tr&ecirc;n chiếc đồng hồ đeo tr&ecirc;n tay.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/229036/apple-watch-s6-lte-40mm-vien-thep-day-cao-su-writee-10.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S6 LTE 40mm viền thép dây cao su vơi nhiều tiện ích khác\" src=\"https://cdn.tgdd.vn/Products/Images/7077/229036/apple-watch-s6-lte-40mm-vien-thep-day-cao-su-writee-10.jpg\" /></a></p>\r\n\r\n<p>Với thiết kế mang t&iacute;nh biểu tượng của Apple, kiểu d&aacute;ng c&ugrave;ng m&agrave;u sắc hiện đại v&agrave; thời thượng,&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh?g=silicon\" target=\"_blank\">d&acirc;y đeo cao su</a>&nbsp;c&oacute; trọng lượng nhẹ, kh&ocirc;ng thấm nước v&agrave; mồ h&ocirc;i, c&oacute; độ đ&agrave;n hồi tốt gi&uacute;p bạn thoải m&aacute;i khi đeo cả ng&agrave;y d&agrave;i. Phi&ecirc;n bản&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh-apple-watch-s6\" target=\"_blank\">Apple Watch Series 6</a>&nbsp;n&agrave;y đ&atilde; được cải tiến v&agrave; n&acirc;ng cấp cả về phần cứng v&agrave; phần mềm, cho tốc độ xử l&iacute; nhanh v&agrave; mượt m&agrave; hơn. C&aacute;c chức năng như chế độ luyện tập đa dạng,&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh-do-nhip-tim\" target=\"_blank\">theo d&otilde;i nhịp tim</a>, theo d&otilde;i giấc ngủ, ph&aacute;t hiện t&eacute; ng&atilde; sẽ hỗ trợ bạn bảo vệ sức khỏe tốt hơn. Nh&igrave;n chung, với c&aacute;c t&iacute;nh năng vượt trội c&ugrave;ng thiết kế đẳng cấp, mẫu đồng hồ rất th&iacute;ch hợp với những t&iacute;n đồ y&ecirc;u c&ocirc;ng nghệ hay c&aacute;c bạn văn ph&ograve;ng hiện đại, hứa hẹn đem đến cho người d&ugrave;ng những tiện &iacute;ch v&agrave; những trải nghiệm th&uacute; vị.&nbsp;</p>', 4, '2021-04-25 21:40:35', '2021-04-25 21:59:40'),
(16, 'Apple Watch S5 LTE 44mm viền thép dây thép đen', '6.jpg', 19392000, 10, '12 tháng', 'Cáp - Sạc -Tai nghe', '300000', 'Nguyên Seal - Chính Hãng', 1, 1, '<h2>Đặc điểm nổi bật của Apple Watch S5 LTE 44mm viền th&eacute;p d&acirc;y th&eacute;p đen</h2>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/7077/215816/Slider/apple-watch-s5-lte-day-thep-301020-1019200.jpg\" /></p>\r\n\r\n<h3>Với kiểu d&aacute;ng thiết kế sang trọng&nbsp;</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/dong-ho-thong-minh/apple-watch-s5-lte-day-thep#danhgia\" target=\"_blank\">Apple Watch S5 LTE 44mm viền th&eacute;p d&acirc;y th&eacute;p</a>&nbsp;được thiết kế bằng d&acirc;y th&eacute;p Milanese sang trọng hơn rất nhiều nhờ viền th&eacute;p mạnh mẽ v&agrave; d&acirc;y đeo thiết kế dạng lưới đan mỏng xuất ph&aacute;t từ v&ugrave;ng đất Milan xinh đẹp, vừa mềm mại, lại vừa qu&yacute; ph&aacute;i.&nbsp;Đồng thời, m&agrave;n h&igrave;nh đồng hồ c&oacute; viền mỏng hơn, trọng lượng nhẹ hơn, m&agrave;n h&igrave;nh lớn hơn mang lại sự trải nghiệm đẳng cấp&nbsp;hơn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/215816/apple-watch-s5-lte-day-thep-11.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế đẳng cấp\" src=\"https://cdn.tgdd.vn/Products/Images/7077/215816/apple-watch-s5-lte-day-thep-11.jpg\" /></a></p>\r\n\r\n<h3>M&agrave;n h&igrave;nh Always-On Retina lu&ocirc;n lu&ocirc;n bật</h3>\r\n\r\n<p>Một điểm nổi trội của c&ocirc;ng nghệ Always-On tr&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh-apple\" target=\"_blank\">Apple Watch</a>&nbsp;S5 LTE l&agrave; việc lu&ocirc;n lu&ocirc;n hiển thị thời gian v&agrave; những th&ocirc;ng b&aacute;o tr&ecirc;n m&agrave;n h&igrave;nh bất cứ l&uacute;c n&agrave;o m&agrave; vẫn đảm bảo tiết kiệm năng lượng hiệu quả. Giờ bạn chỉ cần liếc ngang qua v&agrave; xem giờ chứ kh&ocirc;ng cần đưa tay l&ecirc;n cho m&agrave;n h&igrave;nh bật s&aacute;ng nữa.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/215816/apple-watch-s5-lte-day-thep-cont-2.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S5 LTE 44mm viền thép dây thép với công nghệ màn hình OLED\" src=\"https://cdn.tgdd.vn/Products/Images/7077/215816/apple-watch-s5-lte-day-thep-cont-2.jpg\" /></a></p>\r\n\r\n<h3>Ph&acirc;n biệt 2 phi&ecirc;n bản LTE v&agrave; GPS dựa v&agrave;o n&uacute;m vặn Digital Crown&nbsp;</h3>\r\n\r\n<p>- Apple Watch LTE sẽ c&oacute; n&uacute;m vặn Digital Crown m&agrave;u đỏ nguy&ecirc;n n&uacute;m xoay đối với Series 3 v&agrave; viền đỏ với Series 4 v&agrave; Series 5.</p>\r\n\r\n<p>- Apple Watch GPS thường c&oacute; phần n&uacute;m vặn Digital Crown m&agrave;u trơn, đồng m&agrave;u với th&acirc;n chiếc đồng hồ.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/215816/apple-watch-s5-lte-day-thep-cont-10.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S5 LTE 44mm viền thép dây thép có núm vặn Digital Crown giúp người dùng phân biệt bản LTE và GPS\" src=\"https://cdn.tgdd.vn/Products/Images/7077/215816/apple-watch-s5-lte-day-thep-cont-10.jpg\" /></a></p>\r\n\r\n<h3>T&iacute;ch hợp t&iacute;nh năng eSim, kết nối qua Wifi, Bluetooth v5.0</h3>\r\n\r\n<p>Apple Watch S5 LTE c&oacute; hỗ trợ t&iacute;nh năng eSim, cho ph&eacute;p nhận những cuộc gọi v&agrave; th&ocirc;ng b&aacute;o như 1 chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\">điện thoại</a>&nbsp;độc lập. Khi kết nối qua Wifi, bạn sẽ c&oacute; một kho ứng dụng App Store nằm ngay tr&ecirc;n cổ tay. Kết nối qua Bluetooth để nhận được th&ocirc;ng b&aacute;o tin nhắn, c&aacute;c cuộc gọi, Messenger (Facebook), Line, Viber,...</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/esim-la-gi-esim-co-su-dung-duoc-o-viet-nam-khong-1118062#subhmenuid7\" target=\"_blank\">C&aacute;ch kết nối eSIM tr&ecirc;n smartwatch</a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/215816/apple-watch-s5-lte-day-thep-cont-3.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S5 LTE 44mm viền thép dây thép tích hợp eSim, kết nối Wifi, Bluetooth\" src=\"https://cdn.tgdd.vn/Products/Images/7077/215816/apple-watch-s5-lte-day-thep-cont-3.jpg\" /></a></p>\r\n\r\n<h3>Thời gian sử dụng 1.5 ng&agrave;y v&agrave; bộ nhớ trong l&ecirc;n đến 32GB</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/cap-dien-thoai\" target=\"_blank\">C&aacute;p sạc</a>&nbsp;từ t&iacute;nh đi k&egrave;m theo&nbsp;<a href=\"https://www.thegioididong.com/dong-ho\" target=\"_blank\">đồng hồ</a>&nbsp;sẽ đầy pin trong v&ograve;ng gần 2 giờ v&agrave; người d&ugrave;ng c&oacute; thể d&ugrave;ng để luyện tập thể thao trong 1.5 ng&agrave;y, đ&aacute;ng buồn l&agrave; nếu bạn qu&ecirc;n sạc n&oacute; qua đ&ecirc;m th&igrave; h&ocirc;m sau bạn sẽ phải để đồng hồ ở nh&agrave;. Điểm tốt v&agrave; tuyệt vời nhất l&agrave; bạn sẽ c&oacute; đến 32GB bộ nhớ trong v&agrave; c&oacute; cả 1 cửa h&agrave;ng ứng dụng ri&ecirc;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/215816/apple-watch-s5-lte-day-thep-301520-101535.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S5 LTE 44mm viền thép dây thép thời lượng pin 1.5 ngày và dung lượng bộ nhớ 32GB\" src=\"https://cdn.tgdd.vn/Products/Images/7077/215816/apple-watch-s5-lte-day-thep-301520-101535.jpg\" /></a></p>\r\n\r\n<h3>Cảm biến đo nhịp tim li&ecirc;n tục</h3>\r\n\r\n<p>Apple đ&atilde; mang tới cho Apple Watch S5 LTE ứng dụng ECG &ndash; Đ&acirc;y l&agrave; c&ocirc;ng nghệ cho ph&eacute;p đồng hồ th&ocirc;ng minh c&oacute; thể theo d&otilde;i nhịp tim người d&ugrave;ng một c&aacute;ch li&ecirc;n tục v&agrave; hiệu quả, sau đ&oacute; sẽ đưa ra những th&ocirc;ng b&aacute;o kịp thời, chi tiết về t&igrave;nh trạng sức khỏe người d&ugrave;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/215816/apple-watch-s5-lte-day-thep-cont-5.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S5 LTE 44mm viền thép dây thép đo nhịp tim liên tục\" src=\"https://cdn.tgdd.vn/Products/Images/7077/215816/apple-watch-s5-lte-day-thep-cont-5.jpg\" /></a></p>\r\n\r\n<h3>Theo d&otilde;i chu k&igrave; cho ph&aacute;i nữ</h3>\r\n\r\n<p>Cycle Tracking - T&iacute;nh năng hỗ trợ ph&aacute;i nữ theo d&otilde;i chi tiết chu kỳ kinh nguyệt h&agrave;ng th&aacute;ng của bản th&acirc;n. Sau khi nhập đủ c&aacute;c th&ocirc;ng tin cần thiết, chiếc đồng&nbsp; hồ n&agrave;y sẽ tự t&iacute;nh to&aacute;n v&agrave; theo d&otilde;i chu kỳ h&agrave;ng th&aacute;ng, sau đ&oacute; kết quả sẽ được th&ocirc;ng b&aacute;o cho người d&ugrave;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/215816/apple-watch-s5-lte-day-thep-cont-7.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S5 LTE 44mm viền thép dây thép cảnh báo tiếng ồn và theo dõi chu kì cho phụ nữ\" src=\"https://cdn.tgdd.vn/Products/Images/7077/215816/apple-watch-s5-lte-day-thep-cont-7.jpg\" /></a></p>\r\n\r\n<h3>C&ocirc;ng nghệ ph&aacute;t hiện t&eacute; ng&atilde;</h3>\r\n\r\n<p>Đồng hồ Apple Watch S5 LTE sẽ ph&aacute;t t&iacute;n hiệu SOS đến số điện thoại đ&atilde; được lưu sẵn nếu ph&aacute;t hiện người d&ugrave;ng đeo đồng hồ bị&nbsp; tai nạn t&eacute; ng&atilde; v&agrave; bất động trong v&ograve;ng 15 gi&acirc;y.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/215816/apple-watch-s5-lte-day-thep-cont-6.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S5 LTE 44mm viền thép dây thép với tính năng phát hiện té ngã\" src=\"https://cdn.tgdd.vn/Products/Images/7077/215816/apple-watch-s5-lte-day-thep-cont-6.jpg\" /></a></p>\r\n\r\n<h3>Tăng cường hoạt động thể dục thể thao</h3>\r\n\r\n<p>Apple Watch S5 LTE l&agrave; thiết bị l&yacute; tưởng để bạn tập thể dục hiệu quả, với những chỉ số tập luyện n&acirc;ng cao, định vị GPS v&agrave; khả năng chống nước độ s&acirc;u 50m. Đồng hồ sẽ theo d&otilde;i tiến tr&igrave;nh tập luyện, truyền cảm hứng, cảnh b&aacute;o khi bạn ngồi qu&aacute; l&acirc;u, khuyến kh&iacute;ch đi lại nhiều hơn v&agrave; tập luyện mỗi ng&agrave;y. Nhờ vậy, sức khỏe của bạn sẽ được cải thiện r&otilde; rệt.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/215816/apple-watch-s5-lte-day-thep-cont-8.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S5 LTE 44mm viền thép dây thép trao nguồn cảm hứng tập luyện thể thao\" src=\"https://cdn.tgdd.vn/Products/Images/7077/215816/apple-watch-s5-lte-day-thep-cont-8.jpg\" /></a></p>\r\n\r\n<h3>Rất nhiều tiện &iacute;ch tuyệt vời kh&aacute;c</h3>\r\n\r\n<p>Gồm nhiều tiện &iacute;ch kh&aacute;c như dự b&aacute;o thời tiết, rung th&ocirc;ng b&aacute;o, la b&agrave;n, đồng hồ bấm giờ, điều khiển chơi nhạc, b&aacute;o thức, t&igrave;m điện thoại,... với c&aacute;ch d&ugrave;ng cực k&igrave; đơn giản.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/215816/apple-watch-s5-lte-day-thep-cont-9.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S5 LTE 44mm viền thép dây thép gồm rất nhiều tiện ích khác\" src=\"https://cdn.tgdd.vn/Products/Images/7077/215816/apple-watch-s5-lte-day-thep-cont-9.jpg\" /></a></p>\r\n\r\n<p>Apple Watch S5 LTE với c&ocirc;ng nghệ đo nhịp tim cực k&igrave; ch&iacute;nh x&aacute;c, theo d&otilde;i chu k&igrave; kinh nguyệt, ph&aacute;t hiện người d&ugrave;ng gặp tai nạn, khuyến kh&iacute;ch luyện tập thể thao v&agrave; quan trọng l&agrave; đeo rất thoải m&aacute;i. Với những t&iacute;nh năng tr&ecirc;n, Apple Watch S5 LTE chắc chắn sẽ mang lại những trải nghiệm tuyệt vời v&agrave; hiệu quả đến người d&ugrave;ng.</p>', 4, '2021-04-25 21:41:28', '2021-04-25 21:48:25'),
(17, 'Apple Watch S6 40mm viền nhôm dây cao su đen', '7.jpg', 9990000, 10, '12 tháng', 'Cáp - Sạc -Tai nghe', '99000', 'Nguyên Seal - Chính Hãng', 1, 0, '<h2>Đặc điểm nổi bật của Apple Watch S6 40mm viền nh&ocirc;m d&acirc;y cao su đen</h2>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/7077/234915/Slider/vi-vn-s6-40mm-vien-nhom-day-cao-su-den-01.jpg\" /></p>\r\n\r\n<h3>Thiết kế ph&ugrave; hợp cho tất cả</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/dong-ho-thong-minh/s6-40mm-vien-nhom-day-cao-su-den\" target=\"_blank\">Apple Watch series 6 40 mm</a>&nbsp;viền nh&ocirc;m d&acirc;y cao su l&agrave; c&oacute; thiết kế kh&ocirc;ng kh&aacute;c với người tiền nhiệm, tuy nhi&ecirc;n với thiết kế đ&atilde; qu&aacute; ho&agrave;n hảo, c&aacute;c chi tiết được l&agrave;m một c&aacute;ch tinh tế, m&agrave;u sắc v&agrave; k&iacute;ch thước ph&ugrave; hợp với mọi lứa tuổi v&agrave; giới t&iacute;nh, chiếc đồng hồ sẽ thu h&uacute;t mọi &aacute;nh nh&igrave;n khi xuất hiện.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/234915/s6-40mm-vien-nhom-day-cao-su-den-01.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S6 40mm viền nhôm dây cao su đen - thiết kế\" src=\"https://cdn.tgdd.vn/Products/Images/7077/234915/s6-40mm-vien-nhom-day-cao-su-den-01.jpg\" /></a></p>\r\n\r\n<h3>Con chip mạnh mẽ</h3>\r\n\r\n<p>Đ&atilde; qu&aacute; nổi tiếng với những con chip mạnh vượt trội, chip xử l&yacute; với hiệu năng nhanh hơn 20% so với thế hệ chip Apple S5 trước đ&acirc;y đ&atilde; được &aacute;p dụng l&ecirc;n Apple S6 gi&uacute;p cho người d&ugrave;ng c&oacute; những trải nghiệm ho&agrave;n hảo hơn tr&ecirc;n chiếc đồng hồ.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/234915/s6-40mm-vien-nhom-day-cao-su-den-11.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S6 40mm viền nhôm dây cao su đen - chip S6\" src=\"https://cdn.tgdd.vn/Products/Images/7077/234915/s6-40mm-vien-nhom-day-cao-su-den-11.jpg\" /></a></p>\r\n\r\n<h3>Hệ điều h&agrave;nh mới mang đến nhiều t&iacute;nh năng</h3>\r\n\r\n<p>Kết hợp với con chip mạnh ch&iacute;nh l&agrave; hệ điều h&agrave;nh tối ưu v&agrave; tương th&iacute;ch ho&agrave;n to&agrave;n với bộ vi xử l&yacute;, mang đến khả năng vận h&agrave;nh mạnh mẽ trơn tru. Kh&ocirc;ng chỉ vậy, hệ điều h&agrave;nh&nbsp;watchOS 7 c&ograve;n mang đến nhiều t&iacute;nh năng mới hiện đại hơn, gi&uacute;p cho người d&ugrave;ng trải nghiệm cuộc sống ho&agrave;n hảo hơn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/234915/s6-40mm-vien-nhom-day-cao-su-den-12.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S6 40mm viền nhôm dây cao su đen - watchOS 7\" src=\"https://cdn.tgdd.vn/Products/Images/7077/234915/s6-40mm-vien-nhom-day-cao-su-den-12.jpg\" /></a></p>\r\n\r\n<h3>Sở hữu m&agrave;n h&igrave;nh đỉnh cao</h3>\r\n\r\n<p>Với tấm m&agrave;n OLED cho độ s&aacute;ng cao v&agrave; độ tương phản tốt, với k&iacute;ch thước 1.57 inch c&ugrave;ng độ ph&acirc;n giải&nbsp;324 x 394&nbsp;pixels, mang đến trải nghiệm nh&igrave;n tuyệt vời tr&ecirc;n chiếc đồng hồ.</p>\r\n\r\n<p>Mặt của chiếc đồng hồ n&agrave;y được bảo vệ bởi mặt k&iacute;nh Ion-X strengthened glass gi&uacute;p cho người d&ugrave;ng y&ecirc;n t&acirc;m hơn khi đeo, kh&ocirc;ng c&ograve;n lo lắng qu&aacute; nhiều về vấn đề trầy xước.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/234915/s6-40mm-vien-nhom-day-cao-su-den-03.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S6 40mm viền nhôm dây cao su đen - màn hình OLED\" src=\"https://cdn.tgdd.vn/Products/Images/7077/234915/s6-40mm-vien-nhom-day-cao-su-den-03.jpg\" /></a></p>\r\n\r\n<h3>Nghe gọi tr&ecirc;n đồng hồ th&ocirc;ng qua kết nối Bluetooth</h3>\r\n\r\n<p>Chiếc&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh-apple\" target=\"_blank\">Apple Watch</a>&nbsp;được trang bị t&iacute;nh năng&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh?g=nghe-goi-tren-dong-ho\" target=\"_blank\">nghe, gọi</a>&nbsp;đồng hồ, hỗ trợ người d&ugrave;ng nhận th&ocirc;ng b&aacute;o, tin nhắn tr&ecirc;n c&aacute;c nền tảng ứng dụng x&atilde; hội như (Facebook, Zalo, Viber,...) th&ocirc;ng qua kết nối Bluetooth, gi&uacute;p bạn kh&ocirc;ng bỏ lỡ những th&ocirc;ng tin quan trọng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/234915/s6-40mm-vien-nhom-day-cao-su-den-02.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S6 40mm viền nhôm dây cao su đen - nhận thông báo trên đồng hồ\" src=\"https://cdn.tgdd.vn/Products/Images/7077/234915/s6-40mm-vien-nhom-day-cao-su-den-02.jpg\" /></a></p>\r\n\r\n<h3>C&oacute; ri&ecirc;ng cho m&igrave;nh mặt&nbsp;<a href=\"https://www.thegioididong.com/dong-ho\" target=\"_blank\">đồng hồ</a>&nbsp;y&ecirc;u th&iacute;ch</h3>\r\n\r\n<p>Chiếc đồng hồ c&ograve;n cho bạn chọn mặt đồng hồ với một bộ sưu tập đa dạng, hơn nữa người d&ugrave;ng c&ograve;n c&oacute; thể tự tạo cho m&igrave;nh ri&ecirc;ng một mặt đồng hồ để c&oacute; thỏa sức s&aacute;ng tạo ngay tr&ecirc;n chiếc&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh\" target=\"_blank\">đồng hồ th&ocirc;ng minh</a>&nbsp;của m&igrave;nh.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/234915/s6-40mm-vien-nhom-day-cao-su-den-04.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S6 40mm viền nhôm dây cao su đen - bộ sưu tập mặt đồng hồ\" src=\"https://cdn.tgdd.vn/Products/Images/7077/234915/s6-40mm-vien-nhom-day-cao-su-den-04.jpg\" /></a></p>\r\n\r\n<h3>Y&ecirc;n t&acirc;m hơn với thời lượng pin</h3>\r\n\r\n<p>kh&ocirc;ng c&ograve;n lo lắng v&igrave; chiếc&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh-apple-watch-s6\" target=\"_blank\">Apple Watch Series 6</a>&nbsp;sẽ hết pin giữa ng&agrave;y nữa, với thời lượng pin l&ecirc;n đến 1.5 ng&agrave;y c&ugrave;ng thời gian sạc chỉ 2 giờ bạn ho&agrave;n to&agrave;n c&oacute; thể đeo đồng hồ trong cả ng&agrave;y d&agrave;i đi l&agrave;m, đi chơi,...</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/234915/s6-40mm-vien-nhom-day-cao-su-den-05.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S6 40mm viền nhôm dây cao su đen - pin 1.5 ngày\" src=\"https://cdn.tgdd.vn/Products/Images/7077/234915/s6-40mm-vien-nhom-day-cao-su-den-05.jpg\" /></a></p>\r\n\r\n<h3>Hỗ trợ chăm s&oacute;c sức khỏe người d&ugrave;ng</h3>\r\n\r\n<p>Gi&uacute;p người d&ugrave;ng theo d&otilde;i, nắm bắt v&agrave; cải thiện sức khỏe của m&igrave;nh, đưa ra c&aacute;c th&ocirc;ng số như:&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh-theo-doi-giac-ngu\" target=\"_blank\">theo d&otilde;i giấc ngủ</a>, theo d&otilde;i nhịp tim,&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh?g=tinh-luong-calories-tieu-thu\" target=\"_blank\">t&iacute;nh lượng calories ti&ecirc;u thụ</a>&nbsp;v&agrave; nhiều t&iacute;nh năng chăm s&oacute;c sức khỏe kh&aacute;c gi&uacute;p cho bạn c&oacute; thể hiểu r&otilde; t&igrave;nh trạng sức khỏe của ch&iacute;nh bản th&acirc;n m&igrave;nh.</p>\r\n\r\n<p>Đặc biệt hơn chiếc đồng hồ c&ograve;n c&oacute; t&iacute;nh năng đo nồng độ oxy trong m&aacute;u SpO2 v&agrave; điện t&acirc;m đồ ECG, l&agrave; hai t&iacute;nh năng mới, c&ograve;n rất &iacute;t h&atilde;ng hỗ trợ.</p>\r\n\r\n<p><strong>Lưu &yacute;</strong>: hai t&iacute;nh tăng SpO2 v&agrave; ECG hiện chưa khả dụng ở Việt Nam.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/234915/s6-40mm-vien-nhom-day-cao-su-den-06.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S6 40mm viền nhôm dây cao su đen - theo dõi sức khỏe\" src=\"https://cdn.tgdd.vn/Products/Images/7077/234915/s6-40mm-vien-nhom-day-cao-su-den-06.jpg\" /></a></p>\r\n\r\n<h3>T&iacute;nh năng ph&aacute;t hiện t&eacute; ng&atilde;</h3>\r\n\r\n<p>Đ&acirc;y l&agrave; một t&iacute;nh năng thể hiện sự tối ưu v&agrave; hiện đại của chiếc đồng hồ khi người d&ugrave;ng v&ocirc; t&igrave;nh bị vấp hay bị t&aacute;c động ngoại lực dẫn đến mất thăng bằng v&agrave; ng&atilde; sụp xuống, Apple Watch Series 6 sẽ tự động ph&aacute;t chu&ocirc;ng b&aacute;o động v&agrave; hiện 3 tuỳ chọn: li&ecirc;n hệ dịch vụ khẩn cấp, huỷ chu&ocirc;ng cảnh b&aacute;o nếu người d&ugrave;ng kh&ocirc;ng bị chấn thương hoặc tắt cảnh b&aacute;o nếu kh&ocirc;ng t&eacute; ng&atilde;.</p>\r\n\r\n<p>Đ&acirc;y chắc chắn sẽ l&agrave; t&iacute;nh năng đ&aacute;ng gi&aacute; nhất đối với những gia đ&igrave;nh c&oacute; người gi&agrave;, sức khỏe kh&ocirc;ng được ổn định.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/234915/s6-40mm-vien-nhom-day-cao-su-den-07.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S6 40mm viền nhôm dây cao su đen - phát hiện té ngã\" src=\"https://cdn.tgdd.vn/Products/Images/7077/234915/s6-40mm-vien-nhom-day-cao-su-den-07.jpg\" /></a></p>\r\n\r\n<h3>L&agrave; một huấn luyện vi&ecirc;n luyện tập chuy&ecirc;n nghiệp</h3>\r\n\r\n<p>Chiếc đồng hồ n&agrave;y c&ograve;n được trang bị những&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh?g=che-do-luyen-tap\" target=\"_blank\">b&agrave;i tập</a>&nbsp;từ cơ bản đến n&acirc;ng cao gi&uacute;p người d&ugrave;ng cải thiện sức khỏe h&agrave;ng ng&agrave;y th&ocirc;ng qua c&aacute;c b&agrave;i tập khoa học hơn mang lại hiệu quả cao hơn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/dong-ho-thong-minh?g=silicon\" target=\"_blank\">D&acirc;y đeo cao su</a>&nbsp;của chiếc đồng hồ mang đến cho người d&ugrave;ng thoải m&aacute;i hơn trong l&uacute;c luyện tập.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/234915/s6-40mm-vien-nhom-day-cao-su-den-08.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S6 40mm viền nhôm dây cao su đen - nhiều chế độ luyện tập\" src=\"https://cdn.tgdd.vn/Products/Images/7077/234915/s6-40mm-vien-nhom-day-cao-su-den-08.jpg\" /></a></p>\r\n\r\n<h3>Trợ l&yacute; ảo Siry gi&uacute;p người d&ugrave;ng trải nghiệm tốt hơn</h3>\r\n\r\n<p>Với 1 c&ocirc; trợ l&yacute; ho&agrave;n hảo, c&oacute; thể gi&uacute;p bạn thao t&aacute;c mọi thứ tr&ecirc;n đồng hồ, chỉ cần bạn ra lệnh bằng giọng n&oacute;i v&agrave; trải nghiệm những th&uacute; vị m&agrave; c&ocirc; trợ l&yacute; n&agrave;y mang đến.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/234915/s6-40mm-vien-nhom-day-cao-su-den-13.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S6 40mm viền nhôm dây cao su đen - trợ lý Siri\" src=\"https://cdn.tgdd.vn/Products/Images/7077/234915/s6-40mm-vien-nhom-day-cao-su-den-13.jpg\" /></a></p>\r\n\r\n<h3>Kết nối gia đ&igrave;nh với t&iacute;nh năng&nbsp;Family Setup&nbsp;</h3>\r\n\r\n<p>T&iacute;nh năng Family Setup cho ph&eacute;p nhiều chiếc Apple watch kết nối với một t&agrave;i khoản iCloud, hỗ trợ bạn chăm s&oacute;c người cao tuổi tốt hơn th&ocirc;ng qua chức năng định vị, ph&aacute;t hiện t&eacute; ng&atilde;,... Ngo&agrave;i ra d&ograve;ng seri 6 c&ograve;n c&oacute; th&ecirc;m t&iacute;nh năng Kids mode, gi&uacute;p cho người d&ugrave;ng l&agrave; phụ huynh c&oacute; thể quan t&acirc;m v&agrave; biết r&otilde; về con của m&igrave;nh.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/234915/s6-40mm-vien-nhom-day-cao-su-den-09.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S6 40mm viền nhôm dây cao su đen - Family Setup\" src=\"https://cdn.tgdd.vn/Products/Images/7077/234915/s6-40mm-vien-nhom-day-cao-su-den-09.jpg\" /></a></p>\r\n\r\n<p><strong>Lưu &yacute;</strong>: T&iacute;nh năng n&agrave;y hiện kh&ocirc;ng khả dụng ở Việt Nam.</p>\r\n\r\n<h3>Trải nghiệm th&ecirc;m nhiều tiện &iacute;ch</h3>\r\n\r\n<p>Ngo&agrave;i những t&iacute;nh năng kể tr&ecirc;n chiếc đồng hồ c&ograve;n c&oacute; nhiều t&iacute;nh năng kh&aacute;c như&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh?g=man-hinh-luon-hien-thi\" target=\"_blank\">m&agrave;n h&igrave;nh lu&ocirc;n hiển thị</a>,&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh?g=dem-so-buoc-chan\" target=\"_blank\">đếm số bước ch&acirc;n</a>, dự b&aacute;o thời tiết, tr&igrave;nh ph&aacute;t nhạc, nhận th&ocirc;ng b&aacute;o từ thiết bị, đồng hồ bấm giờ, t&igrave;m&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\">điện thoại</a>,...</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/234915/s6-40mm-vien-nhom-day-cao-su-den-10.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch S6 40mm viền nhôm dây cao su đen - nhiều tiện ích\" src=\"https://cdn.tgdd.vn/Products/Images/7077/234915/s6-40mm-vien-nhom-day-cao-su-den-10.jpg\" /></a></p>\r\n\r\n<p>Với thiết kế hiện đại,&nbsp;<a href=\"https://www.thegioididong.com/day-dong-ho\" target=\"_blank\">d&acirc;y đeo</a>&nbsp;cao su &ecirc;m &aacute;i, c&ugrave;ng với đ&oacute; con chip mạnh mẽ, hệ điều h&agrave;nh tối ưu, v&agrave; nhiều t&iacute;nh năng đi k&egrave;m sẽ l&agrave;m cho bạn h&agrave;i l&ograve;ng khi d&ugrave;ng. Chiếc&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh-apple\" target=\"_blank\">Apple Watch</a>&nbsp;n&agrave;y hứa hẹn đem đến cho bạn sự h&agrave;i l&ograve;ng khi đeo tr&ecirc;n tay.</p>', 4, '2021-04-25 21:42:17', '2021-04-25 21:48:13');
INSERT INTO `products` (`id`, `name`, `images`, `price`, `quantity`, `warranty`, `accessories`, `discount`, `status`, `active`, `hot`, `desc`, `categories_id`, `created_at`, `updated_at`) VALUES
(18, 'Apple Watch SE LTE 44mm viền nhôm dây cao su đen', '8.jpg', 10791000, 20, '12 tháng', 'Cáp - Sạc -Tai nghe', '300000', 'Nguyên Seal - Chính Hãng', 1, 0, '<h2>Đặc điểm nổi bật của Apple Watch SE LTE 44mm viền nh&ocirc;m d&acirc;y cao su đen</h2>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/7077/229059/Slider/apple-watch-se-lte-44mm-vien-nhom-day-cao-su-221020-0924580.jpg\" /></p>\r\n\r\n<h3>Thiết kế sang trọng, tinh tế</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/dong-ho-thong-minh/apple-watch-se-lte-44mm-vien-nhom-day-cao-su\" target=\"_blank\">Đồng hồ Apple Watch SE LTE</a>&nbsp; vẫn mang trong m&igrave;nh n&eacute;t tinh tế v&agrave; sang trọng của một chiếc đồng hồ cao cấp với những chi tiết được gia c&ocirc;ng tinh xảo, chất liệu cao cấp (khung viền được l&agrave;m từ 100% nh&ocirc;m t&aacute;i chế), đi c&ugrave;ng với&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh?g=silicon\" target=\"_blank\">d&acirc;y đeo cao su</a>&nbsp;&ecirc;m &aacute;i cho người d&ugrave;ng thoải m&aacute;i hơn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/229059/apple-watch-se-lte-44mm-vien-nhom-day-cao-su-273520-093556.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch SE LTE 44mm viền nhôm dây cao su có thiết kế thời trang\" src=\"https://cdn.tgdd.vn/Products/Images/7077/229059/apple-watch-se-lte-44mm-vien-nhom-day-cao-su-273520-093556.jpg\" /></a></p>\r\n\r\n<h3>Bộ vi xử l&yacute; hiện đại</h3>\r\n\r\n<p>Gi&uacute;p cho những t&aacute;c vụ của người d&ugrave;ng trở n&ecirc;n dễ d&agrave;ng hơn, mượt m&agrave; hơn, mang đến những trải nghiệm tuyệt vời nhờ đồng hồ sử dụng con chip Apple S5 mang đến hiệu năng mạnh mẽ, theo như Apple th&igrave; con chip n&agrave;y sẽ mạnh hơn chip tr&ecirc;n d&ograve;ng Series 3 đến hai lần.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/229059/apple-watch-se-lte-44mm-vien-nhom-day-cao-su-250920-1226222.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch SE LTE 44mm viền nhôm dây cao su trang bị con chip mạnh mẽ\" src=\"https://cdn.tgdd.vn/Products/Images/7077/229059/apple-watch-se-lte-44mm-vien-nhom-day-cao-su-250920-1226222.jpg\" /></a></p>\r\n\r\n<h3>C&oacute; hệ điều h&agrave;nh tối ưu</h3>\r\n\r\n<p>Ngo&agrave;i chip ra th&igrave; Apple c&ograve;n c&oacute; hệ điều h&agrave;nh tối ưu, với hệ điều h&agrave;nh WatchOS 7 n&agrave;y chiếc đồng hồ c&ograve;n mang đến nhiều t&iacute;nh năng đ&aacute;ng gi&aacute; như&nbsp;theo d&otilde;i giấc ngủ, mặt đồng hồ artist, kiểm tra độ chai pin của đồng hồ, th&ocirc;ng b&aacute;o tr&ecirc;n iphone khi Apple watch sạc đầy pin,&nbsp;tối ưu h&oacute;a sạc pin.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/229059/apple-watch-se-lte-44mm-vien-nhom-day-cao-su-250920-1226233.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch SE LTE 44mm viền nhôm dây cao su có hệ điều hành tối ưu\" src=\"https://cdn.tgdd.vn/Products/Images/7077/229059/apple-watch-se-lte-44mm-vien-nhom-day-cao-su-250920-1226233.jpg\" /></a></p>\r\n\r\n<h3>C&oacute; m&agrave;n h&igrave;nh r&otilde; n&eacute;t</h3>\r\n\r\n<p>C&ocirc;ng nghệ m&agrave;n h&igrave;nh OLED với mật độ điểm ảnh cao, cho c&aacute;c chi tiết tr&ecirc;n chiếc đồng hồ đều như l&agrave; thật v&agrave; tiết kiệm pin hơn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/229059/apple-watch-se-lte-44mm-vien-nhom-day-cao-su-250920-1226244.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch SE LTE 44mm viền nhôm dây cao su có màn hình rõ nét\" src=\"https://cdn.tgdd.vn/Products/Images/7077/229059/apple-watch-se-lte-44mm-vien-nhom-day-cao-su-250920-1226244.jpg\" /></a></p>\r\n\r\n<h3>Trang bị bộ sưu tập mặt đồng hồ khủng lồ</h3>\r\n\r\n<p>Với hệ mặt đồng hồ phong ph&uacute;, với nhiều mặt đồng hồ đẹp, mang đến nhiều sự lựa chọn cho bạn với nhiều chủ đề kh&aacute;c nhau. Hơn hết bạn c&ograve;n c&oacute; thể tự tạo ri&ecirc;ng cho m&igrave;nh một mặt đồng hồ theo phong c&aacute;ch v&agrave; sở th&iacute;ch của bản th&acirc;n.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/229059/apple-watch-se-lte-44mm-vien-nhom-day-cao-su-250920-1226255.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch SE LTE 44mm viền nhôm dây cao su có nhiều mặt đồng hồ cho người dùng tùy chọn\" src=\"https://cdn.tgdd.vn/Products/Images/7077/229059/apple-watch-se-lte-44mm-vien-nhom-day-cao-su-250920-1226255.jpg\" /></a></p>\r\n\r\n<h3>Độc lập hơn với điện thoại</h3>\r\n\r\n<p>Kh&ocirc;ng chỉ c&oacute; c&aacute;c chức năng của một chiếc đồng hồ th&ocirc;ng thường m&agrave; c&ograve;n c&oacute; thể thực hiện một số c&ocirc;ng việc của&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\">điện thoại</a>, trong đ&oacute; bao gồm thực hiện v&agrave; nhận cuộc gọi, người d&ugrave;ng c&oacute; thể sử dụng đồng hồ để&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh?g=nghe-goi-tren-dong-ho\" target=\"_blank\">nghe, gọi</a>&nbsp;kh&ocirc;ng kh&aacute;c g&igrave; chiếc điện thoại.</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/esim-la-gi-esim-co-su-dung-duoc-o-viet-nam-khong-1118062#subhmenuid7\" target=\"_blank\">C&aacute;ch kết nối eSIM tr&ecirc;n smartwatch</a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/229059/apple-watch-se-lte-44mm-vien-nhom-day-cao-su-250920-1226276.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch SE LTE 44mm viền nhôm dây cao su có thể nghe gọi trực tiếp\" src=\"https://cdn.tgdd.vn/Products/Images/7077/229059/apple-watch-se-lte-44mm-vien-nhom-day-cao-su-250920-1226276.jpg\" /></a></p>\r\n\r\n<h3>C&oacute; thời lượng pin l&acirc;u hơn</h3>\r\n\r\n<p>Với thời lượng pin lớn hơn 1 ng&agrave;y cho tất cả t&aacute;c vụ, m&agrave;n h&igrave;nh v&agrave; chức năng trong đồng hồ l&agrave; một thời lượng&nbsp;<a href=\"https://www.thegioididong.com/sac-dtdd\" target=\"_blank\">pin</a>&nbsp;l&yacute; tưởng m&agrave; bạn kh&ocirc;ng thể bỏ qua tr&ecirc;n chiếc đồng hồ&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh-apple-watch-se\">apple watch se&nbsp;</a>n&agrave;y.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/229059/apple-watch-se-lte-44mm-vien-nhom-day-cao-su-250920-1226287.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch SE LTE 44mm viền nhôm dây cao su có thời lượng pin lớn\" src=\"https://cdn.tgdd.vn/Products/Images/7077/229059/apple-watch-se-lte-44mm-vien-nhom-day-cao-su-250920-1226287.jpg\" /></a></p>\r\n\r\n<h3>H&agrave;ng loạt t&iacute;nh năng theo d&otilde;i sức khỏe</h3>\r\n\r\n<p>Chiếc&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh\" target=\"_blank\">đồng hồ th&ocirc;ng minh</a>&nbsp;n&agrave;y sẽ cho bạn nhiều th&ocirc;ng số về sức khỏe của bạn như đo&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh-theo-doi-giac-ngu\" target=\"_blank\">theo d&otilde;i giấc ngủ</a>, theo d&otilde;i nhịp tim,&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh?g=tinh-luong-calories-tieu-thu\" target=\"_blank\">t&iacute;nh lượng calories ti&ecirc;u thụ</a>,&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh?g=dem-so-buoc-chan\" target=\"_blank\">đếm số bước ch&acirc;n</a>,&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh?g=che-do-luyen-tap\" target=\"_blank\">chế độ luyện tập</a>, t&iacute;nh qu&atilde;ng đường chạy, gi&uacute;p bạn theo d&otilde;i v&agrave; nắm bắt được sức khỏe của m&igrave;nh tốt hơn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/229059/apple-watch-se-lte-44mm-vien-nhom-day-cao-su-250920-1226298.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch SE LTE 44mm viền nhôm dây cao su có nhiều tính năng hỗ trợ sức khỏe\" src=\"https://cdn.tgdd.vn/Products/Images/7077/229059/apple-watch-se-lte-44mm-vien-nhom-day-cao-su-250920-1226298.jpg\" /></a></p>\r\n\r\n<h3>Theo d&otilde;i v&agrave; ph&aacute;t hiện t&eacute; ng&atilde;</h3>\r\n\r\n<p>Chiếc đồng hồ sẽ ph&aacute;t ra &acirc;m thanh cảnh b&aacute;o nếu n&oacute; ph&aacute;t hiện người đeo vừa bị ng&atilde;, đồng thời tr&ecirc;n m&agrave;n h&igrave;nh xuất hiện một th&ocirc;ng b&aacute;o đẩy (push notification) hỏi xem người d&ugrave;ng c&oacute; ổn kh&ocirc;ng. Nếu&nbsp;<a href=\"https://www.thegioididong.com/dong-ho\" target=\"_blank\">đồng hồ</a>&nbsp;kh&ocirc;ng ph&aacute;t hiện bất kỳ chuyển động n&agrave;o từ người đeo sau một khoảng thời gian nhất định, n&oacute; sẽ tự động li&ecirc;n hệ với c&aacute;c dịch vụ cấp cứu v&agrave; gọi cho c&aacute;c số điện thoại li&ecirc;n lạc khẩn cấp m&agrave; người d&ugrave;ng đ&atilde; c&agrave;i đặt trước đ&oacute; để xin trợ gi&uacute;p.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/229059/apple-watch-se-lte-44mm-vien-nhom-day-cao-su-250920-1226309.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch SE LTE 44mm viền nhôm dây cao su bảo vệ bạn tốt hơn\" src=\"https://cdn.tgdd.vn/Products/Images/7077/229059/apple-watch-se-lte-44mm-vien-nhom-day-cao-su-250920-1226309.jpg\" /></a></p>\r\n\r\n<h3>Cải thiện thể chất của bản th&acirc;n</h3>\r\n\r\n<p>Gi&uacute;p người d&ugrave;ng c&oacute; thể cải thiện được sức khỏe của bản th&acirc;n dễ d&agrave;ng hơn khi được hỗ trợ nhiều&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh?g=che-do-luyen-tap\" target=\"_blank\">chế độ luyện tập</a>&nbsp;từ cơ bản đến n&acirc;ng cao, gi&uacute;p cho bạn c&oacute; thể luyện tập, cải thiện sức khỏe của bản th&acirc;n h&agrave;ng ng&agrave;y, với những b&agrave;i tập khoa học, đưa ra những th&ocirc;ng số ch&iacute;nh x&aacute;c về b&agrave;i tập vừa tập.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/229059/apple-watch-se-lte-44mm-vien-nhom-day-cao-su-250920-1244531.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch SE LTE 44mm viền nhôm dây cao su có nhiều chế độ luyện tập\" src=\"https://cdn.tgdd.vn/Products/Images/7077/229059/apple-watch-se-lte-44mm-vien-nhom-day-cao-su-250920-1244531.jpg\" /></a></p>\r\n\r\n<h3>Thay mặt bạn l&agrave;m việc nhiều hơn</h3>\r\n\r\n<p>Với trợ l&yacute; ảo Siri, bạn c&oacute; thể thực hiện nhiều c&ocirc;ng việc hơn th&ocirc;ng qua điều khiển bằng giọng n&oacute;i, Siri sẽ gi&uacute;p bạn t&igrave;m hoặc l&agrave;m những g&igrave; bạn cần. Bạn c&oacute; thể đặt c&acirc;u hỏi cho Siri, y&ecirc;u cầu hướng dẫn cho bạn điều g&igrave; đ&oacute; hoặc ra lệnh cho Siri thực hiện thay một t&aacute;c vụ g&igrave; đ&oacute; thay mặt bạn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/229059/apple-watch-se-lte-44mm-vien-nhom-day-cao-su-250920-12263311.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch SE LTE 44mm viền nhôm dây cao su có trợ lý ảo Siri\" src=\"https://cdn.tgdd.vn/Products/Images/7077/229059/apple-watch-se-lte-44mm-vien-nhom-day-cao-su-250920-12263311.jpg\" /></a></p>\r\n\r\n<h3>Dễ d&agrave;ng kết nối với những th&agrave;nh vi&ecirc;n trong gia đ&igrave;nh</h3>\r\n\r\n<p>Với t&iacute;nh năng Family Setup, một t&agrave;i khoản iCloud c&oacute; thể quản l&yacute; nhiều chiếc đồng hồ th&ocirc;ng minh kh&aacute;c nhau, gi&uacute;p bạn thuận tiện hơn trong việc chăm s&oacute;c trẻ em, người gi&agrave;.</p>\r\n\r\n<p>Lưu &yacute;: T&iacute;nh năng n&agrave;y hiện kh&ocirc;ng khả dụng ở Việt Nam.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/229059/apple-watch-se-lte-44mm-vien-nhom-day-cao-su-225420-095416.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch SE LTE 44mm viền nhôm dây cao su được hỗ trợ family setup\" src=\"https://cdn.tgdd.vn/Products/Images/7077/229059/apple-watch-se-lte-44mm-vien-nhom-day-cao-su-225420-095416.jpg\" /></a></p>\r\n\r\n<h3>C&ograve;n rất nhiều t&iacute;nh năng kh&aacute;c</h3>\r\n\r\n<p>Sản phẩm của nh&agrave; t&aacute;o c&ograve;n mang đến nhiều tiện &iacute;ch kh&aacute;c đang chờ người d&ugrave;ng trải nghiệm như thời tiết, điều khiển nhạc, bản đồ, t&igrave;m đồng hồ, th&ocirc;ng b&aacute;o tin nhắn,...</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/7077/229059/apple-watch-se-lte-44mm-vien-nhom-day-cao-su-250920-12263513.jpg\" onclick=\"return false;\"><img alt=\"Apple Watch SE LTE 44mm viền nhôm dây cao su có thêm nhiều tính năng thú vị\" src=\"https://cdn.tgdd.vn/Products/Images/7077/229059/apple-watch-se-lte-44mm-vien-nhom-day-cao-su-250920-12263513.jpg\" /></a></p>\r\n\r\n<p>Nh&igrave;n chung,&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh-apple\" target=\"_blank\">Apple Watch</a>&nbsp;lu&ocirc;n l&agrave; một trong những chiếc đồng hồ đ&aacute;ng đồng tiền b&aacute;t gạo nhờ c&oacute; thiết kế hiện đại, sang trọng,&nbsp;<a href=\"https://www.thegioididong.com/day-dong-ho\" target=\"_blank\">d&acirc;y đeo</a>&nbsp;&ecirc;m tay. Chiếc đồng hồ n&agrave;y c&ograve;n sở hữu h&agrave;ng loạt t&iacute;nh năng thời thượng m&agrave; nhiều h&atilde;ng đồng hồ đang hướng tới.&nbsp;</p>', 4, '2021-04-25 21:43:29', '2021-04-25 21:48:11'),
(19, 'Máy tính bảng iPad Pro 12.9 inch Wifi 128GB (2020)', '2.jpg', 27190000, 20, '12 tháng', 'Cáp - Sạc -Tai nghe', '0', 'Nguyên Seal - Chính Hãng', 1, 1, '<h2>Đặc điểm nổi bật của iPad Pro 12.9 inch Wifi 128GB (2020)</h2>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/522/221775/Slider/ipad-pro-12-9-inch-wifi-128gb-2020-073320-033345-687.jpg\" /></p>\r\n\r\n<h2>Sở hữu thiết kế tinh tế, m&agrave;n h&igrave;nh xuất sắc v&agrave; cấu h&igrave;nh mạnh mẽ, đ&aacute;p ứng được hầu hết nhu cầu của một người s&aacute;ng tạo chuy&ecirc;n nghiệp. Điều kh&ocirc;ng một thế hệ&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\">m&aacute;y t&iacute;nh bảng</a>&nbsp;n&agrave;o kh&aacute;c c&oacute; thể l&agrave;m được, đ&atilde; xuất hiện tr&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang/ipad-pro-12-9-inch-wifi-128gb-2020\" target=\"_blank\">iPad Pro 12.9 inch 2020</a>.</h2>\r\n\r\n<h3>M&agrave;n h&igrave;nh tuyệt đẹp, nhiều c&ocirc;ng nghệ t&iacute;ch hợp</h3>\r\n\r\n<p>iPad Pro 12.9 inch 2020 c&oacute; một thiết kế kh&aacute; vu&ocirc;ng vức với viền m&agrave;n h&igrave;nh 4 cạnh kh&ocirc;ng qu&aacute; d&agrave;y v&agrave; đều nhau, gi&uacute;p cho trải nghiệm cầm nắm dễ d&agrave;ng, gi&uacute;p cho tổng thể mặt trước của&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-apple-ipad\" target=\"_blank\">iPad</a>&nbsp;h&agrave;i h&ograve;a v&agrave; đẹp mắt hơn.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/221775/ipad-pro-12-9-inch-wifi-128gb-2020.jpg\" onclick=\"return false;\"><img alt=\"mặt trước iPad Pro 12.9 2020\" src=\"https://cdn.tgdd.vn/Products/Images/522/221775/ipad-pro-12-9-inch-wifi-128gb-2020.jpg\" /></a></p>\r\n\r\n<p>Tổng thể chiếc m&aacute;y c&oacute; trọng lượng chỉ 471 g v&agrave; mỏng 5.9 mm so với k&iacute;ch thước 12.9 inch th&igrave; điều đ&oacute; cho thấy iPad Pro 12.9 inch 2020 rất mỏng nhẹ, gọn g&agrave;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/221775/ipad-pro-12-9-inch-wifi-128gb-2020-4.jpg\" onclick=\"return false;\"><img alt=\"Trên tay kích thước iPad Pro 12.9 2020\" src=\"https://cdn.tgdd.vn/Products/Images/522/221775/ipad-pro-12-9-inch-wifi-128gb-2020-4.jpg\" /></a></p>\r\n\r\n<p>Thật kh&oacute; l&ograve;ng khi t&igrave;m ra khuyết điểm về m&agrave;n h&igrave;nh của iPad Pro 12.9 inch 2020, với k&iacute;ch thước 12.9 inch c&ugrave;ng với độ ph&acirc;n giải 2048 x 2732 pixels gi&uacute;p cho m&aacute;y hiển thị v&ocirc; c&ugrave;ng sắc n&eacute;t v&agrave; kh&ocirc;ng gian l&agrave;m việc lớn.&nbsp;</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-ve-cac-loai-do-phan-giai-man-hinh-1196142\" target=\"_blank\">T&igrave;m hiểu về c&aacute;c loại độ ph&acirc;n giải m&agrave;n h&igrave;nh</a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/221775/ipad-pro-12-9-inch-wifi-128gb-2020-6.jpg\" onclick=\"return false;\"><img alt=\"kích thước màn hình iPad Pro 12.9 2020\" src=\"https://cdn.tgdd.vn/Products/Images/522/221775/ipad-pro-12-9-inch-wifi-128gb-2020-6.jpg\" /></a></p>\r\n\r\n<p>M&aacute;y sử dụng c&ocirc;ng nghệ m&agrave;n h&igrave;nh Liquid Retina Display độ s&aacute;ng 600 nits với c&aacute;c c&ocirc;ng nghệ hỗ trợ như Pro Motion v&agrave; True Tone, gi&uacute;p m&agrave;u sắc được thể hiện một c&aacute;ch tự nhi&ecirc;n, trung thực v&agrave; sống động.</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/man-hinh-retina-la-gi-905780\" target=\"_blank\">M&agrave;n h&igrave;nh Retina l&agrave; g&igrave;?</a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/221775/ipad-pro-12-9-inch-wifi-128gb-2020-8.jpg\" onclick=\"return false;\"><img alt=\"màn hình hiển thị trên iPad Pro 12.9 2020\" src=\"https://cdn.tgdd.vn/Products/Images/522/221775/ipad-pro-12-9-inch-wifi-128gb-2020-8.jpg\" /></a></p>\r\n\r\n<h3>Hiệu năng mạnh mẽ với chip CPU A12Z với 8 nh&acirc;n đồ họa</h3>\r\n\r\n<p>iPad Pro 12.9 inch 2020 được trang bị bộ vi xử l&yacute; Apple A12Z Bionic mạnh mẽ hơn người tiền nhiệm, gi&uacute;p cho thao t&aacute;c sử dụng những phần mềm đồ họa như Photoshop, illustrator mượt m&agrave; v&agrave; phản hồi nhanh ch&oacute;ng hơn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/221775/ipad-pro-12-9-inch-wifi-128gb-2020-5.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình iPad Pro 2020\" src=\"https://cdn.tgdd.vn/Products/Images/522/221775/ipad-pro-12-9-inch-wifi-128gb-2020-5.jpg\" /></a></p>\r\n\r\n<p>Với việc c&oacute; thể kết nối với b&agrave;n ph&iacute;m, chuột kh&ocirc;ng d&acirc;y v&agrave; con trỏ chuột được l&agrave;m mới, gi&uacute;p người d&ugrave;ng thao t&aacute;c v&agrave; sử dụng một c&aacute;ch dễ d&agrave;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/221775/ipad-pro-12-9-inch-wifi-128gb-2020-3.jpg\" onclick=\"return false;\"><img alt=\"iPad Pro 12.9 2020 kết nối bàn phím\" src=\"https://cdn.tgdd.vn/Products/Images/522/221775/ipad-pro-12-9-inch-wifi-128gb-2020-3.jpg\" /></a></p>\r\n\r\n<p>Hơn thế nữa, bộ vi xử l&yacute; A12Z với 8 nh&acirc;n đồ hoạ th&igrave; m&aacute;y c&oacute; thể chiến hầu hết c&aacute;c tựa game hiện c&oacute; tr&ecirc;n thị trường như PUBG, Li&ecirc;n Qu&acirc;n, Asphalt 9,... với mức thiết lập đồ họa cao nhất với tốc độ khung h&igrave;nh ổn định trong suốt qu&aacute; tr&igrave;nh chơi.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/221775/ipad-pro-12-9-inch-wifi-128gb-2020-7.jpg\" onclick=\"return false;\"><img alt=\"chơi game với iPad Pro 12.9 2020\" src=\"https://cdn.tgdd.vn/Products/Images/522/221775/ipad-pro-12-9-inch-wifi-128gb-2020-7.jpg\" /></a></p>\r\n\r\n<p>Bằng việc t&iacute;ch hợp sẵn bộ nhớ 128 GB gi&uacute;p cho người d&ugrave;ng c&oacute; nhiều kh&ocirc;ng gian lưu trữ hơn, l&agrave;m được nhiều việc hơn tr&ecirc;n chiếc iPad Pro 12.9 inch 2020. Đ&acirc;y l&agrave; mức dung lượng ho&agrave;n hảo cho t&ugrave;y chọn cơ bản nhất của chiếc m&aacute;y.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/221775/ipad-pro-12-9-inch-wifi-128gb-2020-9.jpg\" onclick=\"return false;\"><img alt=\"Bộ nhớ trên iPad Pro 12.9 2020\" src=\"https://cdn.tgdd.vn/Products/Images/522/221775/ipad-pro-12-9-inch-wifi-128gb-2020-9.jpg\" /></a></p>\r\n\r\n<h3>Cụm camera &quot;Pro&quot;, n&acirc;ng tầm trải nghiệm AR</h3>\r\n\r\n<p>Apple thật sự ưu &aacute;i khi t&iacute;ch hợp cho chiếc m&aacute;y n&agrave;y với 2 camera sau, 1 camera ch&iacute;nh 12 MP v&agrave; 1 camera g&oacute;c si&ecirc;u rộng 125 độ 10 MP, c&ugrave;ng với đ&oacute; l&agrave; camera selfie 7 MP hỗ trợ c&ocirc;ng nghệ TrueDepth. Gi&uacute;p bạn ho&agrave;n to&agrave;n c&oacute; thể chụp ra những bức ảnh ưng &yacute; nhất.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/221775/ipad-pro-12-9-inch-wifi-128gb-2020-10.jpg\" onclick=\"return false;\"><img alt=\"Cụm camera trên iPad Pro 12.9 2020\" src=\"https://cdn.tgdd.vn/Products/Images/522/221775/ipad-pro-12-9-inch-wifi-128gb-2020-10.jpg\" /></a></p>\r\n\r\n<p>Việc quay video, chụp ảnh v&agrave; chỉnh sửa trực tiếp một c&aacute;ch chuy&ecirc;n nghiệp để gửi đi kh&aacute;ch h&agrave;ng, đối t&aacute;c chỉ với một thiết bị duy nhất đ&atilde; kh&ocirc;ng c&ograve;n l&agrave; điều xa vời với người d&ugrave;ng&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-apple-ipad-pro\" target=\"_blank\">iPad Pro</a>&nbsp;2020.</p>\r\n\r\n<p>Hơn thế nữa, iPad Pro 12.9 inch 2020 c&ograve;n được t&iacute;ch hợp th&ecirc;m cảm biến Lidar đo chiều s&acirc;u, gi&uacute;p hỗ trợ những ứng dụng AR nhận diện bối cảnh một c&aacute;ch ch&iacute;nh x&aacute;c nhất.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/221775/ipad-pro-12-9-inch-wifi-128gb-2020-11.jpg\" onclick=\"return false;\"><img alt=\"Lidar hỗ trợ AR trên iPad Pro 12.9 2020\" src=\"https://cdn.tgdd.vn/Products/Images/522/221775/ipad-pro-12-9-inch-wifi-128gb-2020-11.jpg\" /></a></p>\r\n\r\n<p>Cảm biến n&agrave;y cực k&igrave; hữu &iacute;ch đặc biệt trong c&aacute;c lĩnh vực thiết kế, thi c&ocirc;ng bởi người d&ugrave;ng c&oacute; thể &quot;ướm&quot; thử c&aacute;c m&ocirc; h&igrave;nh 3D của thiết kế l&ecirc;n thực tế v&agrave; quan s&aacute;t nhiều g&oacute;c độ một c&aacute;ch trực quan nhất bằng c&ocirc;ng nghệ thực tại ảo AR.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/221775/ipad-pro-12-9-inch-wifi-128gb-2020.gif\" onclick=\"return false;\"><img alt=\"Cảm biến Lidar hỗ trợ AR mượt mà trên iPad Pro 12.9 2020\" src=\"https://cdn.tgdd.vn/Products/Images/522/221775/ipad-pro-12-9-inch-wifi-128gb-2020.gif\" /></a></p>\r\n\r\n<p>Nh&igrave;n chung,&nbsp;<a href=\"https://www.thegioididong.com/apple\" target=\"_blank\">Apple</a>&nbsp;đ&atilde; định vị iPad rất r&otilde; r&agrave;ng hướng tới việc ng&agrave;y c&agrave;ng mang lại nhiều trải nghiệm l&agrave;m việc, giải tr&iacute; mạnh mẽ hơn cho người d&ugrave;ng chuy&ecirc;n nghiệp. Ở hiện tại, iPad Pro 2020 chắc chắn l&agrave; thiết bị rất đ&aacute;ng xuống tiền để sử dụng như một thiết bị l&agrave;m việc hỗ trợ để xử l&yacute; c&aacute;c c&ocirc;ng việc gấp khi du lịch, ra ngo&agrave;i hay l&agrave; c&ocirc;ng cụ rất &quot;ngầu&quot; d&ugrave;ng để giao tiếp với đối t&aacute;c.</p>', 2, '2021-04-25 21:46:14', '2021-04-25 22:48:05'),
(20, 'Máy tính bảng iPad Pro 11 inch Wifi Cellular 128GB (2020)', '3.jpg', 25190000, 10, '12 tháng', 'Cáp - Sạc -Tai nghe', '190000', 'Nguyên Seal - Chính Hãng', 1, 1, '<h2>Đặc điểm nổi bật của iPad Pro 11 inch Wifi Cellular 128GB (2020)</h2>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/522/221774/Slider/ipad-pro-11-inch-wifi-cellular-128gb-2020-072420-032425-270.jpg\" /></p>\r\n\r\n<h2>Đ&atilde; 2 năm kể từ khi mẫu&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang/ipad-pro-129-2018\" target=\"_blank\">iPad Pro 2018</a>&nbsp;ra mắt, mới đ&acirc;y, mẫu iPad Pro mới nhất -&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang/ipad-pro-11-inch-2020\" target=\"_blank\">iPad Pro 11 inch (2020)</a>&nbsp;vừa được&nbsp;<a href=\"https://www.thegioididong.com/apple\" target=\"_blank\">Apple</a>&nbsp;tr&igrave;nh l&agrave;ng với nhiều sự cải tiến đ&aacute;ng gi&aacute; lẫn về t&iacute;nh năng v&agrave; sức mạnh xử l&yacute;, hứa hẹn đ&acirc;y sẽ l&agrave; mẫu&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\">m&aacute;y t&iacute;nh bảng</a>&nbsp;được săn đ&oacute;n nhiều nhất trong năm 2020.</h2>\r\n\r\n<h3>Thiết kế &ldquo;b&igrave;nh cũ rượu mới&rdquo; sang trọng đậm chất &ldquo;T&aacute;o&rdquo;</h3>\r\n\r\n<p>C&oacute; thể thấy rằng, mẫu iPad Pro 2020 11 inch hầu như kh&ocirc;ng c&oacute; sự kh&aacute;c biệt nhiều so với thế hệ&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-apple-ipad\" target=\"_blank\">iPad</a>&nbsp;trước đ&oacute; về mặt thiết kế.</p>\r\n\r\n<h2><a href=\"https://www.thegioididong.com/images/522/221774/ipad-pro-11-2020-4-4.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng iPad Pro 11 inch Wifi Cellular 128GB | Thiết kế \" src=\"https://cdn.tgdd.vn/Products/Images/522/221774/ipad-pro-11-2020-4-4.jpg\" /></a></h2>\r\n\r\n<p>M&aacute;y vẫn giữ thiết kế nguy&ecirc;n khối với c&aacute;c cạnh b&ecirc;n được v&aacute;t thẳng c&ugrave;ng 4 g&oacute;c được bo cong, Đ&acirc;y l&agrave; thiết kế vu&ocirc;ng vức quen thuộc m&agrave; Apple lần đầu sử dụng tr&ecirc;n iPhone 5 trước đ&acirc;y.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/221774/ipad-pro-11-2020-4-1.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng iPad Pro 11 inch Wifi Cellular 128GB | Cạnh bên được vát thẳng vuông vức\" src=\"https://cdn.tgdd.vn/Products/Images/522/221774/ipad-pro-11-2020-4-1.jpg\" /></a></p>\r\n\r\n<p>Mặt trước của m&aacute;y l&agrave; một&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang?g=tren-10\" target=\"_blank\">m&agrave;n h&igrave;nh rộng 11 inch</a>, nhờ việc loại bỏ n&uacute;t home truyền thống, gi&uacute;p cho m&agrave;n h&igrave;nh c&oacute; thể tr&agrave;n đều ra 4 cạnh. Tuy trang bị một m&agrave;n h&igrave;nh lớn hơn hẳn nhưng k&iacute;ch thước tổng thể của m&aacute;y cũng kh&ocirc;ng qu&aacute; to với c&aacute;c thế hệ iPad tiền nhiệm.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/221774/ipad-pro-11-2020-4-2.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng iPad Pro 11 inch Wifi Cellular 128GB | Màn hình tràn viền\" src=\"https://cdn.tgdd.vn/Products/Images/522/221774/ipad-pro-11-2020-4-2.jpg\" /></a></p>\r\n\r\n<p>Mặt sau của iPad Pro 2020 vẫn l&agrave; logo T&aacute;o cắn dở quen thuộc được đặt ch&iacute;nh giữa, g&oacute;c tr&aacute;i l&agrave; cụm camera h&igrave;nh vu&ocirc;ng tương tự như tr&ecirc;n bộ đ&ocirc;i&nbsp;<a href=\"https://www.thegioididong.com/dtdd/iphone-11-pro\" target=\"_blank\">iPhone 11 Pro</a>&nbsp;v&agrave;&nbsp;<a href=\"https://www.thegioididong.com/dtdd/iphone-11\" target=\"_blank\">iPhone 11</a>. Đ&acirc;y c&oacute; lẽ l&agrave; đặc điểm duy nhất để người d&ugrave;ng ph&acirc;n biệt với&nbsp;iPad Pro 2018&nbsp;trước đ&oacute;.</p>\r\n\r\n<h3>M&agrave;n h&igrave;nh Liquid Retina sắc n&eacute;t, m&agrave;u sắc cực chuẩn</h3>\r\n\r\n<p>iPad Pro 2020 được trang bị m&agrave;n h&igrave;nh c&ocirc;ng nghệ Liquid Retina ti&ecirc;n tiến do ch&iacute;nh Apple ph&aacute;t triển v&agrave; t&iacute;ch hợp tr&ecirc;n c&aacute;c d&ograve;ng sản phẩm mới của m&igrave;nh.</p>\r\n\r\n<p>T&igrave;m hiểu th&ecirc;m :&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/cung-tim-hieu-ve-man-hinh-liquid-retina-tren-iphon-1125106\" target=\"_blank\">C&ugrave;ng t&igrave;m hiểu về m&agrave;n h&igrave;nh Liquid Retina</a>&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/221774/ipad-pro-11-2020-1.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng iPad Pro 11 inch Wifi Cellular 128GB | Công nghệ màn hình Liquid Retina\" src=\"https://cdn.tgdd.vn/Products/Images/522/221774/ipad-pro-11-2020-1.jpg\" /></a></p>\r\n\r\n<p>Chiếc m&aacute;y t&iacute;nh bảng của nh&agrave; T&aacute;o c&oacute; độ ph&acirc;n giải đạt 2.388 x 1.668 pixel, tần số qu&eacute;t đạt tối đa 120 Hz, hỗ trợ kh&ocirc;ng gian m&agrave;u P3 cực chuẩn, nhờ đ&oacute; m&agrave;u sắc hiển thị tr&ecirc;n iPad Pro 2020 v&ocirc; c&ugrave;ng ch&iacute;nh x&aacute;c, l&yacute; tưởng để bạn chỉnh sửa đồ họa hay giải tr&iacute;, chơi game.</p>\r\n\r\n<p>Với độ s&aacute;ng cực cao 600 nit đồng thời phủ 1 lớp chống ch&oacute;i kết hợp với t&iacute;nh năng&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-cong-nghe-man-hinh-true-tone-992705\" target=\"_blank\">True Tone</a>&nbsp;gi&uacute;p m&agrave;n h&igrave;nh m&aacute;y hiển thị r&otilde; n&eacute;t trong nhiều điều kiện kh&aacute;c nhau.</p>\r\n\r\n<h3>Cụm camera xuất hiện cảm biến mới</h3>\r\n\r\n<p>Camera ch&iacute;nh l&agrave; điểm n&acirc;ng cấp mạnh mẽ v&agrave; r&otilde; r&agrave;ng nhất tr&ecirc;n chiếc&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-apple-ipad-pro\" target=\"_blank\">iPad Pro</a>&nbsp;mới n&agrave;y. M&aacute;y được trang bị cụm camera k&eacute;p bao gồm&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang?g=tren-5-mp\" target=\"_blank\">camera g&oacute;c rộng 12 MP</a>&nbsp;v&agrave; g&oacute;c si&ecirc;u rộng 10 MP. B&ecirc;n cạnh đ&oacute; l&agrave; một cảm biến mới mang t&ecirc;n LiDAR lần đầu xuất hiện tr&ecirc;n thiết bị Apple.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/221774/ipad-pro-11-2020-2.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng iPad Pro 11 inch Wifi Cellular 128GB | Cụm camera sau\" src=\"https://cdn.tgdd.vn/Products/Images/522/221774/ipad-pro-11-2020-2.jpg\" /></a></p>\r\n\r\n<p>Camera ch&iacute;nh 12 MP cho chất lượng ảnh v&ocirc; c&ugrave;ng sắc n&eacute;t, độ chi tiết cao, cung cấp một g&oacute;c chụp đủ rộng để bạn c&oacute; thể thu được nhiều chi tiết hơn. Kh&ocirc;ng những thế, Apple c&ograve;n trang bị th&ecirc;m 1 camera g&oacute;c si&ecirc;u rộng Ultra Wide 10 MP để bạn c&oacute; thể chụp to&agrave;n cảnh ấn tượng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/221774/ipad-pro-11-2020-4-7.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng iPad Pro 11 inch Wifi Cellular 128GB | Ảnh chụp góc siêu rộng\" src=\"https://cdn.tgdd.vn/Products/Images/522/221774/ipad-pro-11-2020-4-7.jpg\" /></a></p>\r\n\r\n<p>Cảm biến LiDAR l&agrave; sự bổ sung đ&aacute;ng ch&uacute; &yacute; nhất tr&ecirc;n iPad Pro mới. Cảm biến n&agrave;y hoạt động dựa tr&ecirc;n nguy&ecirc;n l&yacute; phản xạ ch&ugrave;m s&aacute;ng laser để thu thập chiều s&acirc;u m&ocirc;i trường,&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/221774/ipad-pro-11-2020-4-5.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng iPad Pro 11 inch Wifi Cellular 128GB | Ảnh chụp từ camera góc siêu rộng Ultra Wide 12 MP\" src=\"https://cdn.tgdd.vn/Products/Images/522/221774/ipad-pro-11-2020-4-5.jpg\" /></a></p>\r\n\r\n<p>Cảm biến LiDAR hoạt động kh&aacute; nhanh, khi kết hợp với AI ch&uacute;ng c&oacute; thể tạo ra bản đồ kh&ocirc;ng gian trong b&aacute;n k&iacute;nh 5m gần như lập tức, gi&uacute;p cho việc trải nghiệm c&aacute;c nội dung, ứng dụng c&oacute; t&iacute;ch hợp c&ocirc;ng nghệ thực tế ảo AR được mượt m&agrave; v&agrave; ch&iacute;nh x&aacute;c hơn đ&aacute;ng kể.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/221774/giphy1_480x270.gif\" onclick=\"return false;\"><img alt=\"Máy tính bảng iPad Pro 11 inch Wifi Cellular 128GB | AR mượt mà nhờ hỗ trợ từ cảm biến LIDAR\" src=\"https://cdn.tgdd.vn/Products/Images/522/221774/giphy1_480x270.gif\" /></a></p>\r\n\r\n<p>Ở mặt trước, iPad Pro 2020 được t&iacute;ch hợp camera selfie 7 MP v&agrave; c&ugrave;ng cụm cảm biến&nbsp;nhận diện gương mặt&nbsp;3D Face ID, hỗ trợ chụp selfie v&agrave; t&iacute;nh năng gọi nh&oacute;m Facetime với hiệu ứng Emoji vui nhộn th&uacute; vị.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/221774/ipad-pro-11-2020-3.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng iPad Pro 11 inch Wifi Cellular 128GB | Tính năng gọi nhóm Facetime thú vị\" src=\"https://cdn.tgdd.vn/Products/Images/522/221774/ipad-pro-11-2020-3.jpg\" /></a></p>\r\n\r\n<p>Giao diện camera th&acirc;n thiện dễ sử dụng cho ph&eacute;p bạn lấy n&eacute;t chụp v&agrave; chỉnh sửa ảnh trực tiếp, sau đ&oacute; lưu ảnh v&agrave;o bộ nhớ hay gửi email. Tất cả thao t&aacute;c đều diễn ra v&ocirc; c&ugrave;ng dễ d&agrave;ng gi&uacute;p bạn xử l&yacute; c&ocirc;ng việc nhanh ch&oacute;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/221774/ipad11inch-7.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng iPad Pro 11 inch Wifi Cenllular 128 GB (2020) | Chụp và xử lý ảnh nhanh chóng dễ dàng\" src=\"https://cdn.tgdd.vn/Products/Images/522/221774/ipad11inch-7.jpg\" /></a></p>\r\n\r\n<p>Cụm camera n&agrave;y cũng được trang bị khả năng quay video c&oacute; độ ph&acirc;n giải 4K với tốc độ l&ecirc;n đến 60 khung h&igrave;nh/gi&acirc;y, khả năng thu &acirc;m tốt, kết hợp phần cứng lẫn phần mềm tối ưu cho việc chỉnh video gi&uacute;p bạn c&oacute; thể tạo ra những thước phim ấn tượng trực tiếp ngay tr&ecirc;n iPad Pro.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/221774/ipad-pro-11-2020-4-9.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng iPad Pro 11 inch Wifi Cellular 128GB | Chỉnh sửa video\" src=\"https://cdn.tgdd.vn/Products/Images/522/221774/ipad-pro-11-2020-4-9.jpg\" /></a></p>\r\n\r\n<h3>Xử l&yacute; đồ họa chuy&ecirc;n nghiệp với chip A12Z</h3>\r\n\r\n<p>Trang bị vi xử l&yacute; A12Z Bionic mới nhất, đ&atilde; gi&uacute;p cho iPad Pro 2020 trở th&agrave;nh m&aacute;y t&iacute;nh bảng mạnh mẽ hơn hầu hết c&aacute;c mẫu laptop Window n&agrave;o hiện nay.</p>\r\n\r\n<p>Tham khảo b&agrave;i viết:&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/ipad-pro-2020-voi-chip-apple-a12z-dat-diem-hieu-nang-khung-1243238\" target=\"_blank\">iPad Pro 2020 với chip Apple A12Z, RAM 6GB đạt điểm hiệu năng khủng khiếp</a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/221774/ipad11inch-3.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng iPad Pro 11 inch Wifi Cellular 128GB | Chip A12Z hỗ trợ chỉnh sửa video mượt mà\" src=\"https://cdn.tgdd.vn/Products/Images/522/221774/ipad11inch-3.jpg\" /></a></p>\r\n\r\n<p>Vi xử l&yacute; mới n&agrave;y được trang bị 8 l&otilde;i CPU c&oacute; tốc độ đạt đến 2.5 GHz, t&iacute;ch hợp modem LTE gigabit cho tốc độ nhanh hơn 60%, tiết kiệm pin hơn nhờ vậy A12Z được thiết kế tối ưu cho c&aacute;c t&aacute;c vụ nặng như render video 4K, thiết kế c&aacute;c m&ocirc; h&igrave;nh 3D.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/221774/ipad-pro-11-2020-4-11.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng iPad Pro 11 inch Wifi Cellular 128GB | ANTUTU vượt ngưỡng 700000 điểm\" src=\"https://cdn.tgdd.vn/Products/Images/522/221774/ipad-pro-11-2020-4-11.jpg\" /></a></p>\r\n\r\n<p>iPad Pro trang bị 6 GB RAM với bộ nhớ 128 GB đủ để bạn lưu trữ những ứng dụng cần thiết. Đối với nhu cầu chỉnh sửa phim ảnh, bạn sẽ cần một mức dung lượng bộ nhớ cao hơn với 3 t&ugrave;y chọn 256 GB, 512 GB v&agrave; 1 TB.</p>\r\n\r\n<p>Điểm đ&aacute;ng tiếc, đến tận phi&ecirc;n bản mới n&agrave;y, th&igrave; Apple vẫn chưa c&oacute; kết nối 5G. Phi&ecirc;n bản&nbsp;iPad Pro 11 inch Wifi Cellular 128GB (2020) chỉ được trang bị khe cắm sim tốc độ 4G, đủ để gi&uacute;p bạn truy cập internet nhanh ch&oacute;ng mọi l&uacute;c mọi nơi m&agrave; kh&ocirc;ng cần c&oacute; wifi.</p>\r\n\r\n<p>B&ecirc;n trong m&aacute;y l&agrave; một vi&ecirc;n pin c&oacute; c&ocirc;ng suất 28.65 Wh hoạt động li&ecirc;n tục từ 8 đến 10 tiếng, đảm bảo 1 ng&agrave;y l&agrave;m việc của bạn m&agrave; kh&ocirc;ng cần quan t&acirc;m đến việc t&igrave;m ổ cắm sạc điện.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/221774/ipad-pro-11-2020-4-10.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng iPad Pro 11 inch Wifi Cellular 128GB | Thời lượng pin cho bạn sử dụng cả ngày\" src=\"https://cdn.tgdd.vn/Products/Images/522/221774/ipad-pro-11-2020-4-10.jpg\" /></a></p>\r\n\r\n<h3>Hệ điều h&agrave;nh iPad OS tối ưu hiệu năng cực tốt</h3>\r\n\r\n<p>Với iPad OS, giao diện được cải tiến th&ocirc;ng minh, giao diện đa nhiệm nhiều cửa sổ tốt hơn gi&uacute;p cho chiếc iPad giờ đ&acirc;y c&oacute; thể đảm nhận nhiều c&ocirc;ng việc gần như tương đương 1 chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\">laptop</a>&nbsp;mini.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/221774/ipad11inch-9.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng iPad Pro 11 inch Wifi Cellular 128GB | Chế độ đa nhiệm nhiều cửa sổ trên iPad OS \" src=\"https://cdn.tgdd.vn/Products/Images/522/221774/ipad11inch-9.jpg\" /></a></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, iPad OS c&ograve;n tối ưu tốt hiệu năng phần cứng của iPad Pro, gi&uacute;p chiếc m&aacute;y hoạt động mượt m&agrave; trơn tru. Ngo&agrave;i ra, người d&ugrave;ng c&ograve;n được hỗ trợ cập nhật c&aacute;c phi&ecirc;n bản mới đến tận 5 năm cho ph&eacute;p trải nghiệm nhiều t&iacute;nh năng mới nhất đến từ nh&agrave; T&aacute;o. Đ&acirc;y cũng l&agrave; điểm lợi thế của iPad v&agrave; c&aacute;c thiết bị Apple khi so với c&aacute;c sản phẩm kh&aacute;c tr&ecirc;n thị trường.&nbsp;</p>', 2, '2021-04-25 21:47:05', '2021-04-25 21:48:09'),
(21, 'Máy tính bảng iPad Air 4 Wifi Cellular 256GB (2020)', '4.jpg', 24190000, 10, '12 tháng', 'Cáp - Sạc -Tai nghe', '300000', 'Nguyên Seal - Chính Hãng', 1, 1, '<h2>Đặc điểm nổi bật của iPad Air 4 Wifi Cellular 256GB (2020)</h2>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/522/228899/Slider/ipad-air-4-wifi-cellular-256gb-2020-121020-0206058.jpg\" /></p>\r\n\r\n<h3>Chấn động giới c&ocirc;ng nghệ to&agrave;n cầu, khi một&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\">tablet</a>&nbsp;lần đầu được t&iacute;ch hợp b&ecirc;n cạnh một trong những chipset h&agrave;ng đầu 2020,&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang/ipad-air-4-wifi-cellular-256gb-2020\" target=\"_blank\">iPad Air 4 Wifi Cellular 256GB 2020</a>&nbsp;sở hữu con chip A14 Bionic với hiệu năng v&ocirc; c&ugrave;ng mạnh mẽ, kết nối 4G tiện dụng c&ugrave;ng bộ nhớ cực khủng l&ecirc;n đến 256 GB.</h3>\r\n\r\n<h3>M&agrave;u sắc c&aacute; t&iacute;nh, nhẹ như Air</h3>\r\n\r\n<p>Xứng danh d&ograve;ng Air của&nbsp;<a href=\"https://www.thegioididong.com/apple\" target=\"_blank\">Apple</a>, iPad Air 4 được chế t&aacute;c từ nh&ocirc;m t&aacute;i chế 100%, một chất liệu v&ocirc; c&ugrave;ng nổi bật tr&ecirc;n c&aacute;c mẫu&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-apple-ipad\" target=\"_blank\">iPad</a>&nbsp;của Apple nửa cuối 2020, b&ecirc;n cạnh khối lượng chỉ 460 g, đem đến những trải nghiệm tuyệt hảo cả về phần nh&igrave;n lẫn cảm nhận khi sử dụng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/228899/ipad-air-4-wifi-cellular-256gb-2020-094920-054936.jpg\" onclick=\"return false;\"><img alt=\"iPad Air 4 Wifi Cellular 256GB (2020)\" src=\"https://cdn.tgdd.vn/Products/Images/522/228899/ipad-air-4-wifi-cellular-256gb-2020-094920-054936.jpg\" /></a><br />\r\nMở đầu xu hướng đa sắc tr&ecirc;n c&aacute;c d&ograve;ng iPad hiện nay, đ&aacute;nh dấu chất ri&ecirc;ng, thể hiện c&aacute; t&iacute;nh với bộ sưu tập những m&agrave;u sắc v&ocirc; c&ugrave;ng nổi bật tr&ecirc;n mẫu iPad Air 2020 thế hệ mới.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/228899/ipad-air-4-wifi-cellular-256gb-2020-092120-052118.jpg\" onclick=\"return false;\"><img alt=\"iPad Air 4 Wifi Cellular 256GB (2020) | Đa này sắc giúp bạn dễ dàng sáng tạo chất riêng\" src=\"https://cdn.tgdd.vn/Products/Images/522/228899/ipad-air-4-wifi-cellular-256gb-2020-092120-052118.jpg\" /></a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/hoi-dap/cung-tim-hieu-ve-man-hinh-liquid-retina-tren-iphon-1125106\" target=\"_blank\">M&agrave;n h&igrave;nh Liquid Retina</a>&nbsp;được trang bị tr&ecirc;n iPad Air gen 4 thể hiện xuất sắc từng khung h&igrave;nh b&ecirc;n cạnh một k&iacute;ch thước m&agrave;n h&igrave;nh đ&atilde; được mở rộng l&ecirc;n đến 10.9 inch, đem tới cho người d&ugrave;ng những trải nghiệm tuyệt hảo với những thước phim ấn tượng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/228899/ipad-air-4-wifi-cellular-256gb-2020-092020-052057.jpg\" onclick=\"return false;\"><img alt=\"iPad Air 4 Wifi Cellular 256GB (2020) | Màn hình Liquid Retina, kích thước 10.9 inch\" src=\"https://cdn.tgdd.vn/Products/Images/522/228899/ipad-air-4-wifi-cellular-256gb-2020-092020-052057.jpg\" /></a></p>\r\n\r\n<h3>Quay phim 4K, s&aacute;ng tạo chất ri&ecirc;ng</h3>\r\n\r\n<p>Trang bị camera sau 12 MP, t&iacute;ch hợp quay phim chuẩn 4K, tạo n&ecirc;n những cảnh quay ấn tượng v&agrave; sắc n&eacute;t nhất với chuẩn quay video h&agrave;ng đầu thế giới tablet hiện nay (t&iacute;nh đến th&aacute;ng 9/2020).</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/228899/ipad-air-4-wifi-cellular-256gb-2020-092020-052036.jpg\" onclick=\"return false;\"><img alt=\"iPad Air 4 Wifi Cellular 256GB (2020) | Camera sau 12 MP, quay phim chuẩn 4K\" src=\"https://cdn.tgdd.vn/Products/Images/522/228899/ipad-air-4-wifi-cellular-256gb-2020-092020-052036.jpg\" /></a></p>\r\n\r\n<p>Trở th&agrave;nh một editor chuy&ecirc;n nghiệp với khả năng xử l&yacute; đồ họa mạnh mẽ nhờ chipset A14 Bionic, mang lại tốc độ xử l&yacute; nhanh hơn 30% thế hệ cũ, dễ d&agrave;ng chỉnh sửa v&agrave; tạo n&ecirc;n những t&aacute;c phẩm nghệ thuật đậm chất ri&ecirc;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/228899/ipad-air-4-wifi-cellular-256gb-2020-092120-052113.jpg\" onclick=\"return false;\"><img alt=\"iPad Air 4 Wifi Cellular 256GB (2020) | Chipset A14 Bionic xử lí đồ họa mạnh mẽ\" src=\"https://cdn.tgdd.vn/Products/Images/522/228899/ipad-air-4-wifi-cellular-256gb-2020-092120-052113.jpg\" /></a></p>\r\n\r\n<h3>Cấu h&igrave;nh đỉnh cao, bộ nhớ trong 256 GB</h3>\r\n\r\n<p>Ngo&agrave;i việc trang bị cho m&igrave;nh chipset thế hệ mới nhất 2020 của Apple, A14 Bionic c&ograve;n sở hữu tốc độ CPU nhanh hơn 40% thế hệ cũ, xử l&yacute; Machine Learning nhanh hơn gấp 2 lần, mang lại những trải nghiệm chơi game hay render h&igrave;nh ảnh, video v&ocirc; c&ugrave;ng mượt m&agrave; v&agrave; sắc n&eacute;t.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/228899/ipad-air-4-wifi-cellular-256gb-2020-092020-052049.jpg\" onclick=\"return false;\"><img alt=\"iPad Air 4 Wifi Cellular 256GB (2020) | Bộ nhớ trong 256 GB, mở rộng lên đến 2 TB trên iCloud\" src=\"https://cdn.tgdd.vn/Products/Images/522/228899/ipad-air-4-wifi-cellular-256gb-2020-092020-052049.jpg\" /></a></p>\r\n\r\n<p>Dung lượng lưu trữ l&ecirc;n đến 256 GB, c&oacute; thể mở rộng l&ecirc;n đến 2 TB tr&ecirc;n iCloud, thoải m&aacute;i lưu trữ những game c&oacute; cấu h&igrave;nh nặng, file video hay h&igrave;nh ảnh dung lượng lớn, phục vụ tối đa cho c&ocirc;ng việc v&agrave; học tập hằng ng&agrave;y.</p>\r\n\r\n<h3>Hỗ trợ 4G, tiện &iacute;ch mu&ocirc;n nơi</h3>\r\n\r\n<p>Phi&ecirc;n bản iPad Air 4 Wifi Cellular được t&iacute;ch hợp 4G, dễ d&agrave;ng tương t&aacute;c, ph&aacute; bỏ mọi giới hạn về kh&ocirc;ng gian v&agrave; thời gian trong c&ocirc;ng việc v&agrave; học tập, tr&ecirc;n nền tảng số mạnh mẽ của Apple.</p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/images/522/228899/ipad-air-4-wifi-cellular-256gb-2020-092120-052109.jpg\" onclick=\"return false;\"><img alt=\"iPad Air 4 Wifi Cellular 256GB (2020) | Hỗ trợ 4G muôn nơi\" src=\"https://cdn.tgdd.vn/Products/Images/522/228899/ipad-air-4-wifi-cellular-256gb-2020-092120-052109.jpg\" /></a></h3>\r\n\r\n<h3>T&iacute;ch hợp những tiện &iacute;ch h&agrave;ng đầu</h3>\r\n\r\n<p>iPad Air 4 2020 trang bị khả năng t&iacute;ch hợp&nbsp;<a href=\"https://www.thegioididong.com/phu-kien-thong-minh/but-cam-ung-apple-pencil-gen-2\" target=\"_blank\">Apple Pencil</a>&nbsp;v&agrave;&nbsp;<a href=\"https://www.thegioididong.com/phu-kien-thong-minh/ban-phim-magic-keyboard-us-apple-mla22-trang\" target=\"_blank\">Magic Keyboard</a>&nbsp;nhằm tối ưu h&oacute;a cho c&ocirc;ng việc v&agrave; học tập.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/228899/ipad-air-4-wifi-cellular-256gb-2020-094620-054613.jpg\" onclick=\"return false;\"><img alt=\"iPad Air 4 Wifi Cellular 256GB (2020) | Tương thích Apple Pencil và Magic Keyboard\" src=\"https://cdn.tgdd.vn/Products/Images/522/228899/ipad-air-4-wifi-cellular-256gb-2020-094620-054613.jpg\" /></a></p>\r\n\r\n<p>Mở rộng khả năng s&aacute;ng tạo tr&ecirc;n nền tảng iPadOS được Apple cải tiến nhằm phục vụ tối đa cho trải nghiệm của người d&ugrave;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/228899/ipad-air-4-wifi-cellular-256gb-2020-094620-054609.jpg\" onclick=\"return false;\"><img alt=\"iPad Air 4 Wifi Cellular 256GB (2020) | Bộ nhớ lưu trữ cao, phù hợp mọi đối tượng\" src=\"https://cdn.tgdd.vn/Products/Images/522/228899/ipad-air-4-wifi-cellular-256gb-2020-094620-054609.jpg\" /></a></p>\r\n\r\n<p>iPad Air 4&nbsp; Wifi Cellular 256 GB 2020 l&agrave; một trong những lựa chọn h&agrave;ng đầu hiện nay với hiệu năng si&ecirc;u khủng, quay phim chuẩn 4K, dung lượng bộ nhớ trong l&ecirc;n đến 256 GB v&agrave; t&iacute;ch hợp 4G tiện d&ugrave;ng. M&aacute;y ph&ugrave; hợp với hầu hết mọi đối tượng sử dụng, đặc biệt với người d&ugrave;ng c&oacute; nhu cầu về dung lượng lưu trữ cao v&agrave; t&iacute;nh chất c&ocirc;ng việc thường xuy&ecirc;n di chuyển.</p>', 2, '2021-04-25 21:48:01', '2021-04-25 21:48:07');
INSERT INTO `products` (`id`, `name`, `images`, `price`, `quantity`, `warranty`, `accessories`, `discount`, `status`, `active`, `hot`, `desc`, `categories_id`, `created_at`, `updated_at`) VALUES
(22, 'Máy tính bảng iPad Pro 11 inch Wifi 128GB (2020)', '5.jpg', 21190000, 20, '12 tháng', 'Cáp - Sạc -Tai nghe', '0', 'Nguyên Seal - Chính Hãng', 1, 0, '<h2>Đặc điểm nổi bật của iPad Pro 11 inch Wifi 128GB (2020)</h2>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/522/220163/Slider/ipad-pro-11-inch-2020-073220-033200-788.jpg\" /></p>\r\n\r\n<h2><a href=\"https://www.thegioididong.com/may-tinh-bang/ipad-pro-11-inch-2020\" target=\"_blank\">iPad Pro 11 inch 2020</a>&nbsp;l&agrave; mẫu&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-apple-ipad\" target=\"_blank\">iPad</a>&nbsp;mới nhất vừa được&nbsp;<a href=\"https://www.thegioididong.com/apple\" target=\"_blank\">Apple</a>&nbsp;ra mắt v&agrave;o 18/3 với thiết kế gần như kh&ocirc;ng thay đổi so với thế hệ trước, chủ yếu l&agrave; sự n&acirc;ng cấp đến từ chip A12Z cho hiệu năng mạnh mẽ v&agrave; cụm camera c&oacute; cảm biến mới hỗ trợ tăng cường thực tế ảo.</h2>\r\n\r\n<h3>Cấu h&igrave;nh khủng vượt giới hạn của một chiếc tablet</h3>\r\n\r\n<p>Được Apple trang bị vi xử l&yacute; A12Z Bionic mới nhất, iPad Pro 11 inch c&oacute; một tốc độ vượt trội, c&oacute; thể so s&aacute;nh được với c&aacute;c mẫu&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\">m&aacute;y t&iacute;nh x&aacute;ch tay</a>&nbsp;hiện tại.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/220163/ipad11inch-1.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng iPad Pro 2020 có một cấu hình khủng gần như tương đương PC\" src=\"https://cdn.tgdd.vn/Products/Images/522/220163/ipad11inch-1.jpg\" /></a></p>\r\n\r\n<p>Mọi ứng dụng hoạt động tr&ecirc;n iPad trước vốn đ&atilde; mượt, nay c&ograve;n nhanh v&agrave; tr&ocirc;i chảy hơn, từ&nbsp;c&aacute;c t&aacute;c vụ đơn giản h&agrave;ng ng&agrave;y cho đến c&aacute;c c&ocirc;ng việc chuy&ecirc;n s&acirc;u về đồ họa đều được xử l&yacute; nhanh ch&oacute;ng.</p>\r\n\r\n<p>Apple cũng đ&atilde; tăng cường đến 8 l&otilde;i&nbsp;xử l&yacute; đồ họa, gi&uacute;p cho iPad Pro 2020 trở th&agrave;nh một chiếc&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\">m&aacute;y t&iacute;nh bảng</a>&nbsp;đ&aacute;p ứng tuyệt vời cho nhu cầu đồ họa, giải tr&iacute; v&agrave; chơi game.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/220163/ipad11inch-3.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng Apple iPad Pro 11 inch 2020 sở hữu hiệu năng mạnh mẽ\" src=\"https://cdn.tgdd.vn/Products/Images/522/220163/ipad11inch-3.jpg\" /></a></p>\r\n\r\n<p>iPad&nbsp;Pro 11 inch 2020 vẫn duy tr&igrave; sử dụng m&agrave;n h&igrave;nh Liquid Retina c&ugrave;ng c&aacute;c t&iacute;nh năng h&agrave;ng đầu như ProMotion, tần số qu&eacute;t l&ecirc;n đến 120 Hz, True Tone, gi&uacute;p cho iPad Pro trở th&agrave;nh một trong những thiết bị di động sở hữu m&agrave;n h&igrave;nh tốt nhất hiện nay (3/2020).</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/cung-tim-hieu-ve-man-hinh-liquid-retina-tren-iphon-1125106\" target=\"_blank\">C&ugrave;ng t&igrave;m hiểu về m&agrave;n h&igrave;nh Liquid Retina tr&ecirc;n iPhone XR</a></p>\r\n\r\n<p>Chất lượng hiển thị tr&ecirc;n iPad Pro 2020 l&agrave; cực kỳ xuất sắc, dải m&agrave;u rộng hiển thị chuẩn x&aacute;c nhằm tối ưu cho việc chỉnh sửa đồ họa chuy&ecirc;n nghiệp, độ s&aacute;ng cao 600 nit đ&atilde; gi&uacute;p bạn c&oacute; thể sử dụng m&aacute;y ngo&agrave;i trời một c&aacute;ch dễ d&agrave;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/220163/ipad-pro-11-2020-1.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng iPad Pro 11 inch 2020 | Màn hình Liquid Retina\" src=\"https://cdn.tgdd.vn/Products/Images/522/220163/ipad-pro-11-2020-1.jpg\" /></a></p>\r\n\r\n<h3>Camera đa t&iacute;nh năng, hỗ trợ AR tốt hơn</h3>\r\n\r\n<p>Sự thay đổi&nbsp;về ngoại h&igrave;nh duy nhất của&nbsp;iPad Pro 2020 c&oacute; lẽ l&agrave; cụm camera sau h&igrave;nh vu&ocirc;ng ở mặt lưng với thiết kế tương tự d&ograve;ng iPhone 11. M&ocirc;-đun camera n&agrave;y chứa 3 cảm biến, gồm camera ch&iacute;nh g&oacute;c rộng 12 MP v&agrave; camera g&oacute;c si&ecirc;u rộng 10 MP v&agrave; cảm biến mới c&oacute; t&ecirc;n&nbsp;LiDAR.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/220163/ipad-pro-11-2020-2.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng iPad Pro 11 inch 2020 với cụm camera sau hình vuông\" src=\"https://cdn.tgdd.vn/Products/Images/522/220163/ipad-pro-11-2020-2.jpg\" /></a></p>\r\n\r\n<p>Camera ch&iacute;nh 12 MP hỗ trợ chụp những tấm ảnh thường ng&agrave;y r&otilde; n&eacute;t. Trong khi đ&oacute;, ống k&iacute;nh Ultra Wide 10 MP tăng cường độ mở cho g&oacute;c chụp si&ecirc;u rộng ấn tượng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/220163/ipad11inch-7.jpg\" onclick=\"return false;\"><img alt=\"Chụp hình và chỉnh sửa nhanh chóng với Máy tính bảng iPad Pro 2020 11 inch\" src=\"https://cdn.tgdd.vn/Products/Images/522/220163/ipad11inch-7.jpg\" /></a></p>\r\n\r\n<p>Với giao diện camera tối giản, dễ d&ugrave;ng tr&ecirc;n iPad, bạn c&oacute; thể chụp to&agrave;n bộ căn ph&ograve;ng trong một lần chụp bằng camera Ultra Wide v&agrave; chuyển đến ứng dụng để thiết kế ngay v&agrave; lưu lại t&aacute;c phẩm của m&igrave;nh với những thao t&aacute;c cực kỳ đơn giản, nhanh ch&oacute;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/220163/ipad-pro-11-inch-2020.gif\" onclick=\"return false;\"><img alt=\"Máy tính bảng iPad Pro 2020 11 inch | Chế độ VR mượt mà nhờ cảm biến LiDAR\" src=\"https://cdn.tgdd.vn/Products/Images/522/220163/ipad-pro-11-inch-2020.gif\" /></a></p>\r\n\r\n<p>Hệ thống camera&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-apple-ipad-pro\" target=\"_blank\">iPad Pro</a>&nbsp;vận h&agrave;nh linh hoạt đến kh&ocirc;ng ngờ. Từ việc quay, chỉnh sửa cho đến chia sẻ video 4K, tất cả thật dễ d&agrave;ng tr&ecirc;n c&ugrave;ng một thiết bị. Bạn cũng c&oacute; thể quay video ho&agrave;n hảo với micro đạt chuẩn ph&ograve;ng thu, ph&aacute;t &acirc;m thanh 4 k&ecirc;nh, l&agrave;m cho phần nghe của thước phim trở n&ecirc;n sống động hơn.</p>\r\n\r\n<p>Cảm biến LiDAR (Light Detection and Ranging) l&agrave; một c&ocirc;ng nghệ h&igrave;nh ảnh mới, lần đầu ti&ecirc;n được Apple t&iacute;ch hợp tr&ecirc;n thiết bị của m&igrave;nh.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/220163/ipad11inch-8.jpg\" onclick=\"return false;\"><img alt=\"Cảm biến LiDAR trên iPad Pro 11 inch 2020 hỗ trợ thực tế ảo mạnh mẽ hơn\" src=\"https://cdn.tgdd.vn/Products/Images/522/220163/ipad11inch-8.jpg\" /></a></p>\r\n\r\n<p>Cảm biến n&agrave;y hoạt động dựa tr&ecirc;n nguy&ecirc;n l&yacute; thời gian phản xạ của &aacute;nh s&aacute;ng đến vật thể để phục vụ đo chiều s&acirc;u, t&iacute;nh to&aacute;n khoảng c&aacute;ch một c&aacute;ch ch&iacute;nh x&aacute;c hỗ trợ đắc lực cho hoạt động của c&aacute;c ứng dụng AR.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/220163/ipad-pro-11-2020-3.jpg\" onclick=\"return false;\"><img alt=\"Camera TrueDepth ở mặt trước máy tính bảng iPad Pro 2020\" src=\"https://cdn.tgdd.vn/Products/Images/522/220163/ipad-pro-11-2020-3.jpg\" /></a></p>\r\n\r\n<p>Camera TrueDepth ở mặt trước sẽ hỗ trợ nhận diện khu&ocirc;n mặt 3D Face ID,&nbsp;chụp ảnh ch&acirc;n dung. Tr&ograve; chuyện với bạn b&egrave; qua FaceTime sẽ trở n&ecirc;n th&uacute; vị hơn với&nbsp;t&iacute;nh năng Animoji vui nhộn.</p>\r\n\r\n<h3>Thiết kế quen thuộc, vẫn bền bỉ v&agrave; đẳng cấp</h3>\r\n\r\n<p>Kh&ocirc;ng qu&aacute; nhiều thay đối với thế hệ trước, iPad Pro 2020 vẫn mang ng&ocirc;n ngữ thiết kế kim loại nguy&ecirc;n khối với độ ho&agrave;n thiện cực cao, 4 cạnh được v&aacute;t thẳng vu&ocirc;ng vức, gi&uacute;p chiếc m&aacute;y th&ecirc;m phần &quot;Pro&quot;, sang trọng v&agrave; nam t&iacute;nh hơn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/220163/ipad-pro-11-2020-4-1.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng iPad Pro với thiết kế mỏng nhẹ ấn tượng\" src=\"https://cdn.tgdd.vn/Products/Images/522/220163/ipad-pro-11-2020-4-1.jpg\" /></a></p>\r\n\r\n<p>Kh&ocirc;ng c&ograve;n n&uacute;t Home, m&agrave;n h&igrave;nh tr&ecirc;n iPad Pro nay được k&eacute;o tối đa ra 4 cạnh gi&uacute;p cho m&aacute;y c&oacute; một&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang?g=tren-10\" target=\"_blank\">m&agrave;n h&igrave;nh lớn</a>&nbsp;nhưng vẫn c&oacute; giữ được sự nhỏ gọn. M&aacute;y chỉ mỏng 5.9 mm v&agrave; nhẹ 471 g để bạn c&oacute; thể mang theo b&ecirc;n m&igrave;nh hay thoải m&aacute;i cầm tay sử dụng trong thời gian d&agrave;i.</p>\r\n\r\n<p>B&ecirc;n trong m&aacute;y l&agrave; một vi&ecirc;n pin c&oacute; c&ocirc;ng suất 28.65 Wh c&oacute; thể hoạt động l&ecirc;n đến 10 tiếng li&ecirc;n tục, chiếc m&aacute;y t&iacute;nh bảng từ nh&agrave; T&aacute;o sẽ lu&ocirc;n đủ pin đ&aacute;p ứng cho nhu cầu c&ocirc;ng việc của bạn trong ng&agrave;y.</p>\r\n\r\n<h3>iPadOS tối ưu đa nhiệm v&agrave; hỗ trợ phụ kiện ngo&agrave;i ho&agrave;n to&agrave;n mới&nbsp;</h3>\r\n\r\n<p>iPadOS được thiết kế để khai th&aacute;c sức mạnh v&agrave; hiệu suất của iPad Pro. Phần cứng nhanh v&agrave; phần mềm mạnh mẽ được thiết kế đồng bộ để gi&uacute;p mọi t&aacute;c vụ trở n&ecirc;n linh hoạt v&agrave; mượt m&agrave;.&nbsp;</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tat-ca-khai-niem-moi-ve-ipados-1171675\" target=\"_blank\">Tất cả kh&aacute;i niệm mới về iPadOS</a><a href=\"https://www.thegioididong.com/images/522/220163/ipad11inch-9.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng iPad Pro 2020 với hệ điều hành iPadOS tối ưu cho đa nhiệm\" src=\"https://cdn.tgdd.vn/Products/Images/522/220163/ipad11inch-9.jpg\" /></a></p>\r\n\r\n<p>Nhờ c&oacute; iPadOS tối ưu, bạn c&oacute; thể l&agrave;m việc đa nhiệm nhiều ứng dụng c&ugrave;ng một l&uacute;c, thao t&aacute;c chuyển đổi hay chia m&agrave;n h&igrave;nh để sử dụng 2 ứng dụng rất nhanh ch&oacute;ng v&agrave; dễ d&agrave;ng.</p>\r\n\r\n<p>B&ecirc;n cạnh&nbsp;<a href=\"https://www.thegioididong.com/phu-kien-thong-minh/apple-pencil\" target=\"_blank\">Apple Pencil</a>, iPad Pro c&ograve;n hỗ trợ th&ecirc;m phụ kiện mới l&agrave; b&agrave;n ph&iacute;m Magic Keyboard, với thiết kế mới, trang bị ph&iacute;m bấm cắt k&eacute;o như tr&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/laptop/apple-macbook-air-2020\" target=\"_blank\">MacBook Air 2020</a>, bổ sung trackpad điều khiển con trỏ chuột linh hoạt, đ&atilde; gi&uacute;p cho iPad gần như trở th&agrave;nh một chiếc laptop nhỏ gọn mạnh mẽ phục vụ cho c&ocirc;ng việc của bạn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/220163/ipad11inch-2.png\" onclick=\"return false;\"><img alt=\"Máy tính bảng iPad Pro 2020 11 inch | Bàn phím Magic Keyboard nhiều tính năng\" src=\"https://cdn.tgdd.vn/Products/Images/522/220163/ipad11inch-2.png\" /></a></p>\r\n\r\n<p>Magic Keyboard l&agrave; mẫu phụ kiện mới.&nbsp;Cũng giống như Apple Pencil, rất tiếc phụ kiện n&agrave;y cũng sẽ kh&ocirc;ng c&oacute; sẵn v&agrave; được b&aacute;n ri&ecirc;ng với iPad Pro.</p>\r\n\r\n<p>T&oacute;m lại, vừa duy tr&igrave; những điểm mạnh về thiết kế đồng thời n&acirc;ng cấp s&acirc;u ở phần cứng b&ecirc;n trong, iPad Pro 11 inch 2020 tiếp tục l&agrave; một chiếc m&aacute;y t&iacute;nh bảng h&agrave;ng đầu ở thời điểm hiện tại.&nbsp;</p>\r\n\r\n<p>Đối với với những ai l&agrave;m việc trong lĩnh vực đồ họa chuy&ecirc;n nghiệp, chỉnh sửa video hay c&ocirc;ng việc cần di chuyển li&ecirc;n tục đang t&igrave;m một thiết bị gọn nhẹ c&oacute; t&iacute;nh di động cao, th&igrave; chiếc m&aacute;y t&iacute;nh bảng từ nh&agrave; T&aacute;o ch&iacute;nh l&agrave; sự lựa chọn tốt nhất d&agrave;nh cho bạn.</p>', 2, '2021-04-25 21:52:11', '2021-04-25 21:58:05'),
(23, 'Máy tính bảng iPad Air 4 Wifi 256GB (2020)', 'ipad-air-4-wifi-64gb-2020-xanhla-600x600-600x600.jpg', 20690000, 20, '12 tháng', 'Cáp - Sạc -Tai nghe', '0', 'Nguyên Seal - Chính Hãng', 1, 0, '<h2>Đặc điểm nổi bật của iPad Air 4 Wifi 256GB (2020)</h2>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/522/228896/Slider/ipad-air-4-wifi-256gb-2020-121020-0207068.jpg\" /></p>\r\n\r\n<h3>Ngay khi vừa ra mắt, iPad Air 4&nbsp;đ&atilde; g&acirc;y ra một cơn sốt cho giới c&ocirc;ng nghệ to&agrave;n cầu, khi sử dụng chipset A14 Bionic với hiệu năng cực khủng, b&ecirc;n cạnh một thiết kế cao cấp v&agrave; những c&ocirc;ng nghệ h&agrave;ng đầu thế giới&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\">tablet</a>&nbsp;hiện nay.</h3>\r\n\r\n<h3>Thiết kế ho&agrave;n mỹ, c&ocirc;ng nghệ hiển thị đỉnh cao</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/may-tinh-bang/ipad-air-4-wifi-256gb-2020\" target=\"_blank\">iPad Air 4 Wifi 256GB 2020</a>&nbsp;được thay đổi gần như to&agrave;n bộ thiết kế ở phần mặt trước khi so s&aacute;nh với thế hệ&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang/ipad-air-105-inch-wifi-2019\" target=\"_blank\">iPad Air 3</a>&nbsp;tiền nhiệm, n&uacute;t Home tr&ecirc;n Air 2020 được loại bỏ, mở rộng kh&ocirc;ng gian hiển thị với k&iacute;ch thước lớn hơn, đồng thời t&iacute;ch hợp Touch ID tr&ecirc;n n&uacute;t nguồn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/228896/ipad-air-4-wifi-256gb-2020-301920-071928.jpg\" onclick=\"return false;\"><img alt=\"iPad Air 4 | Lượt bỏ nút Home, tích hợp Touch ID trên nút nguốn\" src=\"https://cdn.tgdd.vn/Products/Images/522/228896/ipad-air-4-wifi-256gb-2020-301920-071928.jpg\" /></a><br />\r\nMặt lưng m&aacute;y vẫn giữ được ng&ocirc;n ngữ thiết kế ri&ecirc;ng tr&ecirc;n d&ograve;ng Air, đường n&eacute;t tinh xảo v&agrave; tỉ mỉ, đặc biệt chế t&aacute;c bằng những chất liệu th&acirc;n thiện với m&ocirc;i trường.</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, m&aacute;y c&ograve;n c&oacute; th&ecirc;m nhiều lựa chọn m&agrave;u sắc đặc biệt hơn so với c&aacute;c thế hệ&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-apple-ipad\" target=\"_blank\">iPad</a>&nbsp;tiền nhiệm, dễ d&agrave;ng lựa chọn để tạo n&ecirc;n những dấu ấn đậm chất ri&ecirc;ng của bạn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/228896/ipad-air-4-wifi-256gb-2020-301520-081544.jpg\" onclick=\"return false;\"><img alt=\"iPad Air 4 | Nhiều lựa chọn màu sắc đặc biệt\" src=\"https://cdn.tgdd.vn/Products/Images/522/228896/ipad-air-4-wifi-256gb-2020-301520-081544.jpg\" /></a></p>\r\n\r\n<p>iPad Air 4 trang bị m&agrave;n h&igrave;nh Liquid Retina 10.9 inch, t&iacute;ch hợp c&ocirc;ng nghệ True Tone cho khả năng hiển thị gần như ho&agrave;n hảo với m&agrave;u sắc gốc, kết hợp c&ugrave;ng dải m&agrave;u CPI-P3 chuẩn điện ảnh Hoa Kỳ, mang lại những trải nghiệm h&igrave;nh ảnh đỉnh cao với chất lượng m&agrave;u sắc v&ocirc; c&ugrave;ng trung thực.</p>\r\n\r\n<h3><br />\r\nN&acirc;ng cấp camera, quay phim 4K</h3>\r\n\r\n<p>Chuẩn quay video 4K được t&iacute;ch hợp tr&ecirc;n camera 12 MP ở mặt sau trở th&agrave;nh một n&acirc;ng cấp đ&aacute;ng gi&aacute; tr&ecirc;n chiếc&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-apple-ipad-air\" target=\"_blank\">iPad d&ograve;ng Air</a>&nbsp;thế hệ mới nhất của Apple (t&iacute;nh đến th&aacute;ng 9/2020).</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/228896/ipad-air-4-wifi-256gb-2020-301920-071952.jpg\" onclick=\"return false;\"><img alt=\"iPad Air 4 | Quay video 4K tích hợp trên Camera 12MB\" src=\"https://cdn.tgdd.vn/Products/Images/522/228896/ipad-air-4-wifi-256gb-2020-301920-071952.jpg\" /></a></p>\r\n\r\n<p>Sử dụng con chip A14 Bionic với hiệu năng h&agrave;ng đầu thế giới chipset hiện nay, cho khả năng chỉnh sửa video mượt m&agrave; với tốc độ xử l&yacute; đồ họa nhanh hơn 30%, mang lại những thước phim xuất sắc đến từng khung h&igrave;nh.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/228896/ipad-air-4-wifi-256gb-2020-301920-071934.jpg\" onclick=\"return false;\"><img alt=\"iPad Air 4 | Chip A14 Bionic hiệu năng, chỉnh sửa video mượt mà\" src=\"https://cdn.tgdd.vn/Products/Images/522/228896/ipad-air-4-wifi-256gb-2020-301920-071934.jpg\" /></a></p>\r\n\r\n<h3>Hiệu năng si&ecirc;u khủng c&ugrave;ng bộ nhớ trong 256 GB</h3>\r\n\r\n<p>Kh&ocirc;ng phải b&agrave;n c&atilde;i khi iPad Air 4 256 GB l&agrave; chiếc tablet đ&aacute;ng được sở hữu nhất hiện nay, với hiệu năng h&agrave;ng đầu thế giới nhờ con chip A14 Bionic mới nhất của Apple, tăng cường tốc độ CPU hơn 40%, xử l&yacute; Machine Learning tăng gấp 2 lần, mang lại những trải nghiệm đồ họa đỉnh cao tr&ecirc;n những tựa game hot nhất, nặng nhất với cấu h&igrave;nh max setting.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/228896/ipad-air-4-wifi-256gb-2020-301920-071946.jpg\" onclick=\"return false;\"><img alt=\"iPad Air 4 | Bộ nhớ trong lên đến 256 GB\" src=\"https://cdn.tgdd.vn/Products/Images/522/228896/ipad-air-4-wifi-256gb-2020-301920-071946.jpg\" /></a></p>\r\n\r\n<p>Dung lượng bộ nhớ trong cực khủng l&ecirc;n đến 256 GB, thoải m&aacute;i lưu trữ những video, h&igrave;nh ảnh hay dữ liệu quan trọng, mở rộng th&ecirc;m bộ nhớ hơn 2000 GB tr&ecirc;n iCloud, th&aacute;ch thức mọi giới hạn lưu trữ trong thế giới tablet hiện nay.</p>\r\n\r\n<p>Thế hệ Wifi 6 mới nhất cũng được t&iacute;ch hợp tr&ecirc;n mẫu iPad d&ograve;ng Air 2020 của Apple, cho khả năng kết nối internet mượt m&agrave;, nhanh ch&oacute;ng v&agrave; tiện lợi.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/228896/ipad-air-4-wifi-256gb-2020-301920-071940.jpg\" onclick=\"return false;\"><img alt=\"iPad Air 4 | Thế hệ Wifi 6 mới nhất\" src=\"https://cdn.tgdd.vn/Products/Images/522/228896/ipad-air-4-wifi-256gb-2020-301920-071940.jpg\" /></a></p>\r\n\r\n<h3>T&iacute;ch hợp nhiều c&ocirc;ng cụ hỗ trợ</h3>\r\n\r\n<p>Theo xu hướng hiện nay, tablet đang dần được n&acirc;ng cấp để thay thế cho một chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\">laptop</a>&nbsp;th&ocirc;ng thường, ch&iacute;nh v&igrave; thế iPad Air 4 đ&atilde; tận dụng để c&oacute; thể kết nối với&nbsp;<a href=\"https://www.thegioididong.com/phu-kien-thong-minh/but-cam-ung-apple-pencil-gen-2\" target=\"_blank\">Apple Pencil 2</a>&nbsp;hay&nbsp;<a href=\"https://www.thegioididong.com/phu-kien-thong-minh/ban-phim-magic-keyboard-us-apple-mla22-trang\" target=\"_blank\">Magic Keyboard</a>&nbsp;nhằm tối ưu h&oacute;a c&ocirc;ng việc.<br />\r\nLưu &yacute;: b&agrave;n ph&iacute;m v&agrave; Apple Pencil kh&ocirc;ng b&aacute;n k&egrave;m theo m&aacute;y.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/228896/ipad-air-4-wifi-256gb-2020-301920-071958.jpg\" onclick=\"return false;\"><img alt=\"iPad Air 4 |  Có thể kết nối với Apple Pencil 2 hay Magic Keyboard\" src=\"https://cdn.tgdd.vn/Products/Images/522/228896/ipad-air-4-wifi-256gb-2020-301920-071958.jpg\" /></a></p>\r\n\r\n<p>Kh&ocirc;ng chỉ ở c&ocirc;ng cụ hỗ trợ m&agrave; phần hiệu năng đến từ A14 Bionic, kho ứng dụng Apple Store, m&agrave; hệ điều h&agrave;nh iPadOS 14 đ&atilde; được tối ưu h&oacute;a nhằm mang lại hiệu quả cao nhất cho học tập v&agrave; c&ocirc;ng việc b&ecirc;n cạnh việc duy tr&igrave; một nền tảng giải tr&iacute; đỉnh cao.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/228896/ipad-air-4-wifi-256gb-2020-302020-072010.jpg\" onclick=\"return false;\"><img alt=\"iPad Air 4 | Đáp ứng nhu cầu công việc, học tập, giải trí hằng ngày\" src=\"https://cdn.tgdd.vn/Products/Images/522/228896/ipad-air-4-wifi-256gb-2020-302020-072010.jpg\" /></a></p>\r\n\r\n<p>iPad Air 4 Wifi 256GB 2020 l&agrave; một trong những tablet h&agrave;ng đầu thế giới với hiệu năng khủng từ chipset A14 Bionic, b&ecirc;n cạnh những cải tiến nhằm tối ưu h&oacute;a cho c&ocirc;ng việc, học tập v&agrave; giải tr&iacute;. iPad Air 4 ph&ugrave; hợp với hầu hết mọi đối tượng người d&ugrave;ng, đặc biệt với người sử dụng cần một dung lượng lưu trữ lớn.</p>', 2, '2021-04-25 21:53:27', '2021-04-25 21:58:03'),
(24, 'Máy tính bảng iPad Air 4 Wifi 64GB (2020)', 'ipad-air-4-wifi-64gb-2020-xanhduong-600x600-600x600.jpg', 16190000, 10, '12 tháng', 'Cáp - Sạc -Tai nghe', '300000', 'Nguyên Seal - Chính Hãng', 1, 0, '<h2>Đặc điểm nổi bật của iPad Air 4 Wifi 64GB (2020)</h2>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/522/228808/Slider/ipad-air-4-wifi-64gb-2020-121020-0207348.jpg\" /></p>\r\n\r\n<h3>Mới đ&acirc;y,&nbsp;<a href=\"https://www.thegioididong.com/apple\" target=\"_blank\">Apple</a>&nbsp;vừa tr&igrave;nh l&agrave;ng m&aacute;y t&iacute;nh bảng&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang/ipad-air-4-wifi-64gb-2020\" target=\"_blank\">iPad Air 4 Wifi 64 GB (2020)</a>. Đ&acirc;y l&agrave; thiết bị đầu ti&ecirc;n của h&atilde;ng được trang bị chip A14 Bionic - chip di động mạnh nhất của Apple hiện nay. V&agrave; c&oacute; m&agrave;n lột x&aacute;c nhờ thiết kế được thừa hưởng từ&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-apple-ipad-pro\" target=\"_blank\">iPad Pro</a>&nbsp;với viền m&agrave;n h&igrave;nh mỏng bo cong quanh m&aacute;y.</h3>\r\n\r\n<h3>Chip mạnh h&agrave;ng đầu cho trải nghiệm ho&agrave;n hảo</h3>\r\n\r\n<p>Apple trang bị cho&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-apple-ipad-air\" target=\"_blank\">iPad Air</a>&nbsp;4 chip A14 Bionic 6 nh&acirc;n được sản xuất bởi TSMC với tiến tr&igrave;nh 5 nm hiện đại nhất hiện nay. Số lượng b&oacute;ng b&aacute;n dẫn đạt 11.8 tỷ cao hơn 40% so với A13 Bionic (8.5 tỷ). C&oacute; thể thấy số lượng b&oacute;ng b&aacute;n dẫn c&agrave;ng lớn th&igrave; chip c&agrave;ng mạnh v&agrave; tiết kiệm năng lượng hơn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/228808/ipad-air-4-wifi-64gb-2020-183320-033337.jpg\" onclick=\"return false;\"><img alt=\"Trang bị chip A14 mạnh hàng đầu của Apple | iPad Air 2020\" src=\"https://cdn.tgdd.vn/Products/Images/522/228808/ipad-air-4-wifi-64gb-2020-183320-033337.jpg\" /></a></p>\r\n\r\n<p>A14 Bionic c&oacute; 6 l&otilde;i chip, trong đ&oacute; 2 l&otilde;i hiệu suất cao cho t&aacute;c vụ phức tạp v&agrave; 4 l&otilde;i c&ograve;n lại cho c&aacute;c t&aacute;c vụ th&ocirc;ng thường.</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, bộ xử l&yacute; đồ hoạ GPU 4 l&otilde;i mang lại hiệu suất tối đa, nhanh hơn 30% so với thế hệ trước hứa hẹn iPad Air c&oacute; thể chơi c&aacute;c tr&ograve; chơi phức tạp đ&ograve;i hỏi độ ph&acirc;n giải cao, video 4K,...</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/228808/ipad-air-4-wifi-64gb-2020-181220-051239.jpg\" onclick=\"return false;\"><img alt=\"Chơi game hay nặng hay xem video 4K đều mượt mà | iPad Air 2020\" src=\"https://cdn.tgdd.vn/Products/Images/522/228808/ipad-air-4-wifi-64gb-2020-181220-051239.jpg\" /></a></p>\r\n\r\n<p>A14 Bionic mang đến tốc độ xử l&yacute; vượt trội, nhanh ch&oacute;ng hơn c&ugrave;ng với khả năng xử l&yacute; đa nhiệm mượt m&agrave; cũng như bộ nhớ trong 64 GB cho bạn kh&ocirc;ng gian lưu trữ h&igrave;nh ảnh, video,...</p>\r\n\r\n<h3>Tinh tế trong thiết kế c&ugrave;ng nhiều m&agrave;u sắc đi k&egrave;m</h3>\r\n\r\n<p>iPad Air 4 c&oacute; kiểu d&aacute;ng mới tương tự iPad Pro 2020 nhưng c&oacute; k&iacute;ch thước nhỏ hơn v&agrave; d&agrave;y chỉ 6.1 mm, trọng lượng đạt 460 g dễ d&agrave;ng mang theo b&ecirc;n m&igrave;nh mọi l&uacute;c mọi nơi. Thiết kế n&agrave;y gi&uacute;p tương th&iacute;ch với b&agrave;n ph&iacute;m Apple Smart Keyboard Folio, Magic Keyboard của iPad Pro 11 inch v&agrave; hỗ trợ b&uacute;t Apple Pencil 2.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/228808/ipad-air-4-wifi-64gb-2020-184520-034526.jpg\" onclick=\"return false;\"><img alt=\"Bàn phím Apple Smart Keyboard Folio | iPad Air 2020\" src=\"https://cdn.tgdd.vn/Products/Images/522/228808/ipad-air-4-wifi-64gb-2020-184520-034526.jpg\" /></a></p>\r\n\r\n<p>Thiết kế trong iPad Air 4 vu&ocirc;ng vắn hơn nếu so với những d&ograve;ng iPad trước, c&aacute;c g&oacute;c cạnh được bo tr&ograve;n nhẹ nh&agrave;ng tạo cảm gi&aacute;c mềm mại hơn cho tổng thể.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/228808/ipad-air-4-wifi-64gb-2020-182620-032617.jpg\" onclick=\"return false;\"><img alt=\"Góc cạnh bo tròn cho cảm giác mềm mại  | iPad Air 2020\" src=\"https://cdn.tgdd.vn/Products/Images/522/228808/ipad-air-4-wifi-64gb-2020-182620-032617.jpg\" /></a></p>\r\n\r\n<p>iPad Air 2020 được thiết kế nguy&ecirc;n khối đẳng cấp c&ugrave;ng nhiều m&agrave;u sắc trẻ trung cho bạn c&oacute; nhiều sự lựa chọn hơn như: bạc, x&aacute;m đen, hồng, xanh l&aacute; v&agrave; xanh dương.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/228808/ipad-air-4-wifi-64gb-2020-185420-035411.jpg\" onclick=\"return false;\"><img alt=\"Đa màu sắc tăng thêm sự lựa chọn | iPad Air 2020\" src=\"https://cdn.tgdd.vn/Products/Images/522/228808/ipad-air-4-wifi-64gb-2020-185420-035411.jpg\" /></a></p>\r\n\r\n<p>N&uacute;t Home truyền thống được loại bỏ, tạo kh&ocirc;ng gian hiển thị rộng hơn v&agrave; t&iacute;ch hợp cảm biến v&acirc;n tay Touch ID tr&ecirc;n n&uacute;t nguồn đặt ở ph&iacute;a tr&ecirc;n của th&acirc;n m&aacute;y. Đồng thời, iPad Air trở th&agrave;nh sản phẩm đầu ti&ecirc;n của Apple t&iacute;ch hợp t&iacute;nh năng nhận diện v&acirc;n tay v&agrave;o chung với n&uacute;t nguồn.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/228808/ipad-air-4-wifi-64gb-2020-185820-035805.jpg\" onclick=\"return false;\"><img alt=\"Touch ID tích hợp nút nguồn mở khoá nhanh chóng | iPad Air 2020\" src=\"https://cdn.tgdd.vn/Products/Images/522/228808/ipad-air-4-wifi-64gb-2020-185820-035805.jpg\" /></a></p>\r\n\r\n<h3>M&agrave;n h&igrave;nh 60 Hz kh&ocirc;ng gian hiển thị tuyệt vời</h3>\r\n\r\n<p>iPad Air 2020 sử dụng c&ocirc;ng nghệ Liquid Retina v&agrave; c&oacute; độ ph&acirc;n giải 1640 x 2360 Pixels v&agrave; c&oacute; tần số qu&eacute;t đến 60 Hz gi&uacute;p iPad Air hiển thị h&igrave;nh ảnh mượt m&agrave; hơn, c&aacute;c chuyển động h&igrave;nh ảnh trơn tru hơn, gần như loại bỏ ho&agrave;n to&agrave;n t&igrave;nh trạng giật lag.Từ đ&oacute;, bạn c&oacute; thể tận hưởng trọn ven những thước phim bom tấn ấn tượng hay chơi game phải gọi l&agrave; &quot;bao mướt&quot;.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/228808/ipad-air-4-wifi-64gb-2020-183220-053216.jpg\" onclick=\"return false;\"><img alt=\"Màn hình đạt chuẩn Liquid Retina | iPad Air 2020\" src=\"https://cdn.tgdd.vn/Products/Images/522/228808/ipad-air-4-wifi-64gb-2020-183220-053216.jpg\" /></a></p>\r\n\r\n<p>M&agrave;n h&igrave;nh của iPad Air c&oacute; hỗ trợ hỗ trợ dải m&agrave;u rộng DCI-P3 với t&iacute;nh năng True-Tone cho khả năng t&aacute;i tạo m&agrave;u sắc ch&iacute;nh x&aacute;c hỗ trợ ưu việt cho c&ocirc;ng việc đồ họa. Hơn nữa, c&oacute; lớp phủ chống l&oacute;a bề mặt, độ s&aacute;ng 500 nits gi&uacute;p lu&ocirc;n hiển thị tốt trong nhiều điều &aacute;nh s&aacute;ng kh&aacute;c nhau, ngo&agrave;i trời cũng kh&ocirc;ng th&agrave;nh vấn đề.</p>\r\n\r\n<h3>Camera đơn đa chức năng</h3>\r\n\r\n<p>Camera sau c&oacute; độ ph&acirc;n giải 12 MP với khẩu độ f/1.8 c&ugrave;ng với đ&oacute; l&agrave; khả năng quay video 4K/60fps chống rung được n&acirc;ng cấp, hỗ trợ Smart HDR, slow motion 240fps v&agrave; kh&ocirc;ng c&oacute; đ&egrave;n flash LED.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/228808/ipad-air-4-wifi-64gb-2020-181820-041847.jpg\" onclick=\"return false;\"><img alt=\"Camera sau với cảm biến 12 MP | iPad Air 2020\" src=\"https://cdn.tgdd.vn/Products/Images/522/228808/ipad-air-4-wifi-64gb-2020-181820-041847.jpg\" /></a><br />\r\nỞ mặt trước được trang bị camera 7 MP, khẩu độ f/2.2, hỗ trợ quay video Full HD 60fps, hỗ trợ chụp ảnh ch&acirc;n dung, phục vụ tốt c&aacute;c t&aacute;c vụ gọi Facetime họp h&agrave;nh hay đơn giản l&agrave; tr&ograve; chuyện c&ugrave;ng người th&acirc;n.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/228808/ipad-air-4-wifi-64gb-2020-182120-042155.jpg\" onclick=\"return false;\"><img alt=\"Camera FaceTime với cảm biến 7 MP | iPad Air 2020\" src=\"https://cdn.tgdd.vn/Products/Images/522/228808/ipad-air-4-wifi-64gb-2020-182120-042155.jpg\" /></a></p>\r\n\r\n<h3>Cổng sạc Type-C thuận lợi</h3>\r\n\r\n<p>iPad Air 2020 chuyển m&igrave;nh với cổng USB Type-C giống tr&ecirc;n c&aacute;c thiết bị iPad Pro với tốc độ truyền tải 5 Gbps. Vi&ecirc;n pin cũng được n&acirc;ng cấp v&agrave; c&oacute; khả năng lướt web l&ecirc;n 10 tiếng đem đến những trải nghiệm, l&agrave;m việc giải tr&iacute; li&ecirc;n tục.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/228808/ipad-air-4-wifi-64gb-2020-183820-053819.jpg\" onclick=\"return false;\"><img alt=\"Cổng sạc Type-C thuận lợi trong quá trình sử dụng | iPad Air 2020\" src=\"https://cdn.tgdd.vn/Products/Images/522/228808/ipad-air-4-wifi-64gb-2020-183820-053819.jpg\" /></a></p>\r\n\r\n<p>Nh&igrave;n chung c&oacute; thể thấy, iPad Air l&agrave; một phi&ecirc;n bản r&uacute;t gọn của iPad Pro với mức gi&aacute; phải chăng hơn. Trong đ&oacute;, tần số qu&eacute;t 120Hz, Face ID v&agrave; cụm camera LiDAR l&agrave; những thứ kh&ocirc;ng c&oacute; tr&ecirc;n iPad Air. Nếu bạn kh&ocirc;ng cần những thiếu s&oacute;t ấy th&igrave; iPad Air 2020 l&agrave; sự lựa chọn rất hợp l&yacute;.</p>', 2, '2021-04-25 21:54:49', '2021-04-25 21:58:02'),
(25, 'Máy tính bảng iPad 8 Wifi Cellular 128GB (2020)', 'ipad-gen-8-cellular-bac-new-600x600-600x600.jpg', 15990000, 20, '12 tháng', 'Cáp - Sạc -Tai nghe', '100000', 'Nguyên Seal - Chính Hãng', 1, 0, '<h2>Đặc điểm nổi bật của iPad 8 Wifi Cellular 128GB (2020)</h2>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/522/228903/Slider/vi-vn-ipad-8-wifi-cellular-128gb-2020-thumbvideo-1.jpg\" /></p>\r\n\r\n<h3>Sau 1 năm kể từ khi&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang/ipad-10-2-inch-wifi-cellular-128gb-2019\" target=\"_blank\">iPad 7</a>&nbsp;được ra mắt,&nbsp;<a href=\"https://www.thegioididong.com/apple\" target=\"_blank\">Apple</a>&nbsp;tiếp tục tung ra thế hệ m&aacute;y t&iacute;nh bảng tiếp theo với&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang/ipad-8-wifi-cellular-128gb-2020\" target=\"_blank\">iPad&nbsp;Wifi&nbsp;8 Cellular 128 GB (2020)</a>. Mặc d&ugrave; kh&ocirc;ng c&oacute; qu&aacute; nhiều n&acirc;ng cấp nổi bật, nhưng iPad 8 vẫn ghi điểm với người d&ugrave;ng nhờ v&agrave;o hiệu năng mạnh mẽ đến từ vi xử l&yacute; A12 Bionic, iPadOS mượt m&agrave; tối ưu cho giải tr&iacute; v&agrave; c&ocirc;ng việc nhưng lại c&oacute; mức gi&aacute; v&ocirc; c&ugrave;ng hấp dẫn.</h3>\r\n\r\n<h3>Thiết kế cũ nhưng kh&ocirc;ng lạc hậu</h3>\r\n\r\n<p>Trong khi&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-apple-ipad-pro\" target=\"_blank\">iPad Pro</a>&nbsp;v&agrave;&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-apple-ipad-air\" target=\"_blank\">iPad Air</a>&nbsp;2020&nbsp;đều đ&atilde; c&oacute; thiết kế mới với những đường v&aacute;t thẳng vu&ocirc;ng vức, th&igrave; iPad 8 vẫn trung th&agrave;nh với kiểu thiết kế truyền thống được duy tr&igrave; từ mẫu iPad 5 ra mắt c&aacute;ch đ&acirc;y 5 năm.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/228903/ipad-8-wifi-cellular-128gb-2020-021320-121337.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng Apple iPad Wifi Cellular 128 GB (2020)\" src=\"https://cdn.tgdd.vn/Products/Images/522/228903/ipad-8-wifi-cellular-128gb-2020-021320-121337.jpg\" /></a></p>\r\n\r\n<p>iPad thế hệ thứ 8 được ho&agrave;n thiện nguy&ecirc;n khối với chất liệu nh&ocirc;m t&aacute;i chế, th&acirc;n thiện với m&ocirc;i trường. Bề mặt nhẵn mịn, m&aacute;t lạnh của chất liệu kim loại mang đến sự cầm nắm th&iacute;ch th&uacute; v&agrave; tăng th&ecirc;m sự sang trọng cho thiết bị.</p>\r\n\r\n<p>Ở mặt trước, m&aacute;y vẫn c&oacute; 4 cạnh viền kh&aacute; d&agrave;y bao xung quanh, đặc biệt l&agrave; cạnh tr&ecirc;n v&agrave; cạnh dưới, do chứa n&uacute;t home v&agrave; camera trước. Tuy nhi&ecirc;n, ch&iacute;nh thiết kế n&agrave;y lại gi&uacute;p bạn thoải m&aacute;i khi để tay khi cầm v&agrave; sử dụng, hạn chế chạm v&agrave;o phần cảm ứng b&ecirc;n trong.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/228903/ipad-8-wifi-cellular-128gb-2020-021320-121315.jpg\" onclick=\"return false;\"><img alt=\"iPad 8 Wifi Cellular 128 GB | Thiết kế nhôm nguyên khối, dễ dàng cầm nắm\" src=\"https://cdn.tgdd.vn/Products/Images/522/228903/ipad-8-wifi-cellular-128gb-2020-021320-121315.jpg\" /></a></p>\r\n\r\n<p>N&uacute;t home mang t&iacute;nh &ldquo;biểu tượng&rdquo; của những thiết bị nh&agrave; T&aacute;o cắn dở vẫn xuất hiện tr&ecirc;n iPad 8, t&iacute;ch hợp cảm biến v&acirc;n tay Touch ID gi&uacute;p bạn bảo mật khi thanh to&aacute;n, mở kho&aacute;, mua ứng dụng tr&ecirc;n App Store.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/228903/ipad-8-wifi-cellular-128gb-2020-021220-121231.jpg\" onclick=\"return false;\"><img alt=\"iPad 8 Wifi Cellular 128 GB | Nút home tích hợp cảm biến vân tay Touch ID\" src=\"https://cdn.tgdd.vn/Products/Images/522/228903/ipad-8-wifi-cellular-128gb-2020-021220-121231.jpg\" /></a></p>\r\n\r\n<p>iPad 8 vẫn l&agrave; một trong số &iacute;t thiết bị c&ograve;n lại vẫn duy tr&igrave; jack cắm tai nghe 3.5 mm dễ d&agrave;ng kết nối với c&aacute;c loại tai nghe c&oacute; d&acirc;y th&ocirc;ng thường m&agrave; kh&ocirc;ng cần c&aacute;p chuyển đổi.</p>\r\n\r\n<h3>Apple A12 bước tiến lớn về hiệu năng</h3>\r\n\r\n<p>So với vi xử A10 Fusion tr&ecirc;n iPad 7, th&igrave; iPad 8 l&agrave; bước &ldquo;nhảy c&oacute;c&rdquo; về hiệu năng khi bỏ qua vi xử l&yacute; A11 v&agrave; trang bị lu&ocirc;n cả A12 Bionic mạnh ngang ngửa&nbsp;<a href=\"https://www.thegioididong.com/dtdd/iphone-xs-max\" target=\"_blank\">iPhone XS Max</a>&nbsp;với sức mạnh vượt trội.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/228903/ipad-8-wifi-cellular-128gb-2020-021220-121240.jpg\" onclick=\"return false;\"><img alt=\"iPad 8 Wifi Cellular 128 GB | Vi xử lí A12 Bionic, tốc độ cao lên đến 2.5 GHz\" src=\"https://cdn.tgdd.vn/Products/Images/522/228903/ipad-8-wifi-cellular-128gb-2020-021220-121240.jpg\" /></a></p>\r\n\r\n<p>A12 Bionic l&agrave; con chip hoạt động tiến tr&igrave;nh 7 nm ti&ecirc;n tiến với 6 nh&acirc;n xử l&yacute; bao gồm 2 nh&acirc;n tốc độ cao l&ecirc;n đến 2.5 GHz v&agrave; 4 nh&acirc;n tiết kiệm điện 1.6 GHz gi&uacute;p cho mọi t&aacute;c vụ thực hiện tr&ecirc;n iPad diễn ra nhanh ch&oacute;ng.</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-chip-apple-a12-bionic-con-chip-7nm-dau-ti-1171937\" target=\"_blank\">T&igrave;m hiểu chip Apple A12 Bionic - con chip 7nm đầu ti&ecirc;n tr&ecirc;n thế giới</a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/228903/ipad-8-wifi-cellular-128gb-2020-021320-121304.jpg\" onclick=\"return false;\"><img alt=\"iPad 8 Wifi Cellular 128 GB | Chơi game có đồ họa nặng, chỉnh sửa ảnh, xuất file video 4K nhanh chóng\" src=\"https://cdn.tgdd.vn/Products/Images/522/228903/ipad-8-wifi-cellular-128gb-2020-021320-121304.jpg\" /></a></p>\r\n\r\n<p>Với GPU được n&acirc;ng cấp 4 nh&acirc;n được Apple ph&aacute;t triển, iPad 8 c&oacute; thể chơi cực mượt c&aacute;c tựa game c&oacute; đồ họa nặng, chỉnh sửa ảnh, xuất file video 4K nhanh ch&oacute;ng, kh&ocirc;ng phải chờ đợi l&acirc;u.</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, bộ nhớ RAM tr&ecirc;n iPad 8 2020 cũng n&acirc;ng cấp l&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang?g=3-gb\" target=\"_blank\">3 GB</a>&nbsp;hỗ trợ tốt hơn c&aacute;c t&iacute;nh năng đa nhiệm như chia đ&ocirc;i m&agrave;n h&igrave;nh chạy 2 ứng dụng song song tr&ecirc;n iPad OS. B&ecirc;n cạnh đ&oacute;,&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-rom-128-den-256gb\" target=\"_blank\">bộ nhớ trong 128 GB</a>&nbsp;mở ra nhiều kh&ocirc;ng gian lưu trữ đ&aacute;p ứng nhu cầu giải tr&iacute;, c&ocirc;ng việc của bạn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/228903/ipad-8-wifi-cellular-128gb-2020-021420-121414.jpg\" onclick=\"return false;\"><img alt=\"iPad 8 Wifi Cellular 128 GB | Bộ nhớ 3 GB/128 GB\" src=\"https://cdn.tgdd.vn/Products/Images/522/228903/ipad-8-wifi-cellular-128gb-2020-021420-121414.jpg\" /></a></p>\r\n\r\n<p>Ngo&agrave;i kết nối mạng wifi, phi&ecirc;n bản Cellular c&ograve;n t&iacute;ch hợp 1 khay cắm sim hoặc e sim truy cập tốc độ 4G (kh&ocirc;ng hỗ trợ nghe gọi), dễ d&agrave;ng truy cập internet tra cứu th&ocirc;ng tin, gửi file, kiểm tra email mọi l&uacute;c mọi nơi hoặc l&agrave;m trạm ph&aacute;t wifi cho những thiết bị kh&aacute;c sử dụng v&ocirc; c&ugrave;ng tiện lợi.</p>\r\n\r\n<h3>Hỗ trợ sạc nhanh hơn</h3>\r\n\r\n<p>Thời lượng pin tr&ecirc;n iPad rất ấn tượng, c&oacute; thể n&oacute;i l&agrave; tốt nhất trong những d&ograve;ng m&aacute;y t&iacute;nh bảng hiện nay khi trang bị vi&ecirc;n pin &ldquo;si&ecirc;u tr&acirc;u khổng lồ&rdquo; l&ecirc;n đến 32,4 Wh. Theo Apple, vi&ecirc;n pin n&agrave;y gi&uacute;p cho iPad 8 c&oacute; khả năng lướt web bằng wifi 10 tiếng li&ecirc;n tục, đ&aacute;p ứng nhu cầu sử dụng trong suốt cả ng&agrave;y.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/228903/ipad-8-wifi-cellular-128gb-2020-021220-121210.jpg\" onclick=\"return false;\"><img alt=\"iPad 8 Wifi Cellular 128 GB | Trang bị cổng sạc Type C, cục sạc nhanh 20 W và cáp Lighting to USB C\" src=\"https://cdn.tgdd.vn/Products/Images/522/228903/ipad-8-wifi-cellular-128gb-2020-021220-121210.jpg\" /></a></p>\r\n\r\n<p>Kh&ocirc;ng những vậy, Apple c&ograve;n h&agrave;o ph&oacute;ng trang bị k&egrave;m trong hộp iPad 8 cục sạc nhanh 20 W v&agrave; cổng sạc&nbsp;Lighting gi&uacute;p bạn sạc nhanh hơn.</p>\r\n\r\n<h3>M&agrave;n h&igrave;nh &ldquo;V&otilde;ng mạc&rdquo; sắc n&eacute;t, m&agrave;u sắc ấn tượng</h3>\r\n\r\n<p>Nhắc đến iPad l&agrave; kh&ocirc;ng thể bỏ qua m&agrave;n h&igrave;nh Retina, một trong những chuẩn m&agrave;n h&igrave;nh tốt nhất hiện nay. M&agrave;n h&igrave;nh Retina với ưu điểm m&agrave;u sắc ch&iacute;nh x&aacute;c, trung thực, tối đa h&oacute;a nhu cầu chỉnh sửa ảnh chuy&ecirc;n s&acirc;u hay xem phim chất lượng cao.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/228903/ipad-8-wifi-cellular-128gb-2020-021420-121431.jpg\" onclick=\"return false;\"><img alt=\"iPad 8 Wifi Cellular 128 GB | Màn hình “Võng mạc” sắc nét, màu sắc ấn tượng\" src=\"https://cdn.tgdd.vn/Products/Images/522/228903/ipad-8-wifi-cellular-128gb-2020-021420-121431.jpg\" /></a></p>\r\n\r\n<p>Tuy nhi&ecirc;n, do &aacute;p dụng c&ocirc;ng nghệ cũ m&agrave;n h&igrave;nh được ẩn kh&aacute; s&acirc;u sau lớp k&iacute;nh d&agrave;y, n&ecirc;n chất lượng hiển thị iPad 8 c&oacute; phần &ldquo;k&eacute;m sắc&rdquo; hơn iPad Pro hay iPad Air, nhưng so với mặt bằng chung, với mức gi&aacute; n&agrave;y, hiển thị tr&ecirc;n iPad vẫn l&agrave; rất tốt, kh&ocirc;ng phải b&agrave;n c&atilde;i.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/228903/ipad-8-wifi-cellular-128gb-2020-021220-121255.jpg\" onclick=\"return false;\"><img alt=\"iPad 8 Wifi Cellular 128 GB | Trang bị hệ thống 2 loa ngoài stereo âm thanh nổi\" src=\"https://cdn.tgdd.vn/Products/Images/522/228903/ipad-8-wifi-cellular-128gb-2020-021220-121255.jpg\" /></a></p>\r\n\r\n<p>Kh&ocirc;ng chỉ về mặt hiển thị, iPad c&ograve;n được trang bị hệ thống 2 loa ngo&agrave;i stereo &acirc;m thanh nổi, chất lượng &acirc;m thanh tr&ecirc;n iPad rất tốt trong tầm gi&aacute;, kh&oacute; c&oacute; thiết bị n&agrave;o c&oacute; thể vượt qua. Những b&agrave;i h&aacute;t hay những bộ phim bom tấn sẽ c&agrave;ng hấp dẫn hơn với chất &acirc;m cực sống động từ iPad,</p>\r\n\r\n<h3>Camera đơn trước sau đủ cho nhu cầu cơ bản</h3>\r\n\r\n<p>Chụp h&igrave;nh kh&ocirc;ng phải l&agrave; t&iacute;nh năng được tập trung tr&ecirc;n iPad 8, khi m&aacute;y vẫn duy tr&igrave; cảm biến 8 MP tương tự như iPad 7 2019. Chất lượng ảnh chụp tr&ecirc;n iPad 8 chi tiết tốt, đ&aacute;p ứng nhu cầu chụp ảnh cơ bản, lưu trữ nhanh c&aacute;c th&ocirc;ng tin.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/228903/ipad-8-wifi-cellular-128gb-2020-021220-121220.jpg\" onclick=\"return false;\"><img alt=\"iPad 8 Wifi Cellular 128 GB | Camera sau 8 MP đáp ứng nhu cầu cơ bản\" src=\"https://cdn.tgdd.vn/Products/Images/522/228903/ipad-8-wifi-cellular-128gb-2020-021220-121220.jpg\" /></a></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, camera của m&aacute;y cũng c&oacute; thể quay phim Full HD với 30 khung h&igrave;nh/gi&acirc;y. Ở mặt trước, m&aacute;y cũng c&oacute; 1 camera 1.2 MP hỗ trợ gọi điện Facetime video call c&ugrave;ng bạn b&egrave; với chất lượng HD</p>\r\n\r\n<h3>iPadOS v&agrave; hệ sinh th&aacute;i Apple cho trải nghiệm tuyệt vời</h3>\r\n\r\n<p>iPadOS l&agrave; hệ điều h&agrave;nh d&agrave;nh ri&ecirc;ng cho iPad ra mắt v&agrave;o năm 2019 gi&uacute;p cho iPad kh&ocirc;ng c&ograve;n l&agrave; 1 chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd-apple-iphone\" target=\"_blank\">iPhone</a>&nbsp;ph&oacute;ng to, m&agrave; dần được tối ưu cho những t&aacute;c vụ tr&ecirc;n m&agrave;n h&igrave;nh lớn, xử l&yacute; nhiều c&ocirc;ng việc hơn.&nbsp;</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://xn--tt%20c%20khi%20nim%20mi%20v%20ipados-46c29500acca00ezg94a/\" target=\"_blank\">Tất cả kh&aacute;i niệm mới về iPadOS</a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/228903/ipad-8-wifi-cellular-128gb-2020-021320-121356.jpg\" onclick=\"return false;\"><img alt=\"iPad 8 Wifi Cellular 128 GB | Hệ điều hành iPadOS mang đến trải nghiệm tuyệt vời\" src=\"https://cdn.tgdd.vn/Products/Images/522/228903/ipad-8-wifi-cellular-128gb-2020-021320-121356.jpg\" /></a></p>\r\n\r\n<p>iPadOS mang đến một loạt t&iacute;nh năng đặc trưng chỉ c&oacute; iPad mới c&oacute; như: t&iacute;nh năng chia đ&ocirc;i m&agrave;n h&igrave;nh chạy song song 2 ứng dụng, k&eacute;o thả file chia sẻ nhanh ch&oacute;ng với c&aacute;c thao t&aacute;c đơn giản như chạm, vuốt, chụm v&agrave; thu ph&oacute;ng, chỉ mất v&agrave;i ph&uacute;t l&agrave;m quen l&agrave; người d&ugrave;ng đ&atilde; c&oacute; thể l&agrave;m chủ chiếc iPad với nhiều t&iacute;nh năng.</p>\r\n\r\n<p>Kh&ocirc;ng chỉ thế, kho ứng dụng App Store khổng lồ với h&agrave;ng triệu ứng dụng đa dạng phong ph&uacute; được chia sẻ c&ugrave;ng hệ sinh th&aacute;i Apple, mỗi ứng dụng đều được chăm ch&uacute;t thiết kế giao diện UX UI tối ưu cho m&agrave;n h&igrave;nh lớn tr&ecirc;n iPad.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/522/228903/ipad-8-wifi-cellular-128gb-2020-021120-121149.jpg\" onclick=\"return false;\"><img alt=\"iPad 8 Wifi Cellular 128 GB | Hoạt động vô cùng mượt mà với bút Apple Pencil\" src=\"https://cdn.tgdd.vn/Products/Images/522/228903/ipad-8-wifi-cellular-128gb-2020-021120-121149.jpg\" /></a></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, iPad 8 c&ograve;n c&oacute; thể hoạt động v&ocirc; c&ugrave;ng mượt m&agrave; với b&uacute;t&nbsp;<a href=\"https://www.thegioididong.com/phu-kien-thong-minh/but-cam-ung-apple-pencil-gen-2\" target=\"_blank\">Apple Pencil</a>&nbsp;gi&uacute;p nhận diện chữ viết tay v&agrave; chuyển th&agrave;nh văn bản v&ocirc; c&ugrave;ng nhanh v&agrave; ch&iacute;nh x&aacute;c, hay&nbsp;<a href=\"https://www.thegioididong.com/phu-kien-thong-minh/ban-phim-smart-keyboard-ipad-10-2-mx3l2za-a\" target=\"_blank\">b&agrave;n ph&iacute;m Smart Keyboard</a>&nbsp;gi&uacute;p bạn trong c&aacute;c c&ocirc;ng việc văn ph&ograve;ng như soạn thảo văn bản, xử l&yacute; c&aacute;c bảng t&iacute;nh chuy&ecirc;n nghiệp như tr&ecirc;n m&aacute;y t&iacute;nh c&aacute; nh&acirc;n.</p>\r\n\r\n<p>T&oacute;m lại, nếu x&eacute;t về cấu h&igrave;nh c&oacute; iPad Pro hiệu năng mạnh mẽ, về thiết kế c&oacute; iPad Air mỏng nhẹ. Th&igrave; iPad 8 (2020) l&agrave; một chiếc m&aacute;y t&iacute;nh bảng c&acirc;n bằng giữa hiệu năng v&agrave; gi&aacute; b&aacute;n, Với những n&acirc;ng cấp về hiệu năng c&ugrave;ng iPadOS mượt m&agrave; hỗ trợ l&acirc;u d&agrave;i, iPad 8 xứng đ&aacute;ng l&agrave; một khoảng đ&aacute;ng n&acirc;ng cấp đ&aacute;ng đầu tư cho c&ocirc;ng việc v&agrave; giải tr&iacute; cho tất cả mọi người mọi đối tượng.</p>', 2, '2021-04-25 21:55:45', '2021-04-25 21:58:00');
INSERT INTO `products` (`id`, `name`, `images`, `price`, `quantity`, `warranty`, `accessories`, `discount`, `status`, `active`, `hot`, `desc`, `categories_id`, `created_at`, `updated_at`) VALUES
(26, 'Tai nghe Bluetooth AirPods Pro Wireless Charge Apple MWP22', 'airpods-pro-wireless-charge-apple-mwp22-ava-600x600.jpg', 6490000, 10, '12 tháng', 'Cáp - Sạc -Tai nghe', '1000000', 'Nguyên Seal - Chính Hãng', 1, 1, '<h3>Thiết kế nhỏ gọn, dễ d&agrave;ng đem theo bất cứ đ&acirc;u</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/tai-nghe/tai-nghe-bluetooth-airpods-pro-apple-mwp22\" target=\"_blank\">AirPods Pro</a>&nbsp;với thiết kế gọn g&agrave;ng, đẹp v&agrave; tinh tế,&nbsp;<a href=\"https://www.thegioididong.com/tai-nghe\" target=\"_blank\">tai nghe</a>&nbsp;được thiết kế theo dạng&nbsp;In-ear thay v&igrave; Earbuds như&nbsp;<a href=\"https://www.thegioididong.com/tai-nghe/tai-nghe-bluetooth-airpods-2-wireless-charge-mrxj2\" target=\"_blank\">AirPods 2</a>, cho ph&eacute;p&nbsp;<strong>chống ồn tốt hơn, kh&oacute; rớt khi đeo v&agrave; &ecirc;m tai hơn</strong>.</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tai-nghe-earbuds-tai-nghe-in-ear-la-gi-khac-nhau-1219749\" target=\"_blank\">Tai nghe Earbuds, tai nghe Inear l&agrave; g&igrave;? Kh&aacute;c nhau như thế n&agrave;o?</a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/236026/tai-nghe-bluetooth-airpods-pro-apple-mwp22.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế nhỏ gọn, tinh tế - Tai nghe Bluetooth Airpods Pro Apple\" src=\"https://cdn.tgdd.vn/Products/Images/54/236026/tai-nghe-bluetooth-airpods-pro-apple-mwp22.jpg\" /></a></p>\r\n\r\n<h3>Kết nối nhanh v&agrave; ổn định với c&ocirc;ng nghệ Bluetooth 5.0</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/tai-nghe-bluetooth\" target=\"_blank\">Tai nghe Bluetooth</a>&nbsp;AirPods Pro vẫn cho&nbsp;tốc độ kết nối ấn tượng, chỉ&nbsp;<strong>khoảng&nbsp;1 - 2 gi&acirc;y</strong>&nbsp;sau khi mở nắp (với c&aacute;c thiết bị của Apple như iPhone, iPad, MacBook,...) v&agrave;&nbsp;<strong>khoảng 3 - 4 gi&acirc;y</strong>&nbsp;đối với Android (sau lần kết nối đầu ti&ecirc;n).</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/236026/tai-nghe-bluetooth-airpods-pro-apple-mwp22-trang2.jpg\" onclick=\"return false;\"><img alt=\"Kết nối nhanh chóng - Tai nghe Bluetooth Airpods Pro Apple\" src=\"https://cdn.tgdd.vn/Products/Images/54/236026/tai-nghe-bluetooth-airpods-pro-apple-mwp22-trang2.jpg\" /></a></p>\r\n\r\n<h3>Linh hoạt lựa chọn với 3 k&iacute;ch cỡ đệm tai k&egrave;m theo</h3>\r\n\r\n<p>Do chuyển sang dạng In-ear, n&ecirc;n AirPods Pro được&nbsp;<strong>k&egrave;m theo đệm tai cao su với 3 k&iacute;ch cỡ kh&aacute;c nhau</strong>, gi&uacute;p người d&ugrave;ng c&oacute; nhiều lựa chọn ph&ugrave; hợp với cỡ tai của m&igrave;nh, đem lại trải nghiệm thoải m&aacute;i nhất.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/236026/tai-nghe-bluetooth-airpods-pro-apple-mwp22-trang3.jpg\" onclick=\"return false;\"><img alt=\"Đi kèm theo 3 cặp đệm tai - Tai nghe Bluetooth Airpods Pro Apple\" src=\"https://cdn.tgdd.vn/Products/Images/54/236026/tai-nghe-bluetooth-airpods-pro-apple-mwp22-trang3.jpg\" /></a></p>\r\n\r\n<h3>&Acirc;m thanh chất lượng, sống động c&ugrave;ng c&ocirc;ng nghệ Adaptive EQ tự&nbsp;điều chỉnh &acirc;m thanh th&ocirc;ng minh</h3>\r\n\r\n<p>AirPods Pro được n&acirc;ng cấp mạnh mẽ ở chất lượng &acirc;m thanh, cho &acirc;m bass mạnh mẽ hơn, ch&acirc;n thực hơn, độ chi tiết cao hơn, c&ugrave;ng c&ocirc;ng nghệ Adaptive EQ, cho ph&eacute;p<strong>&nbsp;tự động điều chỉnh tần số &acirc;m thanh</strong>&nbsp;của bản nhạc theo h&igrave;nh dạng tai người d&ugrave;ng, mang lại trải nghiệm &acirc;m thanh tốt nhất.</p>\r\n\r\n<p>C&oacute; chip H1 chất lượng cao, xử l&yacute; &acirc;m thanh kỹ thuật số,&nbsp;<strong>tốc độ kết nối nhanh</strong>, cho độ trễ gần như bằng kh&ocirc;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/236026/tai-nghe-bluetooth-airpods-pro-apple-mwp22-trangfixx4.jpg\" onclick=\"return false;\"><img alt=\"Công nghệ Adaptive EQ tùy chỉnh âm tùy theo người dùng - Tai nghe Bluetooth Airpods Pro Apple\" src=\"https://cdn.tgdd.vn/Products/Images/54/236026/tai-nghe-bluetooth-airpods-pro-apple-mwp22-trangfixx4.jpg\" /></a></p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/tai-nghe-khong-day\" target=\"_blank\">Tai nghe True Wireless</a>&nbsp;AirPods Pro trang bị 2 chế độ: Transparency Mode (xuy&ecirc;n &acirc;m) v&agrave; Noise Cancellation Mode (chống ồn chủ động)</h3>\r\n\r\n<p><strong>- Chống ồn chủ động:</strong>&nbsp;Với chế độ n&agrave;y, tai nghe sẽ khử tiếng ồn ngo&agrave;i m&ocirc;i trường, loại bỏ tạp &acirc;m xung quanh lọt v&agrave;o tai, gi&uacute;p bạn đắm ch&igrave;m v&agrave;o bản nhạc m&agrave; kh&ocirc;ng bị ảnh hưởng bởi tiếng ồn xung quanh.</p>\r\n\r\n<p><strong>- Chế độ xuy&ecirc;n &acirc;m:&nbsp;</strong>Khi chuyển sang chế độ n&agrave;y, AirPods Pro cho ph&eacute;p bạn nghe được cả nhạc đang ph&aacute;t v&agrave; &acirc;m thanh b&ecirc;n ngo&agrave;i, gi&uacute;p bạn dễ d&agrave;ng hơn khi tr&ograve; chuyện với mọi người d&ugrave; đang đeo tai nghe.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/236026/tai-nghe-bluetooth-airpods-pro-apple-mwp22-6.jpg\" onclick=\"return false;\"><img alt=\"Trang bị chống ồn và chế độ xuyên âm - Tai nghe Bluetooth Airpods Pro Apple\" src=\"https://cdn.tgdd.vn/Products/Images/54/236026/tai-nghe-bluetooth-airpods-pro-apple-mwp22-6.jpg\" /></a></p>\r\n\r\n<h3>Điều khiển dễ d&agrave;ng, nhanh ch&oacute;ng với cảm ứng lực hiện đại</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/tai-nghe-apple-bluetooth\" target=\"_blank\">Tai nghe Bluetooth Apple</a>&nbsp;AirPods Pro&nbsp;sử dụng cảm ứng lực để điều khiển,&nbsp;kh&ocirc;ng cần thao t&aacute;c nhiều tr&ecirc;n điện thoại, cụ thể như sau:</p>\r\n\r\n<p><strong>- B&oacute;p 1 lần:</strong>&nbsp;Ph&aacute;t/tạm dừng nhạc hoặc trả lời cuộc gọi.</p>\r\n\r\n<p><strong>- B&oacute;p 2 lần:</strong>&nbsp;Chuyển b&agrave;i kế tiếp.</p>\r\n\r\n<p><strong>- B&oacute;p 3 lần:</strong>&nbsp;Quay lại b&agrave;i trước.</p>\r\n\r\n<p><strong>- Nhấn giữ:</strong>&nbsp;Chuyển đổi giữa chế độ chống ồn chủ động v&agrave; chế độ xuy&ecirc;n &acirc;m.</p>\r\n\r\n<p>Ngo&agrave;i ra, AirPods Pro c&ograve;n hỗ trợ kết nối nhanh với trợ l&yacute; ảo bằng c&aacute;ch n&oacute;i&nbsp;<strong>&quot;Hey Siri&quot;</strong>, sau đ&oacute; bạn c&oacute; thể ra lệnh ph&aacute;t b&agrave;i h&aacute;t, thực hiện cuộc gọi, nhờ dẫn đường v&agrave; c&aacute;c y&ecirc;u cầu kh&aacute;c (chỉ hỗ trợ giọng n&oacute;i Tiếng Anh).</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/236026/tai-nghe-bluetooth-airpods-pro-apple-mwp22-7.jpg\" onclick=\"return false;\"><img alt=\"Cảm ứng lực dễ dàng thao tác, hiện đại - Tai nghe Bluetooth Airpods Pro Apple\" src=\"https://cdn.tgdd.vn/Products/Images/54/236026/tai-nghe-bluetooth-airpods-pro-apple-mwp22-7.jpg\" /></a></p>\r\n\r\n<h3>T&iacute;ch hợp sạc kh&ocirc;ng d&acirc;y chuẩn Qi cho hộp sạc</h3>\r\n\r\n<p>Hộp sạc AirPods Pro hỗ trợ<a href=\"https://www.thegioididong.com/cap-dien-thoai-sac-khong-day\" target=\"_blank\">&nbsp;sạc kh&ocirc;ng d&acirc;y</a>&nbsp;theo chuẩn Qi, tương th&iacute;ch hầu hết c&aacute;c loại đế sạc kh&ocirc;ng d&acirc;y hiện nay.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/236026/tai-nghe-bluetooth-airpods-pro-apple-mwp22-trang7.jpg\" onclick=\"return false;\"><img alt=\"Hỗ trợ sạc không dây chuẩn Qi - Tai nghe Bluetooth Airpods Pro Apple\" src=\"https://cdn.tgdd.vn/Products/Images/54/236026/tai-nghe-bluetooth-airpods-pro-apple-mwp22-trang7.jpg\" /></a></p>\r\n\r\n<p>Ngo&agrave;i ra, bạn c&oacute; thể sạc cho hộp sạc qua cổng&nbsp;<a href=\"https://www.thegioididong.com/cap-dien-thoai-cap-iphone-lightning\" target=\"_blank\">Lightning</a>, đi k&egrave;m theo bộ sản phẩm l&agrave;&nbsp;<a href=\"https://www.thegioididong.com/cap-dien-thoai-type-c-lightning\" target=\"_blank\">c&aacute;p USB-C To Lightning</a>&nbsp;giống với c&aacute;p sạc đi k&egrave;m với&nbsp;iPhone 11 Pro/11 Pro Max.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/236026/tai-nghe-bluetooth-airpods-pro-apple-mwp22-trang8.jpg\" onclick=\"return false;\"><img alt=\"Hỗ trợ sạc qua cổng Lightning - Tai nghe Bluetooth Airpods Pro Apple\" src=\"https://cdn.tgdd.vn/Products/Images/54/236026/tai-nghe-bluetooth-airpods-pro-apple-mwp22-trang8.jpg\" /></a></p>\r\n\r\n<h3>Thời lượng pin cho ph&eacute;p nghe nhạc đến 4.5 giờ, đ&agrave;m thoại đến 3.5 giờ</h3>\r\n\r\n<p>Khi bật chức năng khử tiếng ồn chủ động, thời gian nghe nhạc đến 4.5 giờ v&agrave; thời gian đ&agrave;m thoại đến 3.5 giờ li&ecirc;n tục nếu bật chống ồn, c&ograve;n&nbsp;nếu tắt chống ồn, c&oacute; thể nghe nhạc đến 5 giờ v&agrave; đ&agrave;m thoại đến 4.5 giờ.</p>\r\n\r\n<p>Khi d&ugrave;ng tai nghe AirPods Pro với hộp sạc th&igrave; người d&ugrave;ng sẽ c&oacute; thời gian&nbsp;<strong>24 giờ nghe nhạc v&agrave; 18 giờ đ&agrave;m thoại</strong>&nbsp;li&ecirc;n tục khi được sạc đầy.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/236026/airpods-pro-wireless-charge-apple-mwp22-90.jpg\" onclick=\"return false;\"><img alt=\"Có thể nghe nhạc liên tục trong 4.5 giờ và 3.5 giờ đàm thoại - Tai nghe Bluetooth Airpods Pro Apple\" src=\"https://cdn.tgdd.vn/Products/Images/54/236026/airpods-pro-wireless-charge-apple-mwp22-90.jpg\" /></a></p>\r\n\r\n<h3>Tiết kiệm thời gian với khả năng sạc nhanh</h3>\r\n\r\n<p>Với t&iacute;nh năng&nbsp;<strong>sạc 5 ph&uacute;t được 1 giờ nghe nhạc</strong>, AirPods Pro gi&uacute;p cho bạn tiết kiệm thời gian v&agrave; an t&acirc;m hơn mỗi khi tai nghe hết pin.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/236026/tai-nghe-bluetooth-airpods-pro-apple-mwp22-trang10.jpg\" onclick=\"return false;\"><img alt=\"Hỗ trợ sạc nhanh, 5 phút cho 1 giờ nghe nhạc - Tai nghe Bluetooth Airpods Pro Apple\" src=\"https://cdn.tgdd.vn/Products/Images/54/236026/tai-nghe-bluetooth-airpods-pro-apple-mwp22-trang10.jpg\" /></a></p>\r\n\r\n<h3>Khả năng kh&aacute;ng nước v&agrave; mồ h&ocirc;i với chuẩn chống nước IPX4</h3>\r\n\r\n<p>AirPods Pro c&oacute; thể chống được mưa nhỏ v&agrave; mồ h&ocirc;i khi vận động, gi&uacute;p người d&ugrave;ng y&ecirc;n t&acirc;m v&agrave; thoải m&aacute;i hơn khi đeo để chơi thể thao hoặc c&oacute; v&ocirc; t&igrave;nh gặp mưa (kh&ocirc;ng n&ecirc;n đeo khi bơi hoặc ng&acirc;m nước).</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/236026/tai-nghe-bluetooth-airpods-pro-apple-mwp22-trang11-1.jpg\" onclick=\"return false;\"><img alt=\"Hỗ trợ chống nước và mồ hôi chuẩn IPX4 - Tai nghe Bluetooth Airpods Pro Apple\" src=\"https://cdn.tgdd.vn/Products/Images/54/236026/tai-nghe-bluetooth-airpods-pro-apple-mwp22-trang11-1.jpg\" /></a></p>\r\n\r\n<h3>Dễ d&agrave;ng theo d&otilde;i trạng th&aacute;i pin th&ocirc;ng qua đ&egrave;n LED</h3>\r\n\r\n<p>Đ&egrave;n LED được đặt ở vị tr&iacute; ngay mặt trước hộp sạc để người d&ugrave;ng tiện theo d&otilde;i trạng th&aacute;i pin, cụ thể đ&egrave;n xanh b&aacute;o hiệu pin đầy v&agrave; đ&egrave;n cam l&agrave; pin yếu.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/236026/tai-nghe-bluetooth-airpods-pro-apple-mwp22-8.jpg\" onclick=\"return false;\"><img alt=\"Trang bị đèn LED báo pin tiện lợi - Tai nghe Bluetooth Airpods Pro Apple\" src=\"https://cdn.tgdd.vn/Products/Images/54/236026/tai-nghe-bluetooth-airpods-pro-apple-mwp22-8.jpg\" /></a></p>\r\n\r\n<h3>Bộ sản phẩm gồm c&oacute;:</h3>\r\n\r\n<p>Hộp sạc, tai nghe (b&ecirc;n trong hộp sạc), d&acirc;y c&aacute;p USB-C To Lightning, 3 cặp đệm tai Silicon (2 cặp rời, 1 cặp đi k&egrave;m sẵn trong tai nghe), s&aacute;ch hướng dẫn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/236026/tai-nghe-bluetooth-airpods-pro-apple-mwp22-trang12.jpg\" onclick=\"return false;\"><img alt=\"Bộ sản phẩm bao gồm Tai nghe, Hộp sạc, USB-C to Lightning, đệm tai Silicon (3 cỡ), sách hướng dẫn\" src=\"https://cdn.tgdd.vn/Products/Images/54/236026/tai-nghe-bluetooth-airpods-pro-apple-mwp22-trang12.jpg\" /></a></p>\r\n\r\n<h3>Hướng dẫn kết nối iPhone/iPad với&nbsp;AirPods Pro:</h3>\r\n\r\n<p>- Bật Bluetooth tr&ecirc;n iPhone/iPad.</p>\r\n\r\n<p>- Mở nắp hộp&nbsp;AirPods Pro, giữ nguy&ecirc;n tai nghe trong hộp, l&uacute;c n&agrave;y tr&ecirc;n thiết bị sẽ xuất hiện 1 bảng pop-up c&oacute; h&igrave;nh tai nghe v&agrave; chữ Connect (kết nối).</p>\r\n\r\n<p>- Bấm Connect sau n&oacute; nhấn giữ n&uacute;t nhấn ph&iacute;a mặt sau hộp sạc, đợi v&agrave;i gi&acirc;y l&agrave; ho&agrave;n th&agrave;nh việc kết nối. Những lần sau chỉ cần mở nắp hộp, tai nghe sẽ tự động kết nối với thiết bị.</p>\r\n\r\n<p>Với MacOS (MacBook/iMac), iPhone c&aacute;ch kết nối cũng rất dễ d&agrave;ng v&agrave; tương th&iacute;ch tốt.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/236026/tai-nghe-bluetooth-airpods-pro-apple-mwp22-trang15.jpg\" onclick=\"return false;\"><img alt=\"Hướng dẫn kết nối Tai nghe Bluetooth Airpods Pro Apple Trắng\" src=\"https://cdn.tgdd.vn/Products/Images/54/236026/tai-nghe-bluetooth-airpods-pro-apple-mwp22-trang15.jpg\" /></a></p>\r\n\r\n<p>Nh&igrave;n chung, với thiết kế nhỏ gọn,<strong>&nbsp;</strong>linh hoạt v&agrave; hợp thời trang,&nbsp;tai nghe Apple&nbsp;AirPods Pro sẽ mang đến cho người d&ugrave;ng những trải nghiệm &acirc;m thanh tốt nhất, chế độ chống ồn chủ động v&agrave; chế độ xuy&ecirc;n &acirc;m hoạt động cực k&igrave; hiệu quả, mang lại trải nghiệm tuyệt vời chất lượng &acirc;m thanh ch&acirc;n thực.&nbsp;</p>', 5, '2021-04-25 22:15:20', '2021-04-25 22:48:02'),
(27, 'Tai nghe Bluetooth AirPods 2 Wireless charge Apple MRXJ2', 'airpods-2-wireless-charge-apple-mrxj2-ava-600x600.jpg', 4990000, 20, '12 tháng', 'Full Box', '500000', 'Nguyên Seal - Chính Hãng', 1, 0, '<h3>Thiết kế nhỏ gọn, ấn tượng người nh&igrave;n</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/tai-nghe\" target=\"_blank\">Tai nghe&nbsp;</a>sở hữu thiết kế thời trang v&agrave; nhỏ gọn, trọng lượng cũng rất nhẹ kh&ocirc;ng kh&aacute;c mấy so với&nbsp;<a href=\"https://www.thegioididong.com/tai-nghe-bluetooth\" target=\"_blank\">tai nghe Bluetooth</a>&nbsp;AirPods thế hệ đầu ti&ecirc;n. Tai nghe&nbsp;c&ograve;n được phủ l&ecirc;n một lớp chất liệu mới ở đầu mỗi tai nghe để gi&uacute;p tai nghe b&aacute;m tr&ecirc;n tai người đeo hơn, tr&aacute;nh bị rơi rớt hơn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/236025/tai-nghe-bluetooth-airpods-2-wireless-charge-mrxj2-1-2.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth AirPods 2 Wireless charge Apple MRXJ2 Trắng có thiết kế nhỏ gọn\" src=\"https://cdn.tgdd.vn/Products/Images/54/236025/tai-nghe-bluetooth-airpods-2-wireless-charge-mrxj2-1-2.jpg\" /></a></p>\r\n\r\n<h3>Truy cập nhanh v&agrave;o Siri bằng c&aacute;ch n&oacute;i &quot;Hey Siri&quot;</h3>\r\n\r\n<p>Với sự trở lại của&nbsp;<a href=\"https://www.thegioididong.com/tai-nghe/tai-nghe-bluetooth-airpods-2-wireless-charge-mrxj2\" target=\"_blank\">Tai nghe Bluetooth AirPods 2 Wireless charge</a>&nbsp;n&agrave;y th&igrave;&nbsp;khả năng chống ồn, xử l&yacute; &acirc;m thanh b&ecirc;n ngo&agrave;i tốt hơn so với tai nghe AirPods đời đầu v&agrave; bạn c&oacute; thể kết nối với&nbsp;nhanh v&agrave;o Siri bằng c&aacute;ch n&oacute;i&nbsp;&ldquo;Hey Siri&rdquo;&nbsp;v&agrave; thực hiện y&ecirc;u cầu của bạn. Ngo&agrave;i ra sản phẩm c&ograve;n c&oacute; thể điều chỉnh &acirc;m lượng, thay đổi b&agrave;i h&aacute;t, thực hiện cuộc gọi hoặc thậm ch&iacute; nhận chỉ đường tiện lợi.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/236025/tai-nghe-bluetooth-airpods-2-wireless-charge-mrxj2-8-1.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth AirPods 2 Wireless charge Apple MRXJ2 Trắng dễ dàng kết nối với Siri\" src=\"https://cdn.tgdd.vn/Products/Images/54/236025/tai-nghe-bluetooth-airpods-2-wireless-charge-mrxj2-8-1.jpg\" /></a></p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/tai-nghe-khong-day\" target=\"_blank\">Tai nghe kh&ocirc;ng d&acirc;y</a>&nbsp;Apple dễ d&agrave;ng thao t&aacute;c bằng cảm ứng để&nbsp;nhận cuộc gọi, nghe hoặc tạm dừng đoạn nhạc, tiện lợi khi kh&ocirc;ng cần thao t&aacute;c nhiều tr&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\">điện thoại</a></h3>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/images/54/236025/tai-nghe-bluetooth-airpods-2-wireless-charge-mrxj2-9-1.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth AirPods 2 Wireless charge Apple MRXJ2 Trắng thao tác dễ dàng\" src=\"https://cdn.tgdd.vn/Products/Images/54/236025/tai-nghe-bluetooth-airpods-2-wireless-charge-mrxj2-9-1.jpg\" /></a></h3>\r\n\r\n<h3>Sạc pin cho tai nghe dễ d&agrave;ng qua cổng sạc&nbsp;<a href=\"https://www.thegioididong.com/cap-dien-thoai-cap-iphone-lightning\" target=\"_blank\">Lightning</a>&nbsp;hoặc c&oacute; thể sạc kh&ocirc;ng d&acirc;y nhờ được trang bị chuẩn sạc Qi hiện đại</h3>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/images/54/236025/tai-nghe-bluetooth-airpods-2-wireless-charge-mrxj2-7-1.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth AirPods 2 Wireless charge Apple MRXJ2 Trắng được sạc pin thông qua cổng Lightning\" src=\"https://cdn.tgdd.vn/Products/Images/54/236025/tai-nghe-bluetooth-airpods-2-wireless-charge-mrxj2-7-1.jpg\" /></a><a href=\"https://www.thegioididong.com/images/54/236025/tai-nghe-bluetooth-airpods-2-wireless-charge-mrxj2-6-1.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth AirPods 2 Wireless charge Apple MRXJ2 Trắng có thể sạc không dây\" src=\"https://cdn.tgdd.vn/Products/Images/54/236025/tai-nghe-bluetooth-airpods-2-wireless-charge-mrxj2-6-1.jpg\" /></a></h3>\r\n\r\n<h3>Bạn c&oacute; thể d&ugrave;ng&nbsp;<a href=\"https://www.thegioididong.com/tai-nghe-nhet-trong\" target=\"_blank\">tai nghe nh&eacute;t trong</a>&nbsp;để nghe nhạc l&ecirc;n đến 5 giờ với &acirc;m lượng l&agrave; 50% cho mỗi một lần sạc đầy hoặc sử dụng song song với hộp sạc c&oacute; thể d&ugrave;ng được l&ecirc;n đến 24 giờ.</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/236025/tai-nghe-bluetooth-airpods-2-wireless-charge-mrxj2-3-2.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth AirPods 2 Wireless charge Apple MRXJ2 Trắng có thời lượng nghe nhạc lớn\" src=\"https://cdn.tgdd.vn/Products/Images/54/236025/tai-nghe-bluetooth-airpods-2-wireless-charge-mrxj2-3-2.jpg\" /></a><a href=\"https://www.thegioididong.com/images/54/236025/tai-nghe-bluetooth-airpods-2-wireless-charge-mrxj2-5-1.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth AirPods 2 Wireless charge Apple MRXJ2 Trắng dùng được 24 giờ\" src=\"https://cdn.tgdd.vn/Products/Images/54/236025/tai-nghe-bluetooth-airpods-2-wireless-charge-mrxj2-5-1.jpg\" /></a></p>\r\n\r\n<h3>Khi hết pin m&agrave; kh&ocirc;ng c&oacute; thời gian, bạn chỉ cần sạc&nbsp;<a href=\"https://www.thegioididong.com/tai-nghe-apple-bluetooth\" target=\"_blank\">tai nghe Bluetooth Apple</a>&nbsp;với&nbsp;15 ph&uacute;t v&agrave; đ&atilde; c&oacute; thể tận hưởng &acirc;m nhạc th&ecirc;m 3 giờ sau đ&oacute;</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/236025/tai-nghe-bluetooth-airpods-2-wireless-charge-mrxj2-4-2.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth AirPods 2 Wireless charge Apple MRXJ2 Trắng sạc nhanh khi đang bận\" src=\"https://cdn.tgdd.vn/Products/Images/54/236025/tai-nghe-bluetooth-airpods-2-wireless-charge-mrxj2-4-2.jpg\" /></a></p>\r\n\r\n<h3>C&oacute; đ&egrave;n Led b&aacute;o sạc khi sạc pin</h3>\r\n\r\n<p>Với vỏ sạc kh&ocirc;ng d&acirc;y bạn sẽ sạc sản phẩm một c&aacute;ch nhanh ch&oacute;ng v&agrave; dễ d&agrave;ng. Chỉ cần đặt vỏ m&aacute;y xuống tr&ecirc;n một tấm sạc tương th&iacute;ch Qi v&agrave; để n&oacute; sạc. Đ&egrave;n b&aacute;o Led ở mặt trước của vỏ cho ph&eacute;p bạn biết rằng&nbsp;<a href=\"https://www.thegioididong.com/tai-nghe-khong-da\" target=\"_blank\">tai nghe True Wireless</a>&nbsp;AirPods của bạn đang sạc. V&agrave; khi bạn rời khỏi thảm sạc, bạn c&oacute; thể sử dụng cổng Lightning để sạc.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/236025/tai-nghe-bluetooth-airpods-2-wireless-charge-mrxj2-10-1.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth AirPods 2 Wireless charge Apple MRXJ2 Trắng có đèn Led tiện lợi\" src=\"https://cdn.tgdd.vn/Products/Images/54/236025/tai-nghe-bluetooth-airpods-2-wireless-charge-mrxj2-10-1.jpg\" /></a></p>\r\n\r\n<p>Chip H1 ho&agrave;n to&agrave;n mới cho tốc độ kết nối tức th&igrave;, chuyển đổi giữa c&aacute;c thiết bị nhanh ch&oacute;ng. D&ugrave; bạn sử dụng đeo cả hai tai nghe hay chỉ một tai nghe th&igrave; chip H1 sẽ tự động định tuyến &acirc;m thanh v&agrave; kết nối micro gi&uacute;p bạn.</p>\r\n\r\n<h3>Thiết bị c&oacute; thể kết nối với c&aacute;c sản phẩm:</h3>\r\n\r\n<p>- iPhone, iPad v&agrave; iPod touch c&oacute; iOS 12.2 trở l&ecirc;n.</p>\r\n\r\n<p>- C&aacute;c mẫu Apple Watch c&oacute; watchOS 5.2 trở l&ecirc;n.</p>\r\n\r\n<p>- C&aacute;c mẫu m&aacute;y Mac c&oacute; macOS 10.14.4 trở l&ecirc;n.</p>\r\n\r\n<p>- C&aacute;c mẫu Apple TV c&oacute; tvOS 12.2 trở l&ecirc;n.<a href=\"https://www.thegioididong.com/images/54/236025/tai-nghe-bluetooth-airpods-2-wireless-charge-mrxj2-2-2.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth AirPods 2 Wireless charge Apple MRXJ2 Trắng kết nối nhanh chóng\" src=\"https://cdn.tgdd.vn/Products/Images/54/236025/tai-nghe-bluetooth-airpods-2-wireless-charge-mrxj2-2-2.jpg\" /></a></p>\r\n\r\n<h3>Bộ sản phẩm gồm c&oacute;: Tai nghe, s&aacute;ch hướng dẫn, hộp sạc: Lightning<a href=\"https://www.thegioididong.com/images/54/236025/tai-nghe-bluetooth-airpods-2-wireless-charge-mrxj2-1-org2.jpg\" onclick=\"return false;\"><img alt=\"Bộ sản phẩm gồm có Tai nghe, Sách hướng dẫn, Hộp sạc: Lightning.\" src=\"https://cdn.tgdd.vn/Products/Images/54/236025/tai-nghe-bluetooth-airpods-2-wireless-charge-mrxj2-1-org2.jpg\" /></a></h3>\r\n\r\n<h3>Hướng dẫn kết nối v&agrave; sử dụng&nbsp;<a href=\"https://www.thegioididong.com/tai-nghe-apple\" target=\"_blank\">tai nghe Apple</a>:</h3>\r\n\r\n<p>- Từ m&agrave;n h&igrave;nh ch&iacute;nh iPhone, vuốt từ dưới l&ecirc;n để mở Control Center v&agrave; bật Bluetooth.</p>\r\n\r\n<p>- Mở nắp hộp AirPods vẫn để 2 tai nghe b&ecirc;n trong hộp, l&uacute;c n&agrave;y tr&ecirc;n iPhone sẽ xuất hiện 1 bảng pops up c&oacute; h&igrave;nh tai nghe v&agrave; d&ograve;ng chữ Connect.</p>\r\n\r\n<p>- Bấm Connect, sau đ&oacute; bấm Done l&agrave; ho&agrave;n tất kết nối.</p>\r\n\r\n<p>- Những lần sau, bạn chỉ cần lấy tai nghe ra khỏi hộp l&agrave; iPhone sẽ tự động kết nối.<a href=\"https://www.thegioididong.com/images/54/236025/tai-nghe-bluetooth-airpods-2-wireless-charge-mrxj2-4-org7.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth AirPods 2 Wireless charge Apple MRXJ2 Trắng hướng dẫn kết nối\" src=\"https://cdn.tgdd.vn/Products/Images/54/236025/tai-nghe-bluetooth-airpods-2-wireless-charge-mrxj2-4-org7.jpg\" /></a></p>', 5, '2021-04-25 22:16:40', '2021-04-25 22:21:39'),
(28, 'Tai nghe Bluetooth AirPods 2 Apple MV7N2', 'bluetooth-airpods-2-apple-mv7n2-imei-ava-600x600.jpg', 3790000, 10, '12 tháng', 'Full Box', '300000', 'Nguyên Seal - Chính Hãng', 1, 0, '<h3><a href=\"https://www.thegioididong.com/tai-nghe/tai-nghe-bluetooth-airpods-2-apple-mv7n2-trang\" target=\"_blank\">Tai nghe Bluetooth AirPods 2 Apple MV7N2 Trắng</a>&nbsp;vẫn giữ cho m&igrave;nh thiết kế hiện đại, cao cấp, hợp thời trang</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/236016/tai-nghe-bluetooth-airpods-2-apple-mv7n2-trang-1-org1.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth AirPods 2 Apple MV7N2 Trắng thiết kế hiện đại, cao cấp\" src=\"https://cdn.tgdd.vn/Products/Images/54/236016/tai-nghe-bluetooth-airpods-2-apple-mv7n2-trang-1-org1.jpg\" /></a></p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/tai-nghe\" target=\"_blank\">Tai nghe</a>&nbsp;AirPods 2 trang bị chip H1 gi&uacute;p tai nghe kết nối ổn định, nhanh ch&oacute;ng v&agrave; tiết kiệm pin hơn</h3>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/chip-apple-h1-la-gi-no-giup-airpods-moi-loi-hai-hon-o-diem-nao--1165255\" target=\"_blank\">Chip Apple H1 l&agrave; g&igrave;? N&oacute; gi&uacute;p AirPods mới lợi hại hơn ở điểm n&agrave;o?</a><a href=\"https://www.thegioididong.com/images/54/236016/tai-nghe-bluetooth-airpods-2-apple-mv7n2-trang-1-org3.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth AirPods 2 Apple MV7N2 Trắng hỗ trợ chip H1 xử lý tốt hơn \" src=\"https://cdn.tgdd.vn/Products/Images/54/236016/tai-nghe-bluetooth-airpods-2-apple-mv7n2-trang-1-org3.jpg\" /></a></p>\r\n\r\n<h3>Hỗ trợ k&iacute;ch hoạt Siri bằng giọng n&oacute;i &quot;Hey, Siri&quot; v&agrave; thao t&aacute;c cảm ứng tiện lợi</h3>\r\n\r\n<p>Ngo&agrave;i ra,&nbsp;<a href=\"https://www.thegioididong.com/tai-nghe-bluetooth\" target=\"_blank\">tai nghe Bluetooth</a>&nbsp;AirPods 2 c&ograve;n hỗ trợ nhận cuộc gọi, c&oacute; thể nghe hoặc tạm dừng đoạn nhạc đang ph&aacute;t tr&ecirc;n điện thoại bằng những thao t&aacute;c chạm cảm ứng tr&ecirc;n tai nghe, tiện lợi cho người d&ugrave;ng khi di chuyển.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/236016/tai-nghe-bluetooth-airpods-2-apple-mv7n2-trang-1-org-2.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth AirPods 2 Apple MV7N2 Trắng kích hoạt Siri bằng giọng nói \" src=\"https://cdn.tgdd.vn/Products/Images/54/236016/tai-nghe-bluetooth-airpods-2-apple-mv7n2-trang-1-org-2.jpg\" /></a></p>\r\n\r\n<h3>Kết nối được hầu hết với c&aacute;c thiết bị Apple v&agrave; cả với c&aacute;c thiết bị hệ điều h&agrave;nh Android, Windows</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/tai-nghe-apple\" target=\"_blank\">Tai nghe Apple</a>&nbsp;AirPods 2 sử dụng chuẩn kết nối&nbsp;<strong>Bluetooth 5.0&nbsp;</strong>được tối ưu cho mọi thiết bị của h&atilde;ng như iPhone, iPad, iMac, Macbook, Apple Watch v&agrave; cả những thiết bị&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\">điện thoại</a>,&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\">m&aacute;y t&iacute;nh bảng</a>&nbsp;chạy Android hay&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\">laptop</a>, m&aacute;y t&iacute;nh Windows.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/236016/tai-nghe-bluetooth-airpods-2-apple-mv7n2-trang-1-org-23.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth AirPods 2 Apple MV7N2 Trắng kết nối với hầu hết các thiết bị Apple\" src=\"https://cdn.tgdd.vn/Products/Images/54/236016/tai-nghe-bluetooth-airpods-2-apple-mv7n2-trang-1-org-23.jpg\" /></a></p>\r\n\r\n<h3>Trang bị c&ocirc;ng nghệ khử tiếng ồn cuộc gọi, đảm bảo chất lượng cuộc đ&agrave;m thoại tốt nhất</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/tai-nghe-apple-bluetooth\" target=\"_blank\">Tai nghe Bluetooth Apple</a>&nbsp;AirPods 2 trang bị t&iacute;nh năng khử ồn để tạo điểm nhấn kh&aacute;c biệt so với chiếc AirPods thế hệ đầu, nhằm cho ra&nbsp;<strong>chất &acirc;m tuyệt vời</strong>&nbsp;v&agrave;&nbsp;<strong>cải thiện khoảng c&aacute;ch kết nối</strong>&nbsp;giữa c&aacute;c thiết bị.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/236016/tai-nghe-bluetooth-airpods-2-apple-mv7n2-trang-1-org-25.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth AirPods 2 Apple MV7N2 Trắng khử tiếng ồn\" src=\"https://cdn.tgdd.vn/Products/Images/54/236016/tai-nghe-bluetooth-airpods-2-apple-mv7n2-trang-1-org-25.jpg\" /></a></p>\r\n\r\n<h3>Nghe nhạc l&ecirc;n đến 5 giờ cho mỗi một lần sạc nhanh 15 ph&uacute;t v&agrave; c&oacute; thể sử dụng hơn 24 giờ khi sạc nhiều lần với hộp sạc</h3>\r\n\r\n<p>Điểm đặc biệt tr&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/tai-nghe-khong-da\" target=\"_blank\">tai nghe True Wireless</a>,&nbsp;<strong>sạc nhanh 15 ph&uacute;t</strong>&nbsp;sẽ c&oacute; thể nghe đến&nbsp;<strong>3 giờ&nbsp;</strong>(sử dụng ở mức &acirc;m lượng 50%) nhờ c&ocirc;ng nghệ sạc nhanh t&iacute;ch hợp.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/236016/tai-nghe-bluetooth-airpods-2-apple-mv7n2-trang-15.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth AirPods 2 Apple MV7N2 Trắng sử dụng pin dài lâu\" src=\"https://cdn.tgdd.vn/Products/Images/54/236016/tai-nghe-bluetooth-airpods-2-apple-mv7n2-trang-15.jpg\" /></a></p>', 5, '2021-04-25 22:17:56', '2021-04-25 22:21:38'),
(29, 'Adapter Sạc Type C 20W dùng cho iPhone/iPad Apple MHJE3 Trắng', 'adapter-sac-type-c-20w-cho-iphone-ipad-apple-mhje3-avatar-1-600x600.jpg', 790000, 20, '12 tháng', 'Không', '100000', 'Nguyên Seal - Chính Hãng', 1, 1, '<h3>Thiết kế gọn đẹp, đầu cắm 2 chấu phổ biến</h3>\r\n\r\n<p>Kiểu d&aacute;ng&nbsp;<a href=\"https://www.thegioididong.com/adapter-sac\" target=\"_blank\">adapter sạc</a>&nbsp;đơn giản, trang nh&atilde;, chất lượng tốt cho độ bền cao. Thiết kế đầu cắm loại c&oacute; 2 chấu quen thuộc, d&ugrave;ng được với c&aacute;c ổ điện ở mọi nơi từ nh&agrave; cho tới c&ocirc;ng sở, trường học, nh&agrave; h&agrave;ng, kh&aacute;ch sạn,...</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/58/230315/adapter-sac-type-c-20w-cho-iphone-ipad-apple-mhje3-264920-024901.jpg\" onclick=\"return false;\"><img alt=\"Adapter Sạc Type C 20W dùng cho iPhone/iPad Apple MHJE3 Trắng - Thiết kế gọn đẹp\" src=\"https://cdn.tgdd.vn/Products/Images/58/230315/adapter-sac-type-c-20w-cho-iphone-ipad-apple-mhje3-264920-024901.jpg\" /></a></p>\r\n\r\n<h3>Sạc nhanh qua cổng ra Type-C c&ocirc;ng suất mạnh mẽ 20W</h3>\r\n\r\n<p>Đặc biệt, t&iacute;ch hợp c&ocirc;ng nghệ Power Delivery hỗ trợ&nbsp;<a href=\"https://www.thegioididong.com/adapter-sac-sac-nhanh\" target=\"_blank\">sạc nhanh</a>&nbsp;ở nhiều d&ograve;ng điện &aacute;p kh&aacute;c nhau, gi&uacute;p thiết bị được nạp đầy năng lượng trong thời gian ngắn một c&aacute;ch an to&agrave;n, tối ưu nhất.</p>\r\n\r\n<p>Cổng Type-C kh&aacute; th&ocirc;ng dụng n&ecirc;n cho adapter l&agrave;m việc được với nhiều loại&nbsp;<a href=\"https://www.thegioididong.com/cap-dien-thoai\" target=\"_blank\">c&aacute;p sạc</a>&nbsp;tr&ecirc;n thị trường như&nbsp;<a href=\"https://www.thegioididong.com/cap-dien-thoai-type-c-type-c\" target=\"_blank\">c&aacute;p Type-C - Type-C</a>,&nbsp;<a href=\"https://www.thegioididong.com/cap-dien-thoai-type-c-lightning\" target=\"_blank\">c&aacute;p Type-C - Lightning</a>.</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/cong-nghe-power-delivery-la-gi-1212331\" target=\"_blank\">C&ocirc;ng nghệ Power Delivery l&agrave; g&igrave;?</a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/58/230315/adapter-sac-type-c-20w-cho-iphone-ipad-apple-mhje3-264920-024907.jpg\" onclick=\"return false;\"><img alt=\"Adapter Sạc Type C 20W dùng cho iPhone/iPad Apple MHJE3 Trắng - Sạc nhanh qua cổng ra Type-C công suất mạnh mẽ 20W\" src=\"https://cdn.tgdd.vn/Products/Images/58/230315/adapter-sac-type-c-20w-cho-iphone-ipad-apple-mhje3-264920-024907.jpg\" /></a></p>\r\n\r\n<h3>Apple MHJE3 ph&ugrave; hợp 100% với c&aacute;c d&ograve;ng iPhone, iPad</h3>\r\n\r\n<p>Sử dụng&nbsp;<a href=\"https://www.thegioididong.com/adapter-sac-apple\" target=\"_blank\">Adapter sạc Apple</a>&nbsp;n&agrave;y để nạp pin cho&nbsp;iPhone, iPad của bạn kh&ocirc;ng những cho khả năng kết nối cao, tăng tuổi thọ cho pin m&agrave; c&ograve;n gi&uacute;p bảo vệ người d&ugrave;ng v&agrave; thiết bị hiệu quả.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/58/230315/adapter-sac-type-c-20w-cho-iphone-ipad-apple-mhje3-264820-024854.jpg\" onclick=\"return false;\"><img alt=\"Adapter Sạc Type C 20W dùng cho iPhone/iPad Apple MHJE3 Trắng - Apple MHJE3 phù hợp 100% với các dòng iPhone, iPad\" src=\"https://cdn.tgdd.vn/Products/Images/58/230315/adapter-sac-type-c-20w-cho-iphone-ipad-apple-mhje3-264820-024854.jpg\" /></a></p>\r\n\r\n<p>Nếu h&agrave;nh trang thường nhật của bạn đang thiếu 1 chiếc&nbsp;adapter&nbsp;sạc nhanh&nbsp;ch&iacute;nh h&atilde;ng tin cậy của Apple th&igrave; mẫu&nbsp;<a href=\"https://www.thegioididong.com/cap-dien-thoai/adapter-sac-type-c-20w-cho-iphone-ipad-apple-mhje3\" target=\"_blank\">adapter Sạc Type C cho 20W d&ugrave;ng cho iPhone/iPad Apple MHJE3 trắng</a>&nbsp;chắc chắn l&agrave; lựa chọn tuyệt vời, chọn mua ngay để trải nghiệm khả năng sạc ấn tượng của em n&oacute; n&agrave;o!</p>', 6, '2021-04-25 22:20:11', '2021-04-25 22:47:55'),
(30, 'Adapter Sạc 5W cho iPhone/iPad/iPod Apple MGN13 Trắng', 'adapter-sac-5w-cho-iphone-ipad-ipod-apple-mgn13-600x600.jpg', 400000, 20, '12 tháng', 'Cáp - Sạc -Tai nghe', '50000', 'Nguyên Seal - Chính Hãng', 1, 1, '<h3>Giới thiệu sản phẩm</h3>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/adapter-sac-apple\" target=\"_blank\">Adapter sạc Apple</a>&nbsp;ch&iacute;nh h&atilde;ng m&agrave;u trắng trẻ trung, kết cấu gọn g&agrave;ng, dễ d&ugrave;ng</h3>\r\n\r\n<p>Trang bị 2 chấu cứng, cắm chắc chắn v&agrave; tiện lợi v&agrave;o c&aacute;c ổ điện được sử dụng rộng r&atilde;i hiện nay.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/58/229595/adapter-sac-5w-cho-iphone-ipad-ipod-apple-mgn13-061920-071927.jpg\" onclick=\"return false;\"><img alt=\"Adapter Sạc 5W dùng cho Iphone/Ipad/Ipod Apple MGN13 Trắng - Màu trắng trẻ trung\" src=\"https://cdn.tgdd.vn/Products/Images/58/229595/adapter-sac-5w-cho-iphone-ipad-ipod-apple-mgn13-061920-071927.jpg\" /></a></p>\r\n\r\n<h3>Kết nối được nhiều loại&nbsp;<a href=\"https://www.thegioididong.com/cap-dien-thoai\" target=\"_blank\">d&acirc;y c&aacute;p</a>&nbsp;phổ biến tr&ecirc;n thị trường (như c&aacute;p&nbsp;<a href=\"https://www.thegioididong.com/cap-dien-thoai?g=iphone#f\" target=\"_blank\">Lightning</a>,&nbsp;<a href=\"https://www.thegioididong.com/cap-dien-thoai?g=micro-usb#f\" target=\"_blank\">Micro USB</a>,&nbsp;<a href=\"https://www.thegioididong.com/cap-dien-thoai?g=type-c#f\" target=\"_blank\">Type-C</a>,...) qua cổng ra USB&nbsp;5 W&nbsp;</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/58/229595/adapter-sac-5w-cho-iphone-ipad-ipod-apple-mgn13-061920-071936.jpg\" onclick=\"return false;\"><img alt=\"Adapter Sạc 5W dùng cho Iphone/Ipad/Ipod Apple MGN13 Trắng - Kết nối được nhiều loại dây cáp\" src=\"https://cdn.tgdd.vn/Products/Images/58/229595/adapter-sac-5w-cho-iphone-ipad-ipod-apple-mgn13-061920-071936.jpg\" /></a></p>\r\n\r\n<h3>Đảm bảo 100% tương th&iacute;ch với mọi&nbsp;d&ograve;ng iPhone, iPad, iPod</h3>\r\n\r\n<p>Nạp năng lượng cho iPhone, iPad, iPod c&ugrave;ng&nbsp;<a href=\"https://www.thegioididong.com/adapter-sac\" target=\"_blank\">adapter sạc</a>&nbsp;n&agrave;y gi&uacute;p d&acirc;y c&aacute;p, thiết bị của bạn bền hơn, hạn chế chai pin, đảm bảo an to&agrave;n cho thiết bị v&agrave; người d&ugrave;ng tối đa.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/58/229595/adapter-sac-5w-cho-iphone-ipad-ipod-apple-mgn13-061920-071932.jpg\" onclick=\"return false;\"><img alt=\"Adapter Sạc 5W dùng cho Iphone/Ipad/Ipod Apple MGN13 Trắng - Đảm bảo 100% tương thích với mọi dòng iPhone, iPad, iPod\" src=\"https://cdn.tgdd.vn/Products/Images/58/229595/adapter-sac-5w-cho-iphone-ipad-ipod-apple-mgn13-061920-071932.jpg\" /></a></p>\r\n\r\n<p>L&agrave; sản phẩm&nbsp;Apple c&ograve;n&nbsp;nguy&ecirc;n seal 100%, mua&nbsp;<a href=\"https://www.thegioididong.com/cap-dien-thoai/adapter-sac-5w-cho-iphone-ipad-ipod-apple-mgn13\" target=\"_blank\">adapter sạc 5W d&ugrave;ng cho Iphone/Ipad/Ipod Apple MGN13 trắng</a>&nbsp;tại Thế Giới Di Động bạn được đảm bảo về chất lượng v&agrave; mẫu m&atilde; đạt chuẩn, ho&agrave;n to&agrave;n</p>', 6, '2021-04-25 22:21:32', '2021-04-25 22:47:59'),
(31, 'Cáp Type C- Lightning 1m Apple MX0K2 Trắng', 'cap-type-c-lightning-1m-apple-mx0k2-trang-avatar-1-600x600.jpg', 590000, 10, '12 tháng', 'Cáp - Sạc -Tai nghe', '40000', 'Nguyên Seal - Chính Hãng', 1, 0, '<h3><a href=\"https://www.thegioididong.com/cap-dien-thoai/cap-type-c-lightning-1m-apple-mx0k2-trang\" target=\"_blank\">C&aacute;p Type C- Lightning 1m Apple MX0K2 Trắng</a>&nbsp;thiết kế đơn giản với độ d&agrave;i&nbsp;<strong>1 m</strong>&nbsp;cho ph&eacute;p người d&ugrave;ng cuộn gọn v&agrave; tiện đem theo bất cứ đ&acirc;u</h3>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/images/58/220088/cap-type-c-lightning-1m-apple-mx0k2-trang1.jpg\" onclick=\"return false;\"><img alt=\"Cáp dài 1 m - Cáp Type C- Lightning 1m Apple MX0K2 Trắng\" src=\"https://cdn.tgdd.vn/Products/Images/58/220088/cap-type-c-lightning-1m-apple-mx0k2-trang1.jpg\" /></a></h3>\r\n\r\n<h3>Dễ d&agrave;ng đồng bộ h&oacute;a dữ liệu v&agrave; sạc giữa&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\">điện thoại</a>,&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\">tablet</a>&nbsp;v&agrave;&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\">laptop</a></h3>\r\n\r\n<p>Tương th&iacute;ch với&nbsp;<a href=\"https://www.thegioididong.com/cap-dien-thoai-adapter-cu-sac\" target=\"_blank\">Adapter sạc</a>,&nbsp;<a href=\"https://www.thegioididong.com/sac-dtdd\" target=\"_blank\">pin sạc dự ph&ograve;ng</a>,&nbsp;<a href=\"https://www.thegioididong.com/laptop-apple-macbook\" target=\"_blank\">MacBook</a>&nbsp;hoặc iPad Pro c&oacute; cổng USB &ndash; C hoặc cổng&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/thunderbolt-3-usb-c-la-gi-959274\" target=\"_blank\">Thunderbolt 3</a>&nbsp;(sử dụng dạng cổng USB &ndash; C).</p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/images/58/220088/cap-type-c-lightning-1m-apple-mx0k2-trang-7.jpg\" onclick=\"return false;\"><img alt=\"Kết nối qua USB - C hoặc Thunderbolt 3 - Cáp Type C- Lightning 1m Apple MX0K2 Trắng\" src=\"https://cdn.tgdd.vn/Products/Images/58/220088/cap-type-c-lightning-1m-apple-mx0k2-trang-7.jpg\" /></a></h3>\r\n\r\n<h3>C&oacute; thể sử dụng&nbsp;<a href=\"https://www.thegioididong.com/cap-dien-thoai\" target=\"_blank\">c&aacute;p sạc</a>&nbsp;kết nối với nguồn sạc 29 W, 30 W, 61 W hoặc 87 W để c&oacute; thể sử dụng&nbsp;<strong>t&iacute;nh năng sạc</strong>&nbsp;nhanh tr&ecirc;n thiết bị của bạn</h3>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/images/58/220088/cap-type-c-lightning-1m-apple-mx0k2-trang3.jpg\" onclick=\"return false;\"><img alt=\"Sạc nhanh cho các thiết bị được hỗ trợ - Cáp Type C- Lightning 1m Apple MX0K2 Trắng\" src=\"https://cdn.tgdd.vn/Products/Images/58/220088/cap-type-c-lightning-1m-apple-mx0k2-trang3.jpg\" /></a></h3>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/cap-dien-thoai-apple\" target=\"_blank\">C&aacute;p sạc Apple</a>&nbsp;dễ d&agrave;ng kết nối với&nbsp;<a href=\"https://www.thegioididong.com/dtdd-apple-iphone\" target=\"_blank\">iPhone</a>,&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-apple-ipad\" target=\"_blank\">iPad</a>&nbsp;hoặc iPod với&nbsp;<a href=\"https://www.thegioididong.com/cap-dien-thoai-cap-iphone-lightning\" target=\"_blank\">đầu sạc Lightning</a></h3>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/images/58/220088/cap-type-c-lightning-1m-apple-mx0k2-trang2.jpg\" onclick=\"return false;\"><img alt=\"Tương thích với các thiết bị của Apple - Sạc nhanh cho các thiết bị được hỗ trợ - Cáp Type C- Lightning 1m Apple MX0K2 Trắng\" src=\"https://cdn.tgdd.vn/Products/Images/58/220088/cap-type-c-lightning-1m-apple-mx0k2-trang2.jpg\" /></a></h3>\r\n\r\n<p>C&aacute;p Type C- Lightning 1m Apple MX0K2 Trắng với 2 đầu Type - C v&agrave; Lightning ph&ugrave; hợp cho c&aacute;c d&ograve;ng sản phẩm của Apple, đặc biệt sản phẩm c&oacute; hỗ trợ sạc nhanh gi&uacute;p người d&ugrave;ng tiết kiệm thời gian.</p>', 6, '2021-04-25 22:23:56', '2021-04-25 22:41:37'),
(32, 'Cáp sạc không dây Apple Watch Magnetic 1 m Apple MX2E2 Trắng', 'cap-sac-apple-watch-magnetic-1m-apple-mx2e2-1-600x600.jpg', 890, 20, '12 tháng', 'Không', '50000', 'Nguyên Seal - Chính Hãng', 1, 0, '<h3>Giới thiệu sản phẩm</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/cap-dien-thoai-apple\" target=\"_blank\">C&aacute;p sạc Apple</a>&nbsp;với&nbsp;thiết kế gọn nhẹ,&nbsp;<strong>chiều d&agrave;i 1m</strong>&nbsp;thuận tiện đem theo b&ecirc;n m&igrave;nh c&ugrave;ng<strong>&nbsp;t&iacute;nh năng sạc cảm ứng</strong>&nbsp;gi&uacute;p bạn sạc&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh-apple\" target=\"_blank\">Apple Watch</a>&nbsp;ho&agrave;n to&agrave;n dễ d&agrave;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/58/215984/cap-sac-apple-watch-magnetic-1m-apple-mx2e21.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế nhỏ gọn, dễ dàng sử dụng - Cáp sạc Apple Watch Magnetic 1m Apple MX2E2 Trắng\" src=\"https://cdn.tgdd.vn/Products/Images/58/215984/cap-sac-apple-watch-magnetic-1m-apple-mx2e21.jpg\" /></a></p>\r\n\r\n<p>Nam ch&acirc;m của Magnetic&nbsp;sẽ&nbsp;<strong>tự động h&uacute;t Apple Watch</strong>&nbsp;gi&uacute;p sạc dễ d&agrave;ng tiện lợi. Đặc biệt với&nbsp;<a href=\"https://www.thegioididong.com/cap-dien-thoai-cap-sac-apple-watch\" target=\"_blank\">d&acirc;y sạc Apple Watch</a>&nbsp;Magnetic bạn kh&ocirc;ng cần phải căn chỉnh ch&iacute;nh x&aacute;c, bạn chỉ cần đưa đưa lại gần Apple Watch.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/58/215984/cap-sac-apple-watch-magnetic-1m-apple-mx2e22.jpg\" onclick=\"return false;\"><img alt=\"Đầu nam châm tự động hút Apple Watch giúp sạc dễ dàng - Cáp sạc Apple Watch Magnetic 1m Apple MX2E2 Trắng\" src=\"https://cdn.tgdd.vn/Products/Images/58/215984/cap-sac-apple-watch-magnetic-1m-apple-mx2e22.jpg\" /></a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/cap-dien-thoai\" target=\"_blank\">C&aacute;p sạc</a><strong>&nbsp;sử dụng cổng USB th&ocirc;ng dụng</strong>. C&oacute; thể sử dụng chung với&nbsp;<a href=\"https://www.thegioididong.com/cap-dien-thoai-adapter-cu-sac\" target=\"_blank\">củ sạc</a>&nbsp;hoặc&nbsp;<a href=\"https://www.thegioididong.com/sac-dtdd\" target=\"_blank\">sạc dự ph&ograve;ng</a>&nbsp;để nạp điện cho thiết bị của bạn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/58/215984/cap-sac-apple-watch-magnetic-1m-apple-mx2e23.jpg\" onclick=\"return false;\"><img alt=\"Đầu sạc USB thông dụng - Cáp sạc Apple Watch Magnetic 1m Apple MX2E2 Trắng\" src=\"https://cdn.tgdd.vn/Products/Images/58/215984/cap-sac-apple-watch-magnetic-1m-apple-mx2e23.jpg\" /></a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/cap-dien-thoai/cap-sac-apple-watch-magnetic-1m-apple-mx2e2\" target=\"_blank\">C&aacute;p sạc Apple Watch Magnetic 1m Apple MX2E2 Trắng</a>&nbsp;với thiết kế trang nh&atilde; c&ugrave;ng t&iacute;nh năng sạc cảm ứng gi&uacute;p người d&ugrave;ng dễ d&agrave;ng sử dụng, loại bỏ được sự phức tạp ph&ugrave; hợp cho những người sử dụng Apple Watch.</p>', 6, '2021-04-25 22:24:50', '2021-04-25 22:41:44'),
(33, 'Adapter chuyển đổi Type C sang HDMI/Type C/USB Apple MUF82 Trắng', 'adapter-type-c-sang-hdmi-type-c-usb-apple-muf82-avatar-1-600x600.jpg', 2290000, 10, '12 tháng', 'Cáp - Sạc -Tai nghe', '50000', 'Nguyên Seal - Chính Hãng', 1, 0, '<h3>Giới thiệu sản phẩm</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/cap-dien-thoai/adapter-type-c-sang-hdmi-type-c-usb-apple-muf82\" target=\"_blank\">Adapter chuyển đổi Type C sang HDMI/Type C/USB Apple MUF82 Trắng</a>&nbsp;c&oacute;&nbsp;<strong>thiết kế nhỏ gọn, sang trọng</strong>&nbsp;gi&uacute;p người d&ugrave;ng dễ d&agrave;ng mang theo b&ecirc;n m&igrave;nh sử dụng. Adapter l&agrave;&nbsp;<strong>sản phẩm ch&iacute;nh h&atilde;ng của Apple</strong>&nbsp;được thiết kế cho&nbsp;<a href=\"https://www.thegioididong.com/laptop-apple-macbook\" target=\"_blank\">Macbook</a>&nbsp;v&agrave; iPad Pro.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/58/215983/adapter-type-c-sang-hdmi-type-c-usb-apple-muf821.jpg\" onclick=\"return false;\"><img alt=\"Sử dụng cho Macbook và iPad Pro - Adapter chuyển đổi Type C sang HDMI/Type C/USB Apple MUF82 Trắng\" src=\"https://cdn.tgdd.vn/Products/Images/58/215983/adapter-type-c-sang-hdmi-type-c-usb-apple-muf821.jpg\" /></a></p>\r\n\r\n<p>Sử dụng cổng USB 3.0 để kết nối c&aacute;c thiết bị như&nbsp;<a href=\"https://www.thegioididong.com/usb\" target=\"_blank\">USB</a>,&nbsp;<a href=\"https://www.thegioididong.com/o-cung-di-dong\" target=\"_blank\">ổ cứng di động</a>,&nbsp;<a href=\"https://www.thegioididong.com/ban-phim/\" target=\"_blank\">b&agrave;n ph&iacute;m</a>,&nbsp;<a href=\"https://www.thegioididong.com/chuot-may-tinh\" target=\"_blank\">chuột</a>,...để đồng bộ h&oacute;a v&agrave; sạc c&aacute;c thiết bị iOS của bạn.&nbsp;Bạn cũng c&oacute; thể kết nối&nbsp;<a href=\"https://www.thegioididong.com/cap-dien-thoai\" target=\"_blank\">c&aacute;p sạc</a>&nbsp;với cổng USB để sạc Macbook hoặc iPad Pro.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/58/215983/adapter-type-c-sang-hdmi-type-c-usb-apple-muf82-4.jpg\" onclick=\"return false;\"><img alt=\"Sử dụng cổng USB 3.0 với tốc độ xử lý nhanh chóng - Adapter chuyển đổi Type C sang HDMI/Type C/USB Apple MUF82 Trắng\" src=\"https://cdn.tgdd.vn/Products/Images/58/215983/adapter-type-c-sang-hdmi-type-c-usb-apple-muf82-4.jpg\" /></a></p>\r\n\r\n<p>Adapter c&oacute; t&iacute;ch hợp th&ecirc;m 1 đầu ra cổng Type-C để hỗ trợ sạc pin, truyền dữ liệu,...</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/58/215983/adapter-type-c-sang-hdmi-type-c-usb-apple-muf82-5.jpg\" onclick=\"return false;\"><img alt=\"Cổng ra Type-C hỗ trợ sạc pin truyền dữ liệu - Adapter chuyển đổi Type C sang HDMI/Type C/USB Apple MUF82 Trắng\" src=\"https://cdn.tgdd.vn/Products/Images/58/215983/adapter-type-c-sang-hdmi-type-c-usb-apple-muf82-5.jpg\" /></a></p>\r\n\r\n<p>C&oacute;&nbsp;<strong>đầu ra cổng HDMI với chuẩn 1080p ở 60Hz hay UHD (4K) ở 30Hz cho độ ph&acirc;n giải cao</strong>&nbsp;đảm bảo chất lượng h&igrave;nh ảnh tốt nhất.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/58/215983/adapter-type-c-sang-hdmi-type-c-usb-apple-muf82-6.jpg\" onclick=\"return false;\"><img alt=\"HDMI chuẩn UHD(4K) 30Hz độ phân giải siêu nét - Adapter chuyển đổi Type C sang HDMI/Type C/USB Apple MUF82 Trắng\" src=\"https://cdn.tgdd.vn/Products/Images/58/215983/adapter-type-c-sang-hdmi-type-c-usb-apple-muf82-6.jpg\" /></a></p>\r\n\r\n<p>C&oacute; thể thấy,&nbsp;Adapter chuyển đổi Type-C sang HDMI/Type-C/USB Apple MUF82 Trắng với thiết kế sang trọng được t&iacute;ch hợp c&aacute;c cổng kết nối l&agrave; HDMI, USB 3.0, Type-C. Sản phẩm sẽ l&agrave; sự lựa chọn tốt cho những kh&aacute;ch h&agrave;ng sử dụng Macbook v&agrave; iPad Pro.</p>', 6, '2021-04-25 22:25:56', '2021-04-25 22:41:47'),
(34, 'Adapter Sạc Type-C 87W Apple MNF82 Trắng', '209110-600x600.jpg', 2490000, 20, '12 tháng', 'Cáp - Sạc -Tai nghe', '0', 'Nguyên Seal - Chính Hãng', 1, 1, '<h3>Giới thiệu sản phẩm</h3>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/cap-dien-thoai/adapter-type-c-87w-cho-ipad-macbook-apple-trang\" target=\"_blank\">Adapter Sạc Type-C 87W Apple MNF82 Trắng</a>&nbsp;c&oacute; thết kế đơn giản, nhỏ gọn, với logo Apple nằm giữa cho Adapter th&ecirc;m phần&nbsp;sang trọng hơn</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/58/209110/adapter-type-c-87w-cho-ipad-macbook-apple-trang-1-1.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế nhỏ gọn, có thể gấp chuôi sạc lại tiện lợi - Adapter Sạc Type-C 87W Apple MNF82 Trắng\" src=\"https://cdn.tgdd.vn/Products/Images/58/209110/adapter-type-c-87w-cho-ipad-macbook-apple-trang-1-1.jpg\" /></a></p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/adapter-sac\" target=\"_blank\">Adapter sạc</a>&nbsp;trang bị cổng sạc USB Type-C hiện đại, kết nối với&nbsp;<a href=\"https://www.thegioididong.com/cap-dien-thoai\" target=\"_blank\">c&aacute;p</a>&nbsp;2 đầu Type-C để sạc nhanh cho&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\">laptop</a>&nbsp;của bạn</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/58/209110/adapter-type-c-87w-cho-ipad-macbook-apple-trang-fixx1.jpg\" onclick=\"return false;\"><img alt=\"Cổng sạc Type-C, sạc nhanh cho laptop - Adapter Sạc Type-C 87W Apple MNF82 Trắng\" src=\"https://cdn.tgdd.vn/Products/Images/58/209110/adapter-type-c-87w-cho-ipad-macbook-apple-trang-fixx1.jpg\" /></a></p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/adapter-sac-apple\" target=\"_blank\">Adapter Apple</a>&nbsp;MNF82 tương th&iacute;ch với nhiều mẫu laptop v&agrave; c&aacute;c mẫu&nbsp;<a href=\"https://www.thegioididong.com/laptop-apple-macbook\" target=\"_blank\">MacBook</a>&nbsp;c&oacute; cổng&nbsp;Type-C, Thunderbolt 3 như:</h3>\r\n\r\n<p>-&nbsp;MacBook&nbsp;(Retina, 12 inch, 2015 - 2017).</p>\r\n\r\n<p>- MacBook Air (Retina, 13 inch, 2018 trở l&ecirc;n).</p>\r\n\r\n<p>- MacBook Pro (13 inch, 2016 trở l&ecirc;n).</p>\r\n\r\n<p>- MacBook Pro (15 inch, 2016 trở l&ecirc;n).</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/58/209110/adapter-type-c-87w-cho-ipad-macbook-apple-trang-5.jpg\" onclick=\"return false;\"><img alt=\"Dùng cho MacBook, MacBook Pro, MacBook Air - Adapter Sạc Type-C 87W Apple MNF82 Trắng\" src=\"https://cdn.tgdd.vn/Products/Images/58/209110/adapter-type-c-87w-cho-ipad-macbook-apple-trang-5.jpg\" /></a></p>\r\n\r\n<h3>Gi&uacute;p cho thiết bị th&ocirc;ng minh của bạn nhanh ch&oacute;ng đầy pin với c&ocirc;ng suất sạc l&ecirc;n đến 87W, tiết kiệm thời gian sạc pin một c&aacute;ch hiệu quả</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/58/209110/adapter-type-c-87w-cho-ipad-macbook-apple-trang-fixx3.jpg\" onclick=\"return false;\"><img alt=\"Sạc nhanh với với 87 W - Adapter Sạc Type-C 87W Apple MNF82 Trắng\" src=\"https://cdn.tgdd.vn/Products/Images/58/209110/adapter-type-c-87w-cho-ipad-macbook-apple-trang-fixx3.jpg\" /></a></p>', 6, '2021-04-25 22:26:45', '2021-04-25 22:47:51');
